// src/components/AccessibleCoursesPage.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAccessibility } from '../hooks/useAccessibility';
import '../styles/CoursesPage.css';

function AccessibleCoursesPage() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCourseIndex, setSelectedCourseIndex] = useState(0);

  const { 
    isAccessibilityMode, 
    useCourseAccessibility, 
    useNavigationAccessibility,
    announceLoading, 
    announceError 
  } = useAccessibility();
  
  const { announceCourseProgress } = useCourseAccessibility();
  const { announcePageLoad } = useNavigationAccessibility();

  // Listener pentru comenzile rapide de accesibilitate
  useEffect(() => {
    if (!isAccessibilityMode) return;

    const handleKeyPress = (e) => {
      if (!isAccessibilityMode || courses.length === 0) return;

      // Navigare prin cursuri cu săgețile
      if (e.key === 'ArrowUp') {
        e.preventDefault();
        setSelectedCourseIndex(prev => prev > 0 ? prev - 1 : courses.length - 1);
      } else if (e.key === 'ArrowDown') {
        e.preventDefault();
        setSelectedCourseIndex(prev => prev < courses.length - 1 ? prev + 1 : 0);
      }
      
      // Enter pentru selectarea cursului
      if (e.key === 'Enter') {
        e.preventDefault();
        const selectedCourse = courses[selectedCourseIndex];
        if (selectedCourse) {
          window.location.href = `/courses/${selectedCourse._id}/lesson/0`;
        }
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [isAccessibilityMode, courses, selectedCourseIndex]);

  // Anunțăm cursul selectat când se schimbă indexul
  useEffect(() => {
    if (isAccessibilityMode && courses.length > 0) {
      const selectedCourse = courses[selectedCourseIndex];
      if (selectedCourse) {
        const announcement = `Cursul ${selectedCourseIndex + 1} din ${courses.length}: ${selectedCourse.name}. ${selectedCourse.description}`;
        setTimeout(() => {
          announceLoading(announcement);
        }, 100);
      }
    }
  }, [selectedCourseIndex, courses, isAccessibilityMode, announceLoading]);

  useEffect(() => {
    console.log('Fetching courses...');
    
    if (isAccessibilityMode) {
      announcePageLoad('Cursuri Java', 'Se încarcă lista cursurilor disponibile...');
    }
    
    fetch('http://localhost:5000/api/courses')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        if (data.courses) {
          console.log('Courses fetched successfully:', data.courses);
          setCourses(data.courses);
          
          if (isAccessibilityMode) {
            setTimeout(() => {
              const announcement = `S-au încărcat ${data.courses.length} cursuri Java. Folosește săgețile pentru navigare și Enter pentru selectare.`;
              announceLoading(announcement);
            }, 1000);
          }
        } else {
          console.error('No courses data received');
        }
        setLoading(false);
      })
      .catch((err) => {
        setError('Failed to load courses');
        console.error('Error fetching courses:', err);
        setLoading(false);
        
        if (isAccessibilityMode) {
          announceError('Nu s-au putut încărca cursurile. Te rugăm să încerci din nou.');
        }
      });
  }, [isAccessibilityMode, announcePageLoad, announceLoading, announceError]);

  if (loading) return (
    <div className="loading-container">
      <div className="loading-spinner"></div>
      <p>Loading...</p>
    </div>
  );

  if (error) return (
    <div className="error-container">
      <p>{error}</p>
    </div>
  );

  return (
    <div className="courses-page">
      {isAccessibilityMode && (
        <div className="accessibility-instructions" style={{ 
          position: 'absolute', 
          left: '-9999px',
          width: '1px',
          height: '1px' 
        }}>
          <p>
            Pagina cu cursuri Java. Sunt disponibile {courses.length} cursuri. 
            Folosește săgețile sus și jos pentru navigare, Enter pentru selectare.
          </p>
        </div>
      )}
      
      <h1 className="page-title">Cursuri Java disponibile</h1>
      <div className="courses-grid">
        {courses.length === 0 ? (
          <p>No courses available</p>
        ) : (
          courses.map((course, index) => (
            <div 
              key={course._id} 
              className={`course-card ${isAccessibilityMode && index === selectedCourseIndex ? 'accessibility-focus' : ''}`}
              role={isAccessibilityMode ? "button" : undefined}
              aria-label={isAccessibilityMode ? `Cursul ${index + 1}: ${course.name}` : undefined}
              tabIndex={isAccessibilityMode ? 0 : undefined}
            >
              <h2 className="course-title">{course.name}</h2>
              <p className="course-description">{course.description}</p>
              <Link 
                to={`/courses/${course._id}/lesson/0`} 
                className="course-link"
                aria-label={isAccessibilityMode ? `Începe cursul ${course.name}` : undefined}
              >
                Începe cursul
              </Link>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default AccessibleCoursesPage;
