import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import MainPage from './components/MainPage';
import Footer from './components/Footer';
import Login from './components/Login';
import Register from './components/Register';
import CoursesPage from './components/CoursesPage'; // Pagina cu cursuri
import CourseDetailPage from './components/CourseDetailPage'; // Detalii curs
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Verificăm dacă token-ul există în localStorage
    const token = localStorage.getItem('authToken');
    if (token) {
      setIsAuthenticated(true); // Setăm starea ca autentificat
    }
  }, []); // Se execută doar la montarea componentei

  const handleLogout = () => {
    // Elimină token-ul din localStorage și setează starea la false
    localStorage.removeItem('authToken');
    setIsAuthenticated(false); // Actualizăm starea locală
    window.location.href = '/'; // Redirecționăm la pagina principală
  };

  return (
    <Router>
      <div className="App">
        <Header isAuthenticated={isAuthenticated} handleLogout={handleLogout} />
        <div className="body-content">
          <Routes>
            {/* Pagina principală pentru utilizatori neautentificați sau cursurile pentru cei autentificați */}
            <Route path="/" element={isAuthenticated ? <CoursesPage /> : <MainPage />} />
            <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
            <Route path="/register" element={<Register />} />
            <Route path="/courses" element={<CoursesPage />} /> {/* Pagina cu cursuri */}
            <Route path="/courses/:courseId" element={<CourseDetailPage />} />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
