import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import MainPage from './components/MainPage';
import Footer from './components/Footer';
import Login from './components/Login';
import Register from './components/Register';
import CoursesPage from './components/CoursesPage';
import CourseDetailPage from './components/CourseDetailPage';
import Quiz from './components/Quiz';
import Profile from './components/Profile';
// import StarsBackground from './components/StarsBackground'; // Comentăm vechiul fundal
import InteractiveBackground from './components/InteractiveBackground'; // Importăm noul fundal interactiv
import './styles/theme.css'; // Importăm tema globală
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      setIsAuthenticated(true);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    setIsAuthenticated(false);
    window.location.href = '/';
  };

  return (
    <Router>
      <div className="App">
        {/* Înlocuim StarsBackground cu noul InteractiveBackground */}
        <InteractiveBackground />

        <Header isAuthenticated={isAuthenticated} handleLogout={handleLogout} />
        <div className="body-content">
          <Routes>
            <Route path="/" element={isAuthenticated ? <CoursesPage /> : <MainPage />} />
            <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
            <Route path="/register" element={<Register />} />
            <Route path="/courses" element={<CoursesPage />} />
            <Route path="/courses/:courseId/lesson/:lessonId" element={<CourseDetailPage />} />
            <Route path="/courses/:courseId/test" element={<Quiz />} />
            <Route path="/profile" element={isAuthenticated ? <Profile /> : <Login setIsAuthenticated={setIsAuthenticated} />} />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
