// src/components/Profile.js - Complete version with fixed tab navigation per section
import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import AvatarManager from './AvatarManager';
import styles from '../styles/Profile.module.css';
import Modal from './Modal';
import Button from './Button';
import { useAccessibility } from '../hooks/useAccessibility';

function Profile() {
  const [userProgress, setUserProgress] = useState([]);
  const [userInfo, setUserInfo] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('courses');
  const [error, setError] = useState(null);
  const [debugInfo, setDebugInfo] = useState(null);
  const navigate = useNavigate();
  
  // Accessibility hook
  const { 
    isAccessibilityMode, 
    useNavigationAccessibility,
    announceLoading,
    announceError 
  } = useAccessibility();
  
  const { announcePageLoad } = useNavigationAccessibility();
  
  // State pentru realizÄƒri
  const [achievements, setAchievements] = useState([]);
  const [loadingAchievements, setLoadingAchievements] = useState(false);
  const [newAchievements, setNewAchievements] = useState([]);
  const [checkingAchievements, setCheckingAchievements] = useState(false);
  const [showNoAchievementsModal, setShowNoAchievementsModal] = useState(false);
  
  // State pentru setÄƒri
  const [username, setUsername] = useState('');
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [updateUsernameLoading, setUpdateUsernameLoading] = useState(false);
  const [changePasswordLoading, setChangePasswordLoading] = useState(false);
  const [usernameError, setUsernameError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [usernameSuccess, setUsernameSuccess] = useState('');
  const [passwordSuccess, setPasswordSuccess] = useState('');

  // State pentru avatar
  const [showAvatarManager, setShowAvatarManager] = useState(false);
  const [userAvatar, setUserAvatar] = useState(null);
  const [userAvatarType, setUserAvatarType] = useState('default');

  // FIXAT: State pentru controlul navigÄƒrii Ã®n secÈ›iuni
  const [currentSectionFocus, setCurrentSectionFocus] = useState('tabs'); // 'tabs', 'content'

  // AnunÈ›Äƒm Ã®ncÄƒrcarea paginii pentru modul accesibilitate
  useEffect(() => {
    if (isAccessibilityMode) {
      announcePageLoad(
        'Profil utilizator', 
        'Pagina ta de profil cu statistici, cursuri È™i setÄƒri.'
      );
    }
  }, [isAccessibilityMode, announcePageLoad]);

  // FIXAT: Handler pentru schimbarea tab-urilor cu anunÈ›uri È™i resetare focus
  const handleTabChange = (tabName) => {
    setActiveTab(tabName);
    setCurrentSectionFocus('content'); // Mergem direct Ã®n conÈ›inutul secÈ›iunii
    
    if (isAccessibilityMode) {
      let tabDescription = '';
      switch (tabName) {
        case 'courses':
          tabDescription = 'Tab cursurile mele selectat. NavigheazÄƒ prin cursurile tale active.';
          break;
        case 'achievements':
          tabDescription = 'Tab realizÄƒri selectat. Primul tab va fi butonul de verificare realizÄƒri.';
          break;
        case 'settings':
          tabDescription = 'Tab setÄƒri selectat. Primul tab va fi cÃ¢mpul nume utilizator din informaÈ›ii cont.';
          break;
      }
      announceLoading(tabDescription);
      
      // FIXAT: SetÄƒm focus-ul pe primul element relevant din secÈ›iune
      setTimeout(() => {
        focusFirstElementInSection(tabName);
      }, 100);
    }
  };

  // FIXAT: FuncÈ›ie pentru focus pe primul element din secÈ›iune
  const focusFirstElementInSection = (tabName) => {
    switch (tabName) {
      case 'courses':
        // Focus pe primul curs sau butonul exploreazÄƒ cursuri
        const firstCourse = document.querySelector('.continueButton');
        const exploreCourses = document.querySelector('.exploreCourses');
        if (firstCourse) {
          firstCourse.focus();
        } else if (exploreCourses) {
          exploreCourses.focus();
        }
        break;
        
      case 'achievements':
        // Focus pe butonul de verificare realizÄƒri
        const checkBtn = document.querySelector('.checkAchievementsBtn');
        if (checkBtn) {
          checkBtn.focus();
          announceLoading('Focus pe butonul verificÄƒ noi realizÄƒri.');
        }
        break;
        
      case 'settings':
        // Focus pe primul cÃ¢mp din informaÈ›ii cont (username)
        const usernameField = document.querySelector('#username');
        if (usernameField) {
          usernameField.focus();
          announceLoading('Focus pe cÃ¢mpul nume utilizator din informaÈ›ii cont.');
        }
        break;
    }
  };

  // FIXAT: Listener pentru navigarea cu Tab Ã®n secÈ›iuni
  useEffect(() => {
    if (!isAccessibilityMode) return;

    const handleKeyDown = (e) => {
      if (!isAccessibilityMode) return;

      // Tab navigation customizat pentru secÈ›iuni
      if (e.key === 'Tab') {
        // Pentru tab-ul setÄƒri, controlÄƒm ordinea de navigare
        if (activeTab === 'settings') {
          e.preventDefault();
          handleSettingsTabNavigation(e.shiftKey);
        }
        // Pentru tab-ul realizÄƒri, controlÄƒm sÄƒ Ã®ncepem cu butonul check
        else if (activeTab === 'achievements') {
          e.preventDefault();
          handleAchievementsTabNavigation(e.shiftKey);
        }
      }
      
      // Enter pe tab-uri
      if (e.key === 'Enter' && currentSectionFocus === 'tabs') {
        const activeTabButton = document.querySelector('.activeTab');
        if (activeTabButton && document.activeElement === activeTabButton) {
          e.preventDefault();
          setCurrentSectionFocus('content');
          focusFirstElementInSection(activeTab);
        }
      }
    };

    document.addEventListener('keydown', handleKeyDown, true);
    return () => document.removeEventListener('keydown', handleKeyDown, true);
  }, [isAccessibilityMode, activeTab, currentSectionFocus]);

  // FIXAT: FuncÈ›ie pentru navigarea Ã®n tab-ul setÄƒri
  const handleSettingsTabNavigation = (isShiftPressed) => {
    const settingsElements = [
      '#username',
      'button[aria-label*="SalveazÄƒ modificÄƒrile"]',
      '#currentPassword', 
      '#newPassword',
      '#confirmPassword',
