        elements.push({
          type: 'method',
          line: index,
          content: trimmedLine,
          name: extractMethodName(trimmedLine)
        });
      }
      
      // Detectare variabile
      if (trimmedLine.match(/^\s*(int|double|String|boolean|char)\s+\w+/)) {
        elements.push({
          type: 'variable',
          line: index,
          content: trimmedLine,
          name: extractVariableName(trimmedLine)
        });
      }
      
      // Detectare bucle
      if (trimmedLine.includes('for') || trimmedLine.includes('while')) {
        elements.push({
          type: 'loop',
          line: index,
          content: trimmedLine
        });
      }
      
      // Detectare condi»õionale
      if (trimmedLine.includes('if') || trimmedLine.includes('else')) {
        elements.push({
          type: 'conditional',
          line: index,
          content: trimmedLine
        });
      }
    });

    return elements;
  };

  const extractMethodName = (line) => {
    const match = line.match(/(\w+)\s*\(/);
    return match ? match[1] : 'metodƒÉ necunoscutƒÉ';
  };

  const extractVariableName = (line) => {
    const match = line.match(/^\s*\w+\s+(\w+)/);
    return match ? match[1] : 'variabilƒÉ necunoscutƒÉ';
  };

