    if (trimmedLine === '') {
      semanticDescription = 'Linie goalƒÉ';
    } else if (trimmedLine.startsWith('//')) {
      semanticDescription = `Comentariu: ${trimmedLine.substring(2).trim()}`;
    } else if (trimmedLine.includes('public static void main')) {
      semanticDescription = 'Metoda principalƒÉ main care √Æncepe execu»õia programului';
    } else if (trimmedLine.includes('System.out.println')) {
      const content = trimmedLine.match(/println\s*\(\s*"([^"]*)"/);
      semanticDescription = `Afi»ôare text: ${content ? content[1] : 'con»õinut necunoscut'}`;
    } else if (trimmedLine.match(/^\s*(int|double|String|boolean|char)\s+\w+/)) {
      const varMatch = trimmedLine.match(/^\s*(\w+)\s+(\w+)/);
      if (varMatch) {
        semanticDescription = `Declarare variabilƒÉ de tip ${varMatch[1]}, numele ${varMatch[2]}`;
      }
    } else if (trimmedLine.includes('for')) {
      semanticDescription = '√énceputul unei bucle for pentru repetarea codului';
    } else if (trimmedLine.includes('while')) {
      semanticDescription = '√énceputul unei bucle while pentru repetarea condi»õionalƒÉ';
    } else if (trimmedLine.includes('if')) {
      semanticDescription = 'Instruc»õiune condi»õionalƒÉ if pentru luarea deciziilor';
    } else if (trimmedLine === '{') {
      semanticDescription = 'ParantezƒÉ acoladƒÉ deschisƒÉ - √Ænceputul unui bloc de cod';
    } else if (trimmedLine === '}') {
      semanticDescription = 'ParantezƒÉ acoladƒÉ √ÆnchisƒÉ - sf√¢r»ôitul unui bloc de cod';
    } else {
      semanticDescription = `Linie de cod: ${trimmedLine}`;
    }
    
    announce(`Linia ${currentLine + 1}: ${semanticDescription}`, 'assertive');
    return semanticDescription;
  };

  // Navigare la urmƒÉtoarea metodƒÉ
  const navigateToNextMethod = () => {
    const elements = parseJavaElements(code);
    const methods = elements.filter(el => el.type === 'method');
    const nextMethod = methods.find(method => method.line > currentLine);
    
    if (nextMethod) {
      setCurrentLine(nextMethod.line);
      moveCursorToLine(nextMethod.line);
      announce(`Navigat la metoda ${nextMethod.name} pe linia ${nextMethod.line + 1}`, 'assertive');
    } else {
      announce('Nu existƒÉ metode dupƒÉ linia curentƒÉ', 'assertive');
    }
  };

  // Navigare la urmƒÉtoarea variabilƒÉ
  const navigateToNextVariable = () => {
    const elements = parseJavaElements(code);
    const variables = elements.filter(el => el.type === 'variable');
    const nextVariable = variables.find(variable => variable.line > currentLine);
    
    if (nextVariable) {
      setCurrentLine(nextVariable.line);
      moveCursorToLine(nextVariable.line);
      announce(`Navigat la variabila ${nextVariable.name} pe linia ${nextVariable.line + 1}`, 'assertive');
    } else {
      announce('Nu existƒÉ variabile dupƒÉ linia curentƒÉ', 'assertive');
    }
  };

  // Mutare cursor la o anumitƒÉ linie
  const moveCursorToLine = (lineNumber) => {
    if (editorRef.current) {
      const lines = code.split('\n');
      let position = 0;
      for (let i = 0; i < lineNumber && i < lines.length; i++) {
        position += lines[i].length + 1; // +1 pentru \n
      }
      editorRef.current.setSelectionRange(position, position);
      editorRef.current.focus();
      setCursorPosition(position);
    }
  };

  // Descrierea structurii codului
  const describeCodeStructure = () => {
    const elements = parseJavaElements(code);
    const methods = elements.filter(el => el.type === 'method');
    const variables = elements.filter(el => el.type === 'variable');
    const loops = elements.filter(el => el.type === 'loop');
    const conditionals = elements.filter(el => el.type === 'conditional');
    
    let description = `Structura codului: `;
    description += `${methods.length} metode, `;
    description += `${variables.length} variabile, `;
    description += `${loops.length} bucle, `;
    description += `${conditionals.length} instruc»õiuni condi»õionale. `;
    
    if (methods.length > 0) {
      description += `Metodele sunt: ${methods.map(m => m.name).join(', ')}. `;
    }
    
    announce(description, 'assertive');
    return description;
  };

  // Afi»ôarea ajutorului pentru accessibility
  const showAccessibilityHelp = () => {
    const helpText = `Comenzi disponibile pentru accessibility:
Control+Shift+R: RuleazƒÉ codul
Control+Shift+A: Ob»õine feedback AI vocal
Control+Shift+M: NavigheazƒÉ la urmƒÉtoarea metodƒÉ
Control+Shift+V: NavigheazƒÉ la urmƒÉtoarea variabilƒÉ
Control+Shift+L: Cite»ôte linia curentƒÉ semantic
Control+Shift+H: Afi»ôeazƒÉ acest ajutor
Control+Shift+S: Descrie structura codului`;
    
    announce(helpText, 'assertive');
    setShowFeedback(true);
    setFeedback(helpText);
  };

  // Executarea codului cu feedback audio
  const runCodeWithAccessibility = async () => {
    announce('√éncepe execu»õia codului', 'assertive');
    setIsRunning(true);
    setOutput('Se executƒÉ codul...');

    try {
      const token = sessionStorage.getItem('authToken');
      
      const response = await fetch('http://localhost:5000/api/execute-code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ code })
      });

      const data = await response.json();
      
      if (data.success) {
        const successMessage = `Execu»õie reu»ôitƒÉ. Rezultat: ${data.output || 'Programul s-a executat fƒÉrƒÉ erori'}`;
        setOutput(data.output || 'Programul s-a executat cu succes!');
        announce(successMessage, 'assertive');
        toast.success('Execu»õie reu»ôitƒÉ!');
      } else {
        const errorMessage = `Eroare de execu»õie: ${data.error}`;
        setOutput(`Eroare: ${data.error}`);
        announce(errorMessage, 'assertive');
        toast.error('Eroare la execu»õie');
      }
    } catch (error) {
      const errorMessage = `Eroare de comunicare: ${error.message}`;
      setOutput(`Eroare: ${error.message}`);
      announce(errorMessage, 'assertive');
      toast.error('Eroare de re»õea');
    } finally {
      setIsRunning(false);
    }
  };

  // Ob»õinerea feedback-ului AI cu citire vocalƒÉ
  const getAIFeedbackAccessible = async () => {
    announce('Se genereazƒÉ feedback AI pentru cod', 'assertive');
    setIsAnalyzing(true);
    setFeedback('Se analizeazƒÉ codul...');
    setShowFeedback(true);

    try {
      const token = sessionStorage.getItem('authToken');
      
      const response = await fetch('http://localhost:5000/api/analyze-code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ 
          code: code,
          lessonId: lessonId 
        })
      });

      const data = await response.json();
      
      if (data.success) {
        setFeedback(data.feedback);
        announce(`Feedback AI generat. ${data.feedback}`, 'assertive');
        toast.success('Feedback generat cu succes!');
      } else {
        const errorMessage = 'Nu am putut genera feedback pentru acest cod.';
        setFeedback(errorMessage);
        announce(errorMessage, 'assertive');
        toast.error('Eroare la generarea feedback-ului');
      }
    } catch (error) {
      const errorMessage = `Eroare la ob»õinerea feedback-ului: ${error.message}`;
      setFeedback(errorMessage);
      announce(errorMessage, 'assertive');
      toast.error('Eroare de comunicare');
    } finally {
      setIsAnalyzing(false);
    }
  };

  // Gestionarea schimbƒÉrilor √Æn editor
  const handleCodeChange = (event) => {
    const newCode = event.target.value;
    setCode(newCode);
    
    // CalculƒÉm linia curentƒÉ
    const cursorPos = event.target.selectionStart;
    const textBeforeCursor = newCode.substring(0, cursorPos);
    const lineNumber = (textBeforeCursor.match(/\n/g) || []).length;
    setCurrentLine(lineNumber);
    setCursorPosition(cursorPos);
    
    if (onChange) {
      onChange(newCode);
    }
  };

  // Gestionarea navigƒÉrii cu tastatura
  const handleKeyDown = (event) => {
    handleKeyboardNavigation(event, 'code-editor', {
      runCode: runCodeWithAccessibility,
      getAIFeedback: getAIFeedbackAccessible,
      navigateToMethod: navigateToNextMethod,
      navigateToVariable: navigateToNextVariable,
      readCurrentLine: readCurrentLineSemanticaly,
      showAccessibilityHelp: showAccessibilityHelp,
      describeStructure: describeCodeStructure
    });
  };

  // Resetarea codului
  const handleResetCode = () => {
    if (window.confirm('Sigur dore»ôti sƒÉ resetezi codul? Toate modificƒÉrile vor fi pierdute.')) {
      const codeToReset = initialCode || defaultCode;
      setCode(codeToReset);
      announce('Codul a fost resetat la versiunea ini»õialƒÉ', 'assertive');
      toast.info('Codul a fost resetat.');
    }
  };

  useEffect(() => {
    if (initialCode && initialCode !== code) {
      setCode(initialCode);
    }
  }, [initialCode, editorKey]);

  // Anun»õƒÉm c√¢nd editorul este gata
  useEffect(() => {
    if (isScreenReaderActive) {
      announce('Editor de cod Java √ÆncƒÉrcat »ôi gata pentru utilizare. ApasƒÉ Control+Shift+H pentru ajutor.', 'polite');
    }
  }, [isScreenReaderActive, announce]);

  return (
    <div className="accessible-code-editor-container">
      {/* Instruc»õiuni pentru screen reader */}
      <div id="editor-instructions" className="sr-only">
        Editor de cod Java cu suport complet pentru accessibility.
        Folose»ôte Control+Shift+H pentru lista completƒÉ de comenzi.
        Linia curentƒÉ: {currentLine + 1}.
        Pozi»õia cursorului: {cursorPosition}.
      </div>

      {/* Header cu informa»õii »ôi ac»õiuni */}
      <div className="editor-header" role="banner">
        <h3 id="editor-title">Editor de cod Java accesibil</h3>
        <div 
          className="editor-status" 
          aria-live="polite"
          aria-label={`Linia ${currentLine + 1}, pozi»õia ${cursorPosition}`}
        >
          Linia {currentLine + 1} / Pozi»õia {cursorPosition}
        </div>
        <div className="editor-actions" role="toolbar" aria-label="Ac»õiuni editor">
          <button 
            onClick={handleResetCode}
            aria-label="ReseteazƒÉ codul la versiunea ini»õialƒÉ"
            className="action-button reset-button"
          >
            üîÑ ReseteazƒÉ
          </button>
          <button 
            onClick={getAIFeedbackAccessible}
            disabled={isAnalyzing}
            aria-label="Ob»õine feedback AI vocal pentru cod"
            className="action-button ai-button"
          >
            {isAnalyzing ? '‚è≥ Se analizeazƒÉ...' : 'ü§ñ Feedback AI'}
          </button>
          <button 
            onClick={runCodeWithAccessibility}
            disabled={isRunning}
            aria-label="ExecutƒÉ codul Java"
            className="action-button run-button"
          >
            {isRunning ? '‚è≥ Se executƒÉ...' : '‚ñ∂Ô∏è RuleazƒÉ'}
          </button>
        </div>
      </div>

      {/* Editorul principal */}
      <div className="editor-wrapper" role="main">
        <label htmlFor="code-textarea" className="sr-only">
          Editor de cod Java. Folose»ôte Control+Shift pentru comenzi speciale de accessibility.
        </label>
        <textarea
          ref={editorRef}
          id="code-textarea"
          className="code-textarea"
          value={code}
          onChange={handleCodeChange}
          onKeyDown={handleKeyDown}
          role="textbox"
          aria-label="Editor de cod Java"
          aria-multiline="true"
          aria-describedby="editor-instructions"
          spellCheck="false"
          autoComplete="off"
          rows={20}
          cols={80}
        />
      </div>

      {/* Zona de output */}
      <div className="output-container" role="region" aria-label="Rezultatul execu»õiei">
        <div className="output-header">
          <h4 id="output-title">Rezultatul execu»õiei</h4>
        </div>
        <pre 
          className="output-content"
          aria-labelledby="output-title"
          aria-live="polite"
        >
          {output || 'RuleazƒÉ codul pentru a vedea rezultatul'}
        </pre>
      </div>

      {/* Zona de feedback AI */}
      {showFeedback && (
        <div 
          className="feedback-container" 
          role="region" 
          aria-label="Feedback AI pentru cod"
          aria-live="polite"
        >
          <div className="feedback-header">
            <h4 id="feedback-title">Feedback AI</h4>
            <button 
              className="close-feedback" 
              onClick={() => setShowFeedback(false)}
              aria-label="√énchide feedback AI"
            >
              &times;
            </button>
          </div>
          <div 
            className="feedback-content"
            aria-labelledby="feedback-title"
          >
            {feedback}
          </div>
        </div>
      )}
    </div>
  );
};

export default AccessibleCodeEditor;
