import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/CourseDetailPage.css';  // Asigură-te că stilul original este aplicat

function CourseDetailPage() {
  const { courseId, lessonId } = useParams(); // Obținem courseId și lessonId din URL
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentLessonIndex, setCurrentLessonIndex] = useState(parseInt(lessonId) || 0); // Începe de la lecția 0
  const [isPreview, setIsPreview] = useState(true); // Gestionăm vizualizarea previzualizării
  const [openLessonIndex, setOpenLessonIndex] = useState(null); // Starea pentru lecțiile deschise

  useEffect(() => {
    fetch(`http://localhost:5000/api/courses/${courseId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.course) {
          setCourse(data.course);
          setLoading(false);
        }
      })
      .catch(() => setLoading(false)); // Tratează erorile de rețea
  }, [courseId]);

  const handleStartCourse = () => {
    setIsPreview(false); // După ce utilizatorul apasă "Start Course", trecem la lecția 1
    setCurrentLessonIndex(0); // Setăm lecția la 0 (lecția 1)
    navigate(`/courses/${courseId}/lesson/0`); // Navigăm la lecția 1
  };

  const handleNextLesson = () => {
    if (currentLessonIndex < course.content.length - 1) {
      setCurrentLessonIndex(currentLessonIndex + 1);
      navigate(`/courses/${courseId}/lesson/${currentLessonIndex + 1}`);
    }
  };

  const handleBackToCourses = () => {
    navigate('/courses'); // Navigăm înapoi la lista de cursuri
  };

  // Funcția pentru a deschide/închide lecțiile pentru previzualizare
  const toggleLessonPreview = (index) => {
    setOpenLessonIndex(openLessonIndex === index ? null : index); // Toggle între deschidere/închidere
  };

  if (loading) return <div>Loading...</div>;
  if (!course) return <div>Course not found.</div>;

  const currentLesson = course.content[currentLessonIndex];

  return (
    <div className="course-detail-page">
      <h1 className="course-detail-title">{course.name}</h1>
      <p className="course-detail-description">{course.description}</p>

      {isPreview ? (
        <>
          <h3 className="course-detail-content-title">Course Content:</h3>
          <div className="course-preview">
            {course.content.map((lesson, index) => (
              <div key={index} className="course-preview-item">
                <div onClick={() => toggleLessonPreview(index)} className="lesson-title">
                  {lesson.title}
                </div>

                {/* Dropdown-ul pentru lecția selectată */}
                {openLessonIndex === index && (
                  <div className="lesson-preview">
                    <p>{lesson.content}</p>
                  </div>
                )}
              </div>
            ))}
          </div>
          <div className="course-buttons">
            <button className="back-to-courses-btn" onClick={handleBackToCourses}>
              Back to Courses
            </button>
            <button className="start-course-btn" onClick={handleStartCourse}>
              Start Course
            </button>
          </div>
        </>
      ) : (
        <>
          <h3 className="course-detail-content-title">Course Content:</h3>
          <div className="course-detail-content">
            <h4>{currentLesson.title}</h4>
            <p>{currentLesson.content}</p>
            <div className="course-lesson-text">
              {currentLesson.examples.map((example, index) => (
                <div key={index}>
                  <pre>{example.code}</pre>
                  <p>{example.explanation}</p>
                </div>
              ))}
            </div>
          </div>
          <div className="course-buttons">
            <button className="back-btn" onClick={() => navigate(`/courses/${courseId}/lesson/${currentLessonIndex - 1}`)}>
              Back
            </button>

            {currentLessonIndex < course.content.length - 1 ? (
              <button className="next-lesson-btn" onClick={handleNextLesson}>
                Next Lesson
              </button>
            ) : (
              <p>You've completed all lessons in this course!</p>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default CourseDetailPage;
