import React, { useState, useEffect, useRef } from 'react';
import { useToast } from './ToastProvider';
import '../styles/CodeEditor.css';
import Button from './Button';

function CodeEditor({ initialCode, lessonId, onChange }) {
  const defaultCode = `public class Main {
    public static void main(String[] args) {
        // Scrie codul tÄƒu aici
        System.out.println("Bine ai venit la JavaVerse!");
    }
}`;

  const [output, setOutput] = useState('');
  const [isRunning, setIsRunning] = useState(false);
  const [isEditorReady, setIsEditorReady] = useState(false);
  const [isEditorLoading, setIsEditorLoading] = useState(true);
  const [feedback, setFeedback] = useState('');
  const [showFeedback, setShowFeedback] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const monacoRef = useRef(null);
  const editorRef = useRef(null);
  const initialCodeSetRef = useRef(false);
  const toast = useToast();

  // Debugging: SÄƒ vedem ce se Ã®ntÃ¢mplÄƒ
  useEffect(() => {
    console.log('ðŸ” CodeEditor: initialCode changed to:', initialCode?.substring(0, 50));
  }, [initialCode]);

  // IniÈ›ializarea Monaco Editor
  useEffect(() => {
    let isMounted = true;
    
    const loadMonacoEditor = async () => {
      try {
        console.log('ðŸ“¦ Loading Monaco Editor...');
        setIsEditorLoading(true);
        
        const monaco = await import('@monaco-editor/react');
        
        if (isMounted) {
          console.log('âœ… Monaco Editor loaded successfully');
          monacoRef.current = monaco;
          setIsEditorLoading(false);
          setIsEditorReady(true);
        }
      } catch (error) {
        console.error('âŒ Error loading Monaco Editor:', error);
        if (isMounted) {
          setIsEditorLoading(false);
          toast.error('Nu s-a putut Ã®ncÄƒrca editorul de cod.');
        }
      }
    };

    loadMonacoEditor();
    
    return () => {
      isMounted = false;
      if (editorRef.current) {
        editorRef.current.dispose();
      }
    };
  }, [toast]);

  const handleEditorDidMount = (editor) => {
    console.log('ðŸš€ Editor mounted');
    editorRef.current = editor;
    setIsEditorReady(true);
    setIsEditorLoading(false);

    // SetÄƒm codul iniÈ›ial DOAR o datÄƒ
    if (!initialCodeSetRef.current) {
      const codeToSet = initialCode || defaultCode;
      console.log('ðŸ“ Setting initial code:', codeToSet.substring(0, 50));
      editor.setValue(codeToSet);
      initialCodeSetRef.current = true;
    }

    editor.onDidChangeModelContent(() => {
      console.log('ðŸ“ Editor content changed by user');
    });

    editor.focus();
  };

  // FuncÈ›ie pentru a obÈ›ine codul curent din editor
  const getCurrentCode = () => {
    if (editorRef.current) {
      const code = editorRef.current.getValue();
      console.log('ðŸ“– Getting current code:', code.substring(0, 50));
      return code;
    }
    console.log('âš ï¸ Editor not ready, returning default');
    return initialCode || defaultCode;
  };

  const handleRunCode = async () => {
    console.log('ðŸƒ Running code...');
    
    if (!isEditorReady || !editorRef.current) {
      console.log('âš ï¸ Editor not ready');
      toast.warning('Editorul nu este pregÄƒtit Ã®ncÄƒ. Te rugÄƒm sÄƒ aÈ™tepÈ›i.');
      return;
    }

    // ObÈ›inem codul EXACT din editor
    const codeToRun = getCurrentCode();
    console.log('ðŸƒ Code to run:', codeToRun.substring(0, 100));
    
    setIsRunning(true);
    setOutput('Se executÄƒ codul...');

    // Simulare simplÄƒ pentru debugging
    setTimeout(() => {
      console.log('âœ… Code execution completed');
      
      if (codeToRun.includes('System.out.println')) {
        setOutput('Hello, JavaVerse!\nCodul a fost executat cu succes!');
      } else {
        setOutput('ExecuÈ›ie reuÈ™itÄƒ! (fÄƒrÄƒ output specific)');
      }
      
      setIsRunning(false);
      toast.success('Codul a fost executat cu succes!');
    }, 1000);
  };

  const handleResetCode = () => {
    if (window.confirm('Sigur doreÈ™ti sÄƒ resetezi codul? Toate modificÄƒrile vor fi pierdute.')) {
      const codeToReset = initialCode || defaultCode;
      console.log('ðŸ”„ Resetting code to:', codeToReset.substring(0, 50));
      if (editorRef.current) {
        editorRef.current.setValue(codeToReset);
      }
      toast.info('Codul a fost resetat.');
    }
  };

  return (
    <div className="code-editor-container">
      <div className="editor-header">
        <h3>Editor de cod Java</h3>
        <div className="editor-actions">
          <Button 
            variant="secondary"
            size="small"
            onClick={handleResetCode}
            icon="ðŸ”„"
            iconPosition="left"
          >
            ReseteazÄƒ
          </Button>
          <Button 
            variant="primary"
            size="small"
            onClick={handleRunCode}
            disabled={isRunning || !isEditorReady}
            icon={isRunning ? "â³" : "â–¶ï¸"}
            iconPosition="left"
          >
            {isRunning ? 'Se executÄƒ...' : 'RuleazÄƒ codul'}
          </Button>
        </div>
      </div>

      <div className="editor-wrapper">
        {isEditorLoading ? (
          <div className="editor-loading">
            <div className="loading-spinner"></div>
            <p>Se Ã®ncarcÄƒ editorul...</p>
          </div>
        ) : monacoRef.current ? (
          <monacoRef.current.default
            height="400px"
            language="java"
            theme="vs-dark"
            defaultValue={initialCode || defaultCode}
            onMount={handleEditorDidMount}
            options={{
              minimap: { enabled: false },
              fontSize: 14,
              scrollBeyondLastLine: false,
              automaticLayout: true,
              tabSize: 2,
              folding: true,
              lineNumbers: 'on',
              renderLineHighlight: 'all',
              wordWrap: 'on',
              formatOnPaste: true,
              formatOnType: true
            }}
          />
        ) : (
          <div className="editor-fallback">
            <p>Nu s-a putut Ã®ncÄƒrca editorul Monaco.</p>
            <textarea
              defaultValue={initialCode || defaultCode}
              className="fallback-editor"
              rows="20"
              placeholder="Scrie codul tÄƒu Java aici..."
            />
          </div>
        )}
      </div>

      <div className="output-container">
        <div className="output-header">
          <h4>Rezultat</h4>
        </div>
        <pre className="output-content">{output}</pre>
      </div>
    </div>
  );
}

export default CodeEditor;
