import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import MainPage from './components/MainPage';
import Footer from './components/Footer';
import Login from './components/Login';
import Register from './components/Register';
import CoursesPage from './components/CoursesPage';
import CourseDetailPage from './components/CourseDetailPage';
import Quiz from './components/Quiz';
import Profile from './components/Profile';
import CodePlayground from './components/CodePlayground';
import StarsBackground from './components/StarsBackground';
import { ToastProvider } from './components/ToastProvider';
import { AccessibilityProvider } from './components/AccessibilityProvider';
import './styles/theme.css';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // SCHIMBARE: Verificăm sessionStorage în loc de localStorage
    const token = sessionStorage.getItem('authToken');
    if (token) {
      setIsAuthenticated(true);
    }
  }, []);

  const handleLogout = () => {
    // SCHIMBARE: Ștergem din sessionStorage în loc de localStorage
    sessionStorage.removeItem('authToken');
    sessionStorage.removeItem('userId');
    setIsAuthenticated(false);
    window.location.href = '/';
  };

  return (
  <AccessibilityProvider>
  <ToastProvider>
    <Router>
      <div className="App">
        {/* Adăugăm fundalul cu stele peste întreaga aplicație */}
        <StarsBackground />

        <Header isAuthenticated={isAuthenticated} handleLogout={handleLogout} />
        <div className="body-content">
          <Routes>
            <Route path="/" element={isAuthenticated ? <CoursesPage /> : <MainPage />} />
            <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
            <Route path="/register" element={<Register />} />
            <Route path="/courses" element={<CoursesPage />} />
            <Route path="/courses/:courseId/lesson/:lessonId" element={<CourseDetailPage />} />
            <Route path="/courses/:courseId/test" element={<Quiz />} />
            <Route path="/profile" element={isAuthenticated ? <Profile /> : <Login setIsAuthenticated={setIsAuthenticated} />} />
            <Route path="/playground" element={<CodePlayground />} />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  </ToastProvider>
  </AccessibilityProvider>
  );
}

export default App;
