    }
    const targetElement = elements[nextIndex];
    const globalIndex = this.focusableElements.indexOf(targetElement);
    if (globalIndex !== -1) {
      this.currentFocusIndex = globalIndex;
      this.setFocus(targetElement);
    }
  }

  // Ajustare viteză citire
  adjustSpeed() {
    const speeds = [0.7, 0.85, 1.0, 1.15, 1.3];
    const currentIndex = speeds.indexOf(this.readingSpeed);
    const nextIndex = (currentIndex + 1) % speeds.length;
    this.readingSpeed = speeds[nextIndex];
    this.speak(`Viteză de citire: ${Math.round(this.readingSpeed * 100)}%`, 'high');
  }

  // Îmbunătățire pentru speak cu viteză ajustabilă
  speak(text, priority = 'normal') {
    if (!text || text.trim() === '') return;
    if (priority === 'high') {
      this.stopReading();
    }
    const utterance = new SpeechSynthesisUtterance(text);
    if (this.voice) {
      utterance.voice = this.voice;
    }
    utterance.rate = this.readingSpeed;
    utterance.pitch = 1.0;
    utterance.volume = 1.0;
    utterance.onstart = () => {
      this.isReading = true;
      this.currentUtterance = utterance;

      // Trimitem event pentru UI
      document.dispatchEvent(new CustomEvent('speechStatusChanged', {
        detail: {
          isSpeaking: true
        }
      }));
    };
    utterance.onend = () => {
      this.isReading = false;
      this.currentUtterance = null;
      this.processQueue();
      document.dispatchEvent(new CustomEvent('speechStatusChanged', {
        detail: {
          isSpeaking: false
        }
      }));
    };
    utterance.onerror = e => {
      console.error('Speech synthesis error:', e);
      this.isReading = false;
      this.currentUtterance = null;
    };
    if (this.isReading && priority !== 'high') {
      this.readingQueue.push(utterance);
    } else {
      this.speechSynthesis.speak(utterance);
    }
  }

  // Navigare înapoi
  navigateBack() {
    if (this.navigationHistory.length > 0) {
      const previousUrl = this.navigationHistory.pop();
      this.speak("Navighez înapoi", 'high');
      window.location.href = previousUrl;
    } else {
      this.speak("Nu există istoric de navigare", 'high');
    }
  }

  // Listare elemente
  listAllElements() {
    this.updateFocusableElements();
    const totalElements = this.focusableElements.length;
    const currentPosition = this.currentFocusIndex + 1;
    let summary = `Pagina conține ${totalElements} elemente interactive. `;
    summary += `Ești la elementul ${currentPosition} din ${totalElements}. `;

    // Numărăm tipurile de elemente
    const counts = {
      buttons: document.querySelectorAll('button, [role="button"]').length,
      links: document.querySelectorAll('a[href]').length,
      inputs: document.querySelectorAll('input, textarea, select').length,
      headings: document.querySelectorAll('h1, h2, h3, h4, h5, h6').length
    };
    summary += `Găsit: ${counts.buttons} butoane, ${counts.links} linkuri, `;
    summary += `${counts.inputs} câmpuri de input, ${counts.headings} titluri.`;
    this.speak(summary, 'high');
  }

  // Navigare rapidă pentru pagini
  navigateToCourses() {
    this.navigationHistory.push(window.location.href);
    this.speak("Navighez la cursuri", 'high');
    window.location.href = '/courses';
  }
  navigateToPlayground() {
    this.navigationHistory.push(window.location.href);
    this.speak("Navighez la playground", 'high');
    window.location.href = '/playground';
  }
  navigateToProfile() {
    this.navigationHistory.push(window.location.href);
    this.speak("Navighez la profil", 'high');
    window.location.href = '/profile';
  }
  goHome() {
    this.navigationHistory.push(window.location.href);
    this.speak("Navighez la pagina principală", 'high');
    window.location.href = '/';
  }

  // Funcții pentru quiz
  selectQuizOption(index) {
    const options = document.querySelectorAll('.question-container li');
    if (options[index]) {
      options[index].click();
      this.speak(`Selectat răspunsul ${index + 1}`, 'high');
    } else {
      this.speak(`Nu există răspunsul ${index + 1}`, 'high');
    }
  }

  // Funcții pentru editor
  executeCode() {
    const runButton = document.querySelector('.editor-actions button[aria-label*="Execută"]');
    if (runButton) {
      runButton.click();
      this.speak("Execut codul", 'high');
    } else {
      this.speak("Butonul de execuție nu a fost găsit", 'high');
    }
  }
  getFeedback() {
    const feedbackButton = document.querySelector('.editor-actions button[aria-label*="feedback"]');
    if (feedbackButton) {
      feedbackButton.click();
      this.speak("Solicit feedback AI", 'high');
    } else {
      this.speak("Butonul de feedback nu a fost găsit", 'high');
    }
  }

  // Citirea informațiilor despre pagina curentă
  readNavigationInfo() {
    const currentUrl = window.location.pathname;
    const totalElements = this.focusableElements.length;
    const position = this.currentFocusIndex + 1;
    let info = `Ești pe ${this.getPageName(currentUrl)}. `;
    info += `Elementul curent: ${position} din ${totalElements}. `;
    info += `Folosește Tab pentru navigare sau apasă H pentru ajutor.`;
    this.speak(info, 'high');
  }
  getPageName(url) {
    if (url === '/' || url === '') return 'pagina principală';
    if (url.includes('/courses')) return 'pagina de cursuri';
    if (url.includes('/playground')) return 'playground-ul de cod';
    if (url.includes('/profile')) return 'pagina de profil';
    if (url.includes('/login')) return 'pagina de autentificare';
    if (url.includes('/register')) return 'pagina de înregistrare';
    if (url.includes('/quiz')) return 'pagina de test';
    return 'pagina curentă';
  }

  // Metodă îmbunătățită pentru citirea help-ului
  readHelp() {
    const currentPage = this.getPageName(window.location.pathname);
    const helpText = `
      Ajutor pentru ${currentPage}.

      Comenzi esențiale:
      - Tab și Shift Tab pentru navigare înainte și înapoi
      - Enter pentru a activa elementul selectat
      - Spațiu pentru pauză sau continuare citire
      - Escape pentru oprire citire

      Navigare rapidă:
      - Tastele 1, 2, 3 pentru header, conținut principal, footer
      - C pentru cursuri, P pentru playground, R pentru profil, H pentru acasă

      Navigare prin tipuri:
      - B pentru butoane, L pentru linkuri
      - I pentru câmpuri input, T pentru titluri

      Comenzi speciale cu Alt:
      - Alt H pentru acest ajutor
      - Alt M pentru citirea paginii
      - Alt N pentru informații navigare
      - Alt S pentru viteză citire
      - Alt B pentru înapoi
      - Alt L pentru lista elementelor

      ${this.getPageSpecificHelp(currentPage)}
    `;
    this.speak(helpText, 'high');
  }
  getPageSpecificHelp(pageName) {
    switch (pageName) {
      case 'pagina de cursuri':
        return 'În această pagină: Navighează prin cursuri cu Tab. Apasă Enter pentru a selecta un curs.';
      case 'playground-ul de cod':
        return 'În editor: Alt E pentru execuție, Alt F pentru feedback AI. Tab citește linia curentă.';
      case 'pagina de test':
        return 'În test: Alt 1-4 pentru răspunsuri. Enter pentru verificare sau următoarea întrebare.';
      case 'pagina de profil':
        return 'În profil: Navighează prin secțiuni cu Tab. Enter pentru a modifica setările.';
      default:
        return '';
    }
  }
  handleAccessibilityShortcuts(e) {
    const key = this.getShortcutKey(e);
    const handler = this.shortcuts.get(key);
    if (handler) {
      e.preventDefault();
      handler();
    }
  }
  getShortcutKey(e) {
    const parts = [];
    if (e.ctrlKey) parts.push('ctrl');
    if (e.altKey) parts.push('alt');
    if (e.shiftKey) parts.push('Shift');

    // Tratăm special anumite taste
    let key = e.key;
    if (key === ' ') key = ' ';
    if (key.length === 1) key = key.toLowerCase();
    parts.push(key);
    return parts.join('+');
  }
  addAccessibilityStyles() {
    const style = document.createElement('style');
    style.id = 'accessibility-styles';
    style.textContent = `
      /* Stiluri pentru modul nevăzători */
      body.accessibility-mode {
        background: #000000 !important;
        color: #ffffff !important;
        font-size: 20px !important;
        line-height: 1.8 !important;
        font-family: Arial, sans-serif !important;
      }

      body.accessibility-mode * {
        animation: none !important;
        transition: none !important;
      }

      accessibility-focus {
       outline: 4px solid #ffff00 !important;
       outline-offset: 4px !important;
       background-color: #000033 !important;
       color: #ffffff !important;
       box-shadow: 0 0 20px #ffff00 !important;
       border-radius: 4px !important;
       position: relative !important;
       z-index: 9999 !important;
     }

     /* Ascundem elementele decorative */
     body.accessibility-mode .stars-background,
     body.accessibility-mode .planet,
     body.accessibility-mode .asteroid,
     body.accessibility-mode .shooting-star,
     body.accessibility-mode .code-snippet,
     body.accessibility-mode .cosmic-dust,
     body.accessibility-mode .twinkling-star {
       display: none !important;
     }

     /* Simplificăm layout-ul */
     body.accessibility-mode .main-banner {
       background: #000000 !important;
       min-height: auto !important;
       padding: 20px !important;
     }

     /* Text mai vizibil */
     body.accessibility-mode h1,
     body.accessibility-mode h2,
     body.accessibility-mode h3,
     body.accessibility-mode h4,
     body.accessibility-mode h5,
     body.accessibility-mode h6 {
       color: #ffffff !important;
       text-decoration: underline !important;
       margin: 20px 0 !important;
     }

     body.accessibility-mode p,
     body.accessibility-mode span,
     body.accessibility-mode div {
       color: #ffffff !important;
     }

     /* Butoane mai vizibile */
     body.accessibility-mode button,
     body.accessibility-mode .button,
     body.accessibility-mode [role="button"] {
       background: #333333 !important;
       color: #ffffff !important;
       border: 2px solid #ffffff !important;
       padding: 15px 20px !important;
       font-size: 18px !important;
       font-weight: bold !important;
       text-transform: uppercase !important;
     }

     body.accessibility-mode button:hover,
     body.accessibility-mode button:focus {
       background: #555555 !important;
     }

     /* Linkuri mai vizibile */
     body.accessibility-mode a {
       color: #00ffff !important;
       text-decoration: underline !important;
       font-weight: bold !important;
     }

     body.accessibility-mode a:hover,
     body.accessibility-mode a:focus {
       background: #333333 !important;
       padding: 2px 4px !important;
     }

     /* Inputuri mai clare */
     body.accessibility-mode input,
     body.accessibility-mode textarea,
     body.accessibility-mode select {
       background: #111111 !important;
       color: #ffffff !important;
       border: 2px solid #ffffff !important;
       padding: 10px !important;
       font-size: 18px !important;
     }

     body.accessibility-mode input:focus,
     body.accessibility-mode textarea:focus,
     body.accessibility-mode select:focus {
       border-color: #ffff00 !important;
       outline: 2px solid #ffff00 !important;
     }

     /* Simplificăm cardurile */
     body.accessibility-mode .course-card,
     body.accessibility-mode .feature-card,
     body.accessibility-mode .example-card {
       background: #111111 !important;
       border: 2px solid #ffffff !important;
       margin: 10px 0 !important;
       padding: 20px !important;
     }

     /* Eliminăm gradiente și efecte */
     body.accessibility-mode [class*="gradient"] {
       background: #000000 !important;
     }

     /* Indicator pentru elementul curent citit */
     .accessibility-reading {
       background-color: #333300 !important;
       outline: 2px dashed #ffff00 !important;
     }

     /* Mesaje de ajutor vizibile */
     .accessibility-help-popup {
       position: fixed;
       bottom: 20px;
       right: 20px;
       background: #000000;
       color: #ffffff;
       border: 2px solid #ffff00;
       padding: 20px;
       max-width: 400px;
       font-size: 16px;
       z-index: 10000;
       border-radius: 8px;
     }

     /* Simplificăm navigarea */
     body.accessibility-mode nav {
       background: #000000 !important;
       border: 2px solid #ffffff !important;
       padding: 10px !important;
     }

     /* Lista de elemente focusabile */
     .accessibility-element-list {
       position: fixed;
       left: 0;
       top: 0;
       width: 300px;
       height: 100%;
       background: #000000;
       border-right: 2px solid #ffffff;
       padding: 20px;
       overflow-y: auto;
       z-index: 9998;
       display: none;
     }

     body.accessibility-mode.show-element-list .accessibility-element-list {
       display: block;
     }
   `;
    document.head.appendChild(style);
    document.body.classList.add('accessibility-mode');
  }
  removeAccessibilityStyles() {
    const style = document.getElementById('accessibility-styles');
    if (style) style.remove();
    document.body.classList.remove('accessibility-mode');

    // Eliminăm toate clasele de focus
    document.querySelectorAll('.accessibility-focus, .accessibility-reading').forEach(el => {
      el.classList.remove('accessibility-focus', 'accessibility-reading');
    });
  }

  // Adaugă aceste metode în clasa AccessibilityManager (în src/utils/AccessibilityManager.js)

  stopReading() {
    if (this.speechSynthesis.speaking) {
      this.speechSynthesis.cancel();
    }
    this.isReading = false;
    this.currentUtterance = null;
    this.readingQueue = [];

    // Trimitem event pentru UI
    document.dispatchEvent(new CustomEvent('speechStatusChanged', {
      detail: {
        isSpeaking: false
      }
    }));
  }
  processQueue() {
    if (this.readingQueue.length > 0 && !this.isReading) {
      const nextUtterance = this.readingQueue.shift();
      this.speechSynthesis.speak(nextUtterance);
    }
  }
  toggleReading() {
    if (this.isReading && this.currentUtterance) {
      if (this.speechSynthesis.paused) {
        this.speechSynthesis.resume();
        this.speak("Reluare citire", 'normal');
      } else {
        this.speechSynthesis.pause();
        this.speak("Pauză citire", 'normal');
      }
    } else {
      this.readCurrentPage();
    }
  }
  readCurrentPage() {
    const pageContent = this.extractPageContent();
    if (pageContent) {
      this.speak(pageContent, 'high');
    } else {
      this.speak("Nu există conținut de citit pe această pagină", 'high');
    }
  }
  extractPageContent() {
    let content = '';

    // Extragem titlul paginii
    const title = document.querySelector('h1');
    if (title) {
      content += `Titlu: ${title.textContent.trim()}. `;
    }

    // Extragem descrierea sau subtitlul
    const description = document.querySelector('.page-description, .course-detail-description, .code-playground-description');
    if (description) {
      content += `${description.textContent.trim()}. `;
    }

    // Extragem conținutul principal
    const mainContent = document.querySelector('main, .body-content, .course-detail-content');
    if (mainContent) {
      // Extragem doar textul relevant, ignorând butoanele și alte elemente interactive
      const paragraphs = mainContent.querySelectorAll('p, h2, h3, h4, li');
      paragraphs.forEach(p => {
        const text = p.textContent.trim();
        if (text && text.length > 0) {
          content += `${text}. `;
        }
      });
    }

    // Dacă suntem pe o pagină de curs, citim și lecțiile
    const lessons = document.querySelectorAll('.course-preview-item, .lesson-title');
    if (lessons.length > 0) {
      content += 'Lecții disponibile: ';
      lessons.forEach((lesson, index) => {
        content += `${index + 1}. ${lesson.textContent.trim()}. `;
      });
    }

    // Dacă suntem pe o pagină de test, citim întrebarea curentă
    const currentQuestion = document.querySelector('.question-container h3');
    if (currentQuestion) {
      content += `Întrebare: ${currentQuestion.textContent.trim()}. `;
      const options = document.querySelectorAll('.question-container li label');
      if (options.length > 0) {
        content += 'Opțiuni: ';
        options.forEach((option, index) => {
          content += `${index + 1}. ${option.textContent.trim()}. `;
        });
      }
    }
    return content.trim();
  }

  // Adaugă și această metodă pentru anunțuri
  announce(message, priority = 'normal') {
    this.speak(message, priority);
  }

  // Adaugă getter pentru proprietatea isSpeaking
  get isSpeaking() {
    return this.isReading || this.speechSynthesis.speaking;
  }

  // Adaugă getter pentru proprietatea isActive
  get isActive() {
    return this.isAccessibilityMode;
  }

  // Metodă pentru citirea întrebărilor de quiz
  readQuizQuestion(question, options) {
    let text = `Întrebare: ${question}. `;
    if (options && options.length > 0) {
      text += 'Variantele de răspuns sunt: ';
      options.forEach((option, index) => {
        text += `${index + 1}: ${option}. `;
      });
    }
    this.speak(text, 'high');
  }

  // Metodă pentru anunțarea rezultatului quiz-ului
  announceQuizResult(score, totalQuestions) {
    const percentage = Math.round(score / totalQuestions * 100);
    const text = `Test finalizat! Ai răspuns corect la ${score} din ${totalQuestions} întrebări. ` + `Scorul tău este ${percentage}%. ` + (percentage >= 80 ? 'Felicitări! Rezultat excelent!' : percentage >= 60 ? 'Rezultat bun! Continuă să înveți.' : 'Nu te descuraja! Încearcă din nou pentru un rezultat mai bun.');
    this.speak(text, 'high');
  }

  // Metodă pentru citirea liniilor de cod
  readCodeLine(lineNumber) {
    const editor = document.querySelector('.monaco-editor');
    if (editor) {
      // Implementare simplificată - în practică ar trebui să accesăm API-ul Monaco
      this.speak(`Linia ${lineNumber} din editor`, 'normal');
    }
  }

  // Metodă pentru anunțarea schimbărilor în cod
  announceCodeChange(newCode, position) {
    if (position && position.line) {
      this.speak(`Cod modificat la linia ${position.line}`, 'normal');
    }
  }
}

// Exportăm o instanță singleton
const accessibilityManager = new AccessibilityManager();
export default accessibilityManager;

