// src/utils/AccessibilityManager.js
class AccessibilityManager {
  constructor() {
    this.isAccessibilityMode = false;
    this.speechSynthesis = window.speechSynthesis;
    this.currentUtterance = null;
    this.voice = null;
    this.isReading = false;
    this.readingQueue = [];
    this.shortcuts = new Map();
    this.focusedElement = null;
    this.navigationMode = 'normal';
    this.currentFocusIndex = 0;
    this.focusableElements = [];
    this.navigationHistory = [];
    this.readingSpeed = 1.0;
    this.isTyping = false; // Flag pentru modul de tastare
    this.typingBuffer = ''; // Buffer pentru caractere tastate
    this.lastTypedTime = 0; // Timestamp pentru ultimul caracter
    this.currentLineContent = ''; // Conținutul liniei curente
    
    this.initializeVoice();
    this.setupGlobalListeners();
    this.registerShortcuts();
  }

  initializeVoice() {
    const setVoice = () => {
      const voices = this.speechSynthesis.getVoices();
      // Prioritizăm vocile în română
      this.voice = voices.find(voice => voice.lang.startsWith('ro-RO')) || 
                   voices.find(voice => voice.lang.startsWith('ro')) || 
                   voices[0];
      console.log('Voice initialized:', this.voice?.name || 'Default');
    };

    if (this.speechSynthesis.getVoices().length > 0) {
      setVoice();
    } else {
      this.speechSynthesis.addEventListener('voiceschanged', setVoice);
    }
  }

  setupGlobalListeners() {
    document.addEventListener('keydown', (e) => {
      // Alt + A pentru activarea modului (evităm Ctrl+Shift care poate avea conflicte)
      if (e.altKey && e.key === 'a') {
        e.preventDefault();
        e.stopPropagation();
        this.toggleAccessibilityMode();
        return;
      }

      if (!this.isAccessibilityMode) return;

      // Prevenim comportamentul default pentru taste speciale
      if (['Tab', 'Enter', ' ', 'ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'].includes(e.key)) {
        e.preventDefault();
        e.stopPropagation();
      }

      this.handleAccessibilityShortcuts(e);
    });

    // Actualizăm lista de elemente focusabile când se schimbă DOM-ul
    const observer = new MutationObserver(() => {
      if (this.isAccessibilityMode) {
        this.updateFocusableElements();
      }
    });

    observer.observe(document.body, {
      childList: true,
      subtree: true
    });
  }

  registerShortcuts() {
    // Comenzi pentru modul NAVIGARE (când NU suntem în input)
    this.navigationShortcuts = new Map();
    
    // Navigare principală
    this.navigationShortcuts.set('Tab', () => this.navigateNext());
    this.navigationShortcuts.set('Shift+Tab', () => this.navigatePrevious());
    this.navigationShortcuts.set('Enter', () => this.activateElement());
    this.navigationShortcuts.set('Escape', () => this.stopReading());
    
    // Navigare rapidă cu F-keys pentru a evita conflictele
    this.navigationShortcuts.set('F1', () => this.readHelp());
    this.navigationShortcuts.set('F2', () => this.navigateToSection('header'));
    this.navigationShortcuts.set('F3', () => this.navigateToSection('main'));
    this.navigationShortcuts.set('F4', () => this.navigateToSection('footer'));
    
    // Navigare cu Ctrl+ pentru pagini
    this.navigationShortcuts.set('Ctrl+Shift+C', () => this.navigateToCourses());
    this.navigationShortcuts.set('Ctrl+Shift+P', () => this.navigateToPlayground());
    this.navigationShortcuts.set('Ctrl+Shift+R', () => this.navigateToProfile());
    this.navigationShortcuts.set('Ctrl+Shift+H', () => this.goHome());
    
    // Navigare prin elemente cu Ctrl+
    this.navigationShortcuts.set('Ctrl+B', () => this.nextButton());
    this.navigationShortcuts.set('Ctrl+L', () => this.nextLink());
    this.navigationShortcuts.set('Ctrl+T', () => this.nextHeading());
    
    // Comenzi utilitare
    this.navigationShortcuts.set('Ctrl+M', () => this.readCurrentPage());
    this.navigationShortcuts.set('Ctrl+N', () => this.readNavigationInfo());
    this.navigationShortcuts.set('Ctrl+Shift+S', () => this.adjustSpeed());
    this.navigationShortcuts.set('Ctrl+Shift+L', () => this.listAllElements());
    
    // Comenzi pentru modul TASTARE (când suntem în input)
    this.typingShortcuts = new Map();
    
    // Ctrl+Enter pentru a ieși din modul editare
    this.typingShortcuts.set('Ctrl+Enter', () => this.exitTypingMode());
    
    // Ctrl+L pentru a citi linia curentă
    this.typingShortcuts.set('Ctrl+L', () => this.readCurrentLine());
    
    // Ctrl+W pentru a citi ultimul cuvânt
    this.typingShortcuts.set('Ctrl+W', () => this.readLastWord());
    
    // Ctrl+Shift+L pentru a citi tot textul
    this.typingShortcuts.set('Ctrl+Shift+L', () => this.readAllText());
    
    // Pentru editor de cod
    this.typingShortcuts.set('Ctrl+Shift+E', () => this.executeCode());
    this.typingShortcuts.set('Ctrl+Shift+F', () => this.getFeedback());
  }

  handleNavigationMode(e) {
    const key = this.getShortcutKey(e);
    const handler = this.navigationShortcuts.get(key);
    
    if (handler) {
      e.preventDefault();
      handler();
    }
  }

    handleTypingMode(e) {
    const key = this.getShortcutKey(e);
    const handler = this.typingShortcuts.get(key);
    
    if (handler) {
      e.preventDefault();
      handler();
      return;
    }

    // Pentru navigare în editor cu săgeți
    if (e.target.classList.contains('monaco-editor') || 
        e.target.closest('.monaco-editor')) {
      this.handleCodeNavigation(e);
    }
  }

  handleTextInput(e) {
    const target = e.target;
    const value = target.value;
    const cursorPosition = target.selectionStart;
    
    // Determinăm ce a fost tastat
    const currentChar = value.charAt(cursorPosition - 1);
    
    // Adăugăm la buffer pentru citire
    this.typingBuffer += currentChar;
    this.lastTypedTime = Date.now();
    
    // Citim caracterul imediat
    if (currentChar && currentChar !== ' ') {
      this.speak(this.getCharacterName(currentChar), 'normal');
    } else if (currentChar === ' ') {
      // La spațiu, citim ultimul cuvânt
      this.readLastWordFromBuffer();
    }
    
    // Resetăm buffer-ul după o pauză
    setTimeout(() => {
      if (Date.now() - this.lastTypedTime > 1000) {
        this.typingBuffer = '';
      }
    }, 1000);
  }

  handleCodeNavigation(e) {
    // Pentru editor Monaco, tratăm special săgețile sus/jos
    if (e.key === 'ArrowUp' || e.key === 'ArrowDown') {
      // Așteaptă puțin pentru ca editorul să își actualizeze poziția
      setTimeout(() => {
        this.readCurrentCodeLine();
      }, 50);
    }
  }

  getCharacterName(char) {
    // Caractere speciale
    const specialChars = {
      ' ': 'spațiu',
      '.': 'punct',
      ',': 'virgulă',
      ';': 'punct și virgulă',
      ':': 'două puncte',
      '!': 'semnul exclamării',
      '?': 'semnul întrebării',
      '"': 'ghilimele',
      "'": 'apostrof',
      '(': 'paranteză deschisă',
      ')': 'paranteză închisă',
      '[': 'paranteză pătrată deschisă',
      ']': 'paranteză pătrată închisă',
      '{': 'acoladă deschisă',
      '}': 'acoladă închisă',
      '+': 'plus',
      '-': 'minus',
      '*': 'asterisc',
      '/': 'slash',
      '\\': 'backslash',
      '=': 'egal',
      '<': 'mai mic',
      '>': 'mai mare',
      '@': 'arond',
      '#': 'diez',
      '$': 'dolar',
      '%': 'procent',
      '^': 'circumflex',
      '&': 'ampersand',
      '_': 'underscore',
      '|': 'bară verticală',
      '~': 'tildă',
      '`': 'accent grav',
      '\n': 'linie nouă',
      '\t': 'tab'
    };
    
    // Pentru cifre
    if (/\d/.test(char)) {
      return `cifra ${char}`;
    }
    
    // Pentru litere mari
    if (/[A-Z]/.test(char)) {
      return `${char} mare`;
    }
    
    return specialChars[char] || char;
  }

  readLastWordFromBuffer() {
    const words = this.typingBuffer.trim().split(/\s+/);
    const lastWord = words[words.length - 1];
    
    if (lastWord && lastWord.length > 0) {
      this.speak(lastWord, 'normal');
    }
  }

  readCurrentLine() {
    const activeElement = document.activeElement;
    
    if (activeElement.tagName === 'INPUT' || activeElement.tagName === 'TEXTAREA') {
      const value = activeElement.value;
      const cursorPosition = activeElement.selectionStart;
      
      // Găsim începutul și sfârșitul liniei curente
      let lineStart = value.lastIndexOf('\n', cursorPosition - 1) + 1;
      let lineEnd = value.indexOf('\n', cursorPosition);
      if (lineEnd === -1) lineEnd = value.length;
      
      const currentLine = value.substring(lineStart, lineEnd);
      
      if (currentLine.trim()) {
        this.speak(`Linia curentă: ${currentLine}`, 'high');
      } else {
        this.speak('Linie goală', 'high');
      }
    }
  }

  readCurrentCodeLine() {
    // Pentru Monaco Editor
    const monacoEditor = document.querySelector('.monaco-editor');
    if (monacoEditor) {
      // Găsim linia activă în Monaco
      const activeLine = monacoEditor.querySelector('.active-line');
      if (activeLine) {
        const lineContent = activeLine.textContent.trim();
        const lineNumber = this.getMonacoLineNumber();
        
        if (lineContent) {
          this.speak(`Linia ${lineNumber}: ${lineContent}`, 'high');
        } else {
          this.speak(`Linia ${lineNumber}: goală`, 'high');
        }
      }
    }
  }

  getMonacoLineNumber() {
    // Găsim numărul liniei din Monaco
    const lineNumbers = document.querySelector('.margin-view-overlays');
    if (lineNumbers) {
      const activeLineNumber = lineNumbers.querySelector('.active-line-number');
      if (activeLineNumber) {
        return activeLineNumber.textContent.trim();
      }
    }
    return '1';
  }

  readLastWord() {
    const activeElement = document.activeElement;
    
    if (activeElement.tagName === 'INPUT' || activeElement.tagName === 'TEXTAREA') {
      const value = activeElement.value;
      const cursorPosition = activeElement.selectionStart;
      
      // Găsim ultimul cuvânt înainte de cursor
      const textBeforeCursor = value.substring(0, cursorPosition);
      const words = textBeforeCursor.trim().split(/\s+/);
      const lastWord = words[words.length - 1];
      
      if (lastWord) {
        this.speak(`Ultimul cuvânt: ${lastWord}`, 'high');
      } else {
        this.speak('Niciun cuvânt', 'high');
      }
    }
  }

  readAllText() {
    const activeElement = document.activeElement;
    
    if (activeElement.tagName === 'INPUT' || activeElement.tagName === 'TEXTAREA') {
      const value = activeElement.value;
      
      if (value.trim()) {
        this.speak(`Textul complet: ${value}`, 'high');
      } else {
        this.speak('Câmpul este gol', 'high');
      }
    }
  }

  exitTypingMode() {
    const activeElement = document.activeElement;
    
    if (activeElement) {
      activeElement.blur();
      this.speak('Mod editare dezactivat. Navigare reactivată', 'high');
      
      // Re-focusăm elementul pentru navigare
      setTimeout(() => {
        this.setFocus(activeElement);
      }, 100);
    }
  }

  toggleAccessibilityMode() {
    this.isAccessibilityMode = !this.isAccessibilityMode;
    
    if (this.isAccessibilityMode) {
      this.speak("Modul pentru nevăzători activat. Bine ai venit în JavaVerse!", 'high');
      this.addAccessibilityStyles();
      this.updateFocusableElements();
      
      setTimeout(() => {
        this.readWelcomeMessage();
      }, 2000);
    } else {
      this.speak("Modul pentru nevăzători dezactivat.");
      this.removeAccessibilityStyles();
      this.stopReading();
    }
    
    document.dispatchEvent(new CustomEvent('accessibilityModeChanged', {
      detail: { isActive: this.isAccessibilityMode }
    }));
  }

  readWelcomeMessage() {
    const welcomeText = `
      Bine ai venit în modul pentru nevăzători JavaVerse!
      
      Comenzi principale:
      - Tab și Shift Tab pentru navigare între elemente
      - Enter pentru a activa elementul selectat
      - Escape pentru a opri citirea
      
      Navigare rapidă:
      - F1 pentru ajutor
      - F2, F3, F4 pentru header, conținut principal, footer
      - Control Shift C pentru cursuri
      - Control Shift P pentru playground
      - Control Shift R pentru profil
      - Control Shift H pentru pagina principală
      
      Navigare prin elemente:
      - Control B pentru următorul buton
      - Control L pentru următorul link
      - Control T pentru următorul titlu
      
      În câmpuri de text:
      - Caracterele sunt citite în timp ce tastezi
      - Spațiu citește ultimul cuvânt
      - Control L pentru a citi linia curentă
      - Control W pentru ultimul cuvânt
      - Control Shift L pentru tot textul
      - Control Enter pentru a ieși din editare
      
      În editor de cod:
      - Săgețile sus și jos citesc automat linia curentă
      - Control Shift E pentru execuție
      - Control Shift F pentru feedback AI
      
      Apasă F1 oricând pentru ajutor contextual.
    `;
    
    this.speak(welcomeText, 'high');
  }

  updateFocusableElements() {
    const selectors = [
      'a[href]:not([disabled])',
      'button:not([disabled])',
      'input:not([disabled]):not([type="hidden"])',
      'select:not([disabled])',
      'textarea:not([disabled])',
      '[tabindex]:not([tabindex="-1"])',
      '.course-card',
      '.question-container li',
      '.feature-card',
      '.example-card',
      'h1', 'h2', 'h3', 'h4', 'h5', 'h6',
      '[role="button"]:not([disabled])',
      '[role="link"]',
      '[role="navigation"]',
      '[role="main"]'
    ];
    
    this.focusableElements = Array.from(document.querySelectorAll(selectors.join(', ')))
      .filter(el => {
        // Verificăm dacă elementul este vizibil
        const rect = el.getBoundingClientRect();
        return rect.width > 0 && rect.height > 0 && el.offsetParent !== null;
      })
      .sort((a, b) => {
        // Sortăm elementele după poziția lor în pagină
        const rectA = a.getBoundingClientRect();
        const rectB = b.getBoundingClientRect();
        
        if (Math.abs(rectA.top - rectB.top) < 10) {
          return rectA.left - rectB.left;
        }
        return rectA.top - rectB.top;
      });
  }

  navigateNext() {
    this.updateFocusableElements();
    
    if (this.focusableElements.length === 0) {
      this.speak("Nu sunt elemente disponibile pentru navigare", 'high');
      return;
    }
    
    this.currentFocusIndex = (this.currentFocusIndex + 1) % this.focusableElements.length;
    this.setFocus(this.focusableElements[this.currentFocusIndex]);
  }

  navigatePrevious() {
    this.updateFocusableElements();
    
    if (this.focusableElements.length === 0) {
      this.speak("Nu sunt elemente disponibile pentru navigare", 'high');
      return;
    }
    
    this.currentFocusIndex = this.currentFocusIndex === 0 
      ? this.focusableElements.length - 1 
      : this.currentFocusIndex - 1;
      
    this.setFocus(this.focusableElements[this.currentFocusIndex]);
  }

  setFocus(element) {
    if (!element) return;
    
    // Eliminăm focus-ul anterior
    document.querySelectorAll('.accessibility-focus').forEach(el => {
      el.classList.remove('accessibility-focus');
    });
    
    // Setăm noul focus
    this.focusedElement = element;
    element.classList.add('accessibility-focus');
    
    // Scroll smooth în vedere
    element.scrollIntoView({ 
      behavior: 'smooth', 
      block: 'center',
      inline: 'nearest'
    });
    
    // Focus real pentru inputuri
    if (['INPUT', 'TEXTAREA', 'SELECT'].includes(element.tagName)) {
      element.focus();
    }
    
    // Citim informații despre element
    this.readElementInfo(element);
  }

  readElementInfo(element) {
    let description = '';
    const tagName = element.tagName.toLowerCase();
    const ariaLabel = element.getAttribute('aria-label');
    const text = element.textContent?.trim();
    
    // Prioritizăm aria-label
    if (ariaLabel) {
      description = ariaLabel;
    } else {
      // Descrieri specifice pentru fiecare tip de element
      switch (tagName) {
        case 'button':
          description = `Buton: ${text || 'Fără text'}`;
          break;
          
        case 'a':
          description = `Link: ${text || 'Fără text'}`;
          break;
          
        case 'input':
          const inputType = element.type;
          const inputLabel = document.querySelector(`label[for="${element.id}"]`)?.textContent;
          const inputValue = element.value;
          
          if (inputType === 'text' || inputType === 'email' || inputType === 'password') {
            description = `Câmp ${inputType}: ${inputLabel || element.placeholder || 'Fără etichetă'}`;
            if (inputValue) {
              description += `. Valoare curentă: ${inputType === 'password' ? 'parolă introdusă' : inputValue}`;
            }
          } else if (inputType === 'radio' || inputType === 'checkbox') {
            description = `${inputType === 'radio' ? 'Opțiune' : 'Bifă'}: ${inputLabel || text}. ${element.checked ? 'Selectat' : 'Neselectat'}`;
          }
          break;
          
        case 'select':
          const selectLabel = document.querySelector(`label[for="${element.id}"]`)?.textContent;
          const selectedOption = element.options[element.selectedIndex]?.text;
          description = `Listă derulantă: ${selectLabel || 'Fără etichetă'}. Selectat: ${selectedOption || 'Nimic'}`;
          break;
          
        case 'textarea':
          const textareaLabel = document.querySelector(`label[for="${element.id}"]`)?.textContent;
          description = `Zonă de text: ${textareaLabel || element.placeholder || 'Fără etichetă'}`;
          if (element.value) {
            description += `. ${element.value.length} caractere introduse`;
          }
          break;
          
        case 'h1':
        case 'h2':
        case 'h3':
        case 'h4':
        case 'h5':
        case 'h6':
          const level = tagName.charAt(1);
          description = `Titlu nivel ${level}: ${text}`;
          break;
          
        default:
          // Pentru elemente custom (carduri, etc.)
          if (element.classList.contains('course-card')) {
            const title = element.querySelector('.course-title')?.textContent;
            const desc = element.querySelector('.course-description')?.textContent;
            description = `Curs: ${title}. ${desc}`;
          } else if (element.classList.contains('feature-card')) {
            const title = element.querySelector('h3')?.textContent;
            const desc = element.querySelector('p')?.textContent;
            description = `Caracteristică: ${title}. ${desc}`;
          } else if (element.classList.contains('example-card')) {
            const title = element.querySelector('.card-title')?.textContent || text;
            description = `Exemplu de cod: ${title}`;
          } else {
            description = text || `Element ${tagName}`;
          }
      }
    }
    
    // Adăugăm indicații pentru interacțiune
    if (['button', 'a'].includes(tagName) || element.getAttribute('role') === 'button') {
      description += ". Apasă Enter pentru a activa";
    } else if (['input', 'textarea', 'select'].includes(tagName)) {
      description += ". Apasă Enter pentru a edita";
    }
    
    this.speak(description, 'high');
  }

  activateElement() {
    if (!this.focusedElement) {
      this.speak("Niciun element selectat", 'high');
      return;
    }
    
    const tagName = this.focusedElement.tagName.toLowerCase();
    
    // Pentru inputuri, permitem editarea
    if (['input', 'textarea', 'select'].includes(tagName)) {
      this.focusedElement.focus();
      this.speak("Mod editare activat. Apasă Escape când termini", 'high');
      
      // Dezactivăm temporar navigarea pentru a permite tastarea
      this.temporarilyDisableNavigation();
      return;
    }
    
    // Pentru alte elemente, simulăm click
    this.speak("Activez elementul", 'normal');
    
    if (this.focusedElement.click) {
      this.focusedElement.click();
    } else if (tagName === 'a') {
      window.location.href = this.focusedElement.href;
    }
  }

  temporarilyDisableNavigation() {
    const handleEscape = (e) => {
      if (e.key === 'Escape') {
        e.preventDefault();
        document.removeEventListener('keydown', handleEscape, true);
        
        // Re-focusăm elementul și anunțăm
        if (this.focusedElement) {
          this.focusedElement.blur();
          this.speak("Mod editare dezactivat. Navigare reactivată", 'high');
          this.setFocus(this.focusedElement);
        }
      }
    };
    
    // Captură pentru Escape cu prioritate mare
    document.addEventListener('keydown', handleEscape, true);
  }

  // Navigare rapidă la secțiuni
  navigateToSection(sectionName) {
    let selector;
    let announcement;
    
    switch (sectionName) {
      case 'header':
        selector = 'header, [role="banner"], .jv-header';
        announcement = "Navighez la header";
        break;
      case 'main':
        selector = 'main, [role="main"], .body-content';
        announcement = "Navighez la conținutul principal";
        break;
      case 'footer':
        selector = 'footer, [role="contentinfo"], .jv-footer';
        announcement = "Navighez la footer";
        break;
    }
    
    const section = document.querySelector(selector);
    if (section) {
      this.speak(announcement, 'high');
      
      // Găsim primul element focusabil din secțiune
      const firstFocusable = section.querySelector(
        'a, button, input, select, textarea, [tabindex]:not([tabindex="-1"])'
      );
      
      if (firstFocusable) {
        const index = this.focusableElements.indexOf(firstFocusable);
        if (index !== -1) {
          this.currentFocusIndex = index;
          this.setFocus(firstFocusable);
        }
      } else {
        section.scrollIntoView({ behavior: 'smooth', block: 'start' });
        this.speak("Secțiune fără elemente interactive", 'normal');
      }
    } else {
      this.speak("Secțiunea nu a fost găsită", 'high');
    }
  }

  // Navigare prin tipuri de elemente
  nextButton() {
    this.navigateToNextOfType('button, [role="button"]', 'buton');
  }

  nextLink() {
    this.navigateToNextOfType('a[href]', 'link');
  }

  nextInput() {
    this.navigateToNextOfType('input, textarea, select', 'câmp de input');
  }

  nextHeading() {
    this.navigateToNextOfType('h1, h2, h3, h4, h5, h6', 'titlu');
  }

  navigateToNextOfType(selector, typeName) {
    const elements = Array.from(document.querySelectorAll(selector))
      .filter(el => el.offsetParent !== null);
    
    if (elements.length === 0) {
      this.speak(`Nu există ${typeName} pe această pagină`, 'high');
      return;
    }
    
    let nextIndex = -1;
    
    if (this.focusedElement) {
      const currentTypeIndex = elements.indexOf(this.focusedElement);
      if (currentTypeIndex !== -1) {
        nextIndex = (currentTypeIndex + 1) % elements.length;
      } else {
        // Găsim următorul element de acest tip după elementul curent
        const currentRect = this.focusedElement.getBoundingClientRect();
        for (let i = 0; i < elements.length; i++) {
          const rect = elements[i].getBoundingClientRect();
          if (rect.top > currentRect.top || 
              (Math.abs(rect.top - currentRect.top) < 10 && rect.left > currentRect.left)) {
            nextIndex = i;
            break;
          }
        }
      }
    }
    
    if (nextIndex === -1) {
      nextIndex = 0;
    }
    
    const targetElement = elements[nextIndex];
    const globalIndex = this.focusableElements.indexOf(targetElement);
    
    if (globalIndex !== -1) {
      this.currentFocusIndex = globalIndex;
      this.setFocus(targetElement);
    }
  }

  // Ajustare viteză citire
  adjustSpeed() {
    const speeds = [0.7, 0.85, 1.0, 1.15, 1.3];
    const currentIndex = speeds.indexOf(this.readingSpeed);
    const nextIndex = (currentIndex + 1) % speeds.length;
    
    this.readingSpeed = speeds[nextIndex];
    this.speak(`Viteză de citire: ${Math.round(this.readingSpeed * 100)}%`, 'high');
  }

  // Îmbunătățire pentru speak cu viteză ajustabilă
  speak(text, priority = 'normal') {
    if (!text || text.trim() === '') return;

    if (priority === 'high') {
      this.stopReading();
    }

    const utterance = new SpeechSynthesisUtterance(text);
    if (this.voice) {
      utterance.voice = this.voice;
    }
    
    utterance.rate = this.readingSpeed;
    utterance.pitch = 1.0;
    utterance.volume = 1.0;

    utterance.onstart = () => {
      this.isReading = true;
      this.currentUtterance = utterance;
      
      // Trimitem event pentru UI
      document.dispatchEvent(new CustomEvent('speechStatusChanged', {
        detail: { isSpeaking: true }
      }));
    };

    utterance.onend = () => {
      this.isReading = false;
      this.currentUtterance = null;
      this.processQueue();
      
      document.dispatchEvent(new CustomEvent('speechStatusChanged', {
        detail: { isSpeaking: false }
      }));
    };

    utterance.onerror = (e) => {
      console.error('Speech synthesis error:', e);
      this.isReading = false;
      this.currentUtterance = null;
    };

    if (this.isReading && priority !== 'high') {
      this.readingQueue.push(utterance);
    } else {
      this.speechSynthesis.speak(utterance);
    }
  }

  // Navigare înapoi
  navigateBack() {
    if (this.navigationHistory.length > 0) {
      const previousUrl = this.navigationHistory.pop();
      this.speak("Navighez înapoi", 'high');
      window.location.href = previousUrl;
    } else {
      this.speak("Nu există istoric de navigare", 'high');
    }
  }

  // Listare elemente
  listAllElements() {
    this.updateFocusableElements();
    
    const totalElements = this.focusableElements.length;
    const currentPosition = this.currentFocusIndex + 1;
    
    let summary = `Pagina conține ${totalElements} elemente interactive. `;
    summary += `Ești la elementul ${currentPosition} din ${totalElements}. `;
    
    // Numărăm tipurile de elemente
    const counts = {
      buttons: document.querySelectorAll('button, [role="button"]').length,
      links: document.querySelectorAll('a[href]').length,
      inputs: document.querySelectorAll('input, textarea, select').length,
      headings: document.querySelectorAll('h1, h2, h3, h4, h5, h6').length
    };
    
    summary += `Găsit: ${counts.buttons} butoane, ${counts.links} linkuri, `;
    summary += `${counts.inputs} câmpuri de input, ${counts.headings} titluri.`;
    
    this.speak(summary, 'high');
  }

  // Navigare rapidă pentru pagini
  navigateToCourses() {
    this.navigationHistory.push(window.location.href);
    this.speak("Navighez la cursuri", 'high');
    window.location.href = '/courses';
  }

  navigateToPlayground() {
    this.navigationHistory.push(window.location.href);
    this.speak("Navighez la playground", 'high');
    window.location.href = '/playground';
  }

  navigateToProfile() {
    this.navigationHistory.push(window.location.href);
    this.speak("Navighez la profil", 'high');
    window.location.href = '/profile';
  }

  goHome() {
    this.navigationHistory.push(window.location.href);
    this.speak("Navighez la pagina principală", 'high');
    window.location.href = '/';
  }

  // Funcții pentru quiz
  selectQuizOption(index) {
    const options = document.querySelectorAll('.question-container li');
    if (options[index]) {
      options[index].click();
      this.speak(`Selectat răspunsul ${index + 1}`, 'high');
    } else {
      this.speak(`Nu există răspunsul ${index + 1}`, 'high');
    }
  }

  // Funcții pentru editor
  executeCode() {
    const runButton = document.querySelector('.editor-actions button[aria-label*="Execută"]');
    if (runButton) {
      runButton.click();
      this.speak("Execut codul", 'high');
    } else {
      this.speak("Butonul de execuție nu a fost găsit", 'high');
    }
  }

  getFeedback() {
    const feedbackButton = document.querySelector('.editor-actions button[aria-label*="feedback"]');
    if (feedbackButton) {
      feedbackButton.click();
      this.speak("Solicit feedback AI", 'high');
    } else {
      this.speak("Butonul de feedback nu a fost găsit", 'high');
    }
  }

  // Citirea informațiilor despre pagina curentă
  readNavigationInfo() {
    const currentUrl = window.location.pathname;
    const totalElements = this.focusableElements.length;
    const position = this.currentFocusIndex + 1;
    
    let info = `Ești pe ${this.getPageName(currentUrl)}. `;
    info += `Elementul curent: ${position} din ${totalElements}. `;
    info += `Folosește Tab pentru navigare sau apasă H pentru ajutor.`;
    
    this.speak(info, 'high');
  }

  getPageName(url) {
    if (url === '/' || url === '') return 'pagina principală';
    if (url.includes('/courses')) return 'pagina de cursuri';
    if (url.includes('/playground')) return 'playground-ul de cod';
    if (url.includes('/profile')) return 'pagina de profil';
    if (url.includes('/login')) return 'pagina de autentificare';
    if (url.includes('/register')) return 'pagina de înregistrare';
    if (url.includes('/quiz')) return 'pagina de test';
    return 'pagina curentă';
  }

  // Metodă îmbunătățită pentru citirea help-ului
  readHelp() {
    const currentPage = this.getPageName(window.location.pathname);
    
    const helpText = `
      Ajutor pentru ${currentPage}.
      
      Comenzi esențiale:
      - Tab și Shift Tab pentru navigare înainte și înapoi
      - Enter pentru a activa elementul selectat
      - Spațiu pentru pauză sau continuare citire
      - Escape pentru oprire citire
      
      Navigare rapidă:
      - Tastele 1, 2, 3 pentru header, conținut principal, footer
      - C pentru cursuri, P pentru playground, R pentru profil, H pentru acasă
      
      Navigare prin tipuri:
      - B pentru butoane, L pentru linkuri
      - I pentru câmpuri input, T pentru titluri
      
      Comenzi speciale cu Alt:
      - Alt H pentru acest ajutor
      - Alt M pentru citirea paginii
      - Alt N pentru informații navigare
      - Alt S pentru viteză citire
      - Alt B pentru înapoi
      - Alt L pentru lista elementelor
      
      ${this.getPageSpecificHelp(currentPage)}
    `;
    
    this.speak(helpText, 'high');
  }

  getPageSpecificHelp(pageName) {
    switch (pageName) {
      case 'pagina de cursuri':
        return 'În această pagină: Navighează prin cursuri cu Tab. Apasă Enter pentru a selecta un curs.';
        
      case 'playground-ul de cod':
        return 'În editor: Alt E pentru execuție, Alt F pentru feedback AI. Tab citește linia curentă.';
        
      case 'pagina de test':
        return 'În test: Alt 1-4 pentru răspunsuri. Enter pentru verificare sau următoarea întrebare.';
        
      case 'pagina de profil':
        return 'În profil: Navighează prin secțiuni cu Tab. Enter pentru a modifica setările.';
        
      default:
        return '';
    }
  }

  handleAccessibilityShortcuts(e) {
    const key = this.getShortcutKey(e);
    const handler = this.shortcuts.get(key);
    
    if (handler) {
      e.preventDefault();
      handler();
    }
  }

  getShortcutKey(e) {
    const parts = [];
    
    if (e.ctrlKey) parts.push('ctrl');
    if (e.altKey) parts.push('alt');
    if (e.shiftKey) parts.push('Shift');
    
    // Tratăm special anumite taste
    let key = e.key;
    if (key === ' ') key = ' ';
    if (key.length === 1) key = key.toLowerCase();
    
    parts.push(key);
    
    return parts.join('+');
  }

  addAccessibilityStyles() {
    const style = document.createElement('style');
    style.id = 'accessibility-styles';
    style.textContent = `
