import React, { useState, useEffect } from 'react';
import styles from '../styles/Profile.module.css'; // Vom folosi stilurile √ÆmbunƒÉtƒÉ»õite

function Profile() {
  const [userProgress, setUserProgress] = useState([]);
  const [userInfo, setUserInfo] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('courses'); // Pentru navigare √Æntre sec»õiuni

  // Ob»õine userId din token-ul JWT stocat √Æn localStorage
  // √én implementarea finalƒÉ, vom decodifica token-ul JWT pentru a ob»õine userId
  // Pentru moment, utilizƒÉm o valoare hardcodatƒÉ pentru demo
  const userId = localStorage.getItem('userId') || 'demo_user_id';

  useEffect(() => {
    // SimulƒÉm datele utilizatorului pentru a demonstra designul
    // √én implementarea finalƒÉ, aceste date vor veni de la API
    const mockUserInfo = {
      username: 'JavaLearner',
      email: 'java.learner@example.com',
      joinDate: '10 Feb 2023',
      totalCompletedLessons: 15,
      totalQuizScore: 85
    };

    const mockProgress = [
      {
        _id: '1',
        completedLessons: 3, 
        totalLessons: 5,
        testScore: 90,
        courseId: {
          _id: '101',
          name: 'Introducere √Æn Java',
          description: '√énva»õƒÉ bazele limbajului Java'
        }
      },
      {
        _id: '2',
        completedLessons: 2,
        totalLessons: 5,
        testScore: 80,
        courseId: {
          _id: '102',
          name: 'Programare OrientatƒÉ pe Obiecte √Æn Java',
          description: 'Concepte fundamentale OOP √Æn Java'
        }
      },
      {
        _id: '3',
        completedLessons: 1,
        totalLessons: 5,
        testScore: null,
        courseId: {
          _id: '103',
          name: 'Java pentru Aplica»õii Web cu Spring',
          description: 'DezvoltƒÉ aplica»õii web folosind Spring'
        }
      }
    ];

    // SimulƒÉm un apel API
    setTimeout(() => {
      setUserInfo(mockUserInfo);
      setUserProgress(mockProgress);
      setIsLoading(false);
    }, 800);

    // √én implementarea realƒÉ, vei folosi fetch sau axios pentru a ob»õine datele
    /*
    if (userId) {
      fetch(`/api/user/progress/${userId}`)
        .then(response => response.json())
        .then(data => {
          console.log("Progresul utilizatorului:", data);
          setUserProgress(data);
          if (data[0]?.userId) {
            setUserInfo(data[0].userId);
          }
          setIsLoading(false);
        })
        .catch(error => {
          console.error('Error fetching user progress:', error);
          setIsLoading(false);
        });
    }
    */
  }, [userId]);

  // Func»õie pentru a calcula progresul √Æn procente
  const calculateProgress = (completedLessons, totalLessons) => {
    return (completedLessons / totalLessons) * 100;
  };

  // Func»õie pentru a determina clasa CSS pentru progres (culoare bazatƒÉ pe progres)
  const getProgressColorClass = (progress) => {
    if (progress < 30) return styles.progressLow;
    if (progress < 70) return styles.progressMedium;
    return styles.progressHigh;
  };

  // Func»õie pentru a ob»õine clasa CSS pentru scorul testului
  const getScoreClass = (score) => {
    if (score === null) return styles.scoreNone;
    if (score < 50) return styles.scoreLow;
    if (score < 80) return styles.scoreMedium;
    return styles.scoreHigh;
  };

  if (isLoading) {
    return (
      <div className={styles.loadingContainer}>
        <div className={styles.loadingSpinner}></div>
        <p>Se √ÆncarcƒÉ profilul...</p>
      </div>
    );
  }

  return (
    <div className={styles.profileContainer}>
      {/* Sec»õiunea header cu informa»õii utilizator »ôi statistici */}
      <div className={styles.profileHeader}>
        <div className={styles.userInfo}>
          <div className={styles.avatarContainer}>
            <img src="https://via.placeholder.com/150" alt="User Avatar" className={styles.userAvatar} />
            <div className={styles.avatarOverlay}>
              <button className={styles.changeAvatarBtn}>
                <i className="fas fa-camera"></i>
              </button>
            </div>
          </div>
          
          <div className={styles.userDetails}>
            <h2 className={styles.username}>{userInfo?.username || 'Utilizator JavaVerse'}</h2>
            <p className={styles.email}>{userInfo?.email || 'email@example.com'}</p>
            <p className={styles.joinDate}>Membru din: {userInfo?.joinDate || 'Ianuarie 2023'}</p>
          </div>
        </div>

        {/* Statistici utilizator */}
        <div className={styles.statsContainer}>
          <div className={styles.statCard}>
            <div className={styles.statIcon}>
              <i className="fas fa-book"></i>
            </div>
            <div className={styles.statInfo}>
              <span className={styles.statValue}>{userProgress.length}</span>
              <span className={styles.statLabel}>Cursuri Active</span>
            </div>
          </div>
          
          <div className={styles.statCard}>
            <div className={styles.statIcon}>
              <i className="fas fa-tasks"></i>
            </div>
            <div className={styles.statInfo}>
              <span className={styles.statValue}>{userInfo?.totalCompletedLessons || '0'}</span>
              <span className={styles.statLabel}>Lec»õii Finalizate</span>
            </div>
          </div>
          
          <div className={styles.statCard}>
            <div className={styles.statIcon}>
              <i className="fas fa-trophy"></i>
            </div>
            <div className={styles.statInfo}>
              <span className={styles.statValue}>{userInfo?.totalQuizScore || '0'}%</span>
              <span className={styles.statLabel}>Scor Mediu</span>
            </div>
          </div>
        </div>
      </div>

      {/* Tab-uri pentru navigare √Æntre sec»õiuni */}
      <div className={styles.tabsContainer}>
        <button 
          className={`${styles.tabButton} ${activeTab === 'courses' ? styles.activeTab : ''}`}
          onClick={() => setActiveTab('courses')}
        >
          Cursurile Mele
        </button>
        <button 
          className={`${styles.tabButton} ${activeTab === 'achievements' ? styles.activeTab : ''}`}
          onClick={() => setActiveTab('achievements')}
        >
          RealizƒÉri
        </button>
        <button 
          className={`${styles.tabButton} ${activeTab === 'settings' ? styles.activeTab : ''}`}
          onClick={() => setActiveTab('settings')}
        >
          SetƒÉri
        </button>
      </div>

      {/* Con»õinut √Æn func»õie de tab-ul activ */}
      <div className={styles.tabContent}>
        {activeTab === 'courses' && (
          <div className={styles.courseGrid}>
            {userProgress.length > 0 ? (
              userProgress.map(progress => {
                const progressPercent = calculateProgress(progress.completedLessons, progress.totalLessons);
                return (
                  <div key={progress._id} className={styles.courseCard}>
                    <div className={styles.courseHeader}>
                      <h3 className={styles.courseName}>{progress.courseId.name}</h3>
                      <div className={styles.courseActions}>
                        <button className={styles.continueButton}>
                          ContinuƒÉ <i className="fas fa-arrow-right"></i>
                        </button>
                      </div>
                    </div>
                    
                    <p className={styles.courseDescription}>{progress.courseId.description}</p>
                    
                    <div className={styles.progressSection}>
                      <div className={styles.progressInfo}>
                        <span>Progres: {Math.round(progressPercent)}%</span>
                        <span>{progress.completedLessons} din {progress.totalLessons} lec»õii</span>
                      </div>
                      
                      <div className={styles.progressBarContainer}>
                        <div
                          className={`${styles.progressBar} ${getProgressColorClass(progressPercent)}`}
                          style={{ width: `${progressPercent}%` }}
                        ></div>
                      </div>
                    </div>
                    
                    <div className={styles.testScoreSection}>
                      {progress.testScore !== null ? (
                        <div className={`${styles.testScore} ${getScoreClass(progress.testScore)}`}>
                          <i className="fas fa-award"></i> Test: {progress.testScore}%
                        </div>
                      ) : (
                        <div className={styles.testNotTaken}>
                          <i className="fas fa-hourglass-half"></i> Test nesus»õinut
                        </div>
                      )}
                    </div>
                  </div>
                );
              })
            ) : (
              <div className={styles.noCourses}>
                <div className={styles.noCoursesIcon}>
                  <i className="fas fa-book-open"></i>
                </div>
                <h3>Nu e»ôti √Ænscris la niciun curs</h3>
                <p>√énscrie-te la cursuri pentru a √Æncepe cƒÉlƒÉtoria ta Java!</p>
                <button className={styles.exploreCourses}>ExploreazƒÉ Cursuri</button>
              </div>
            )}
          </div>
        )}

        {activeTab === 'achievements' && (
          <div className={styles.achievementsContainer}>
            <div className={styles.achievementCard}>
              <div className={styles.achievementIcon}>üèÜ</div>
              <h3>Primul Curs √énceput</h3>
              <p>Ai √Ænceput cƒÉlƒÉtoria de √ÆnvƒÉ»õare Java!</p>
            </div>
            
            <div className={styles.achievementCard}>
              <div className={styles.achievementIcon}>ü•â</div>
              <h3>Prima Lec»õie CompletatƒÉ</h3>
              <p>Ai finalizat prima ta lec»õie</p>
            </div>
            
            <div className={`${styles.achievementCard} ${styles.lockedAchievement}`}>
              <div className={styles.achievementIcon}>üîí</div>
              <h3>Primul Test Perfect</h3>
              <p>CompleteazƒÉ un test cu scor 100%</p>
            </div>
            
            <div className={`${styles.achievementCard} ${styles.lockedAchievement}`}>
              <div className={styles.achievementIcon}>üîí</div>
              <h3>Primul Proiect Complet</h3>
              <p>FinalizeazƒÉ un proiect Java</p>
            </div>
          </div>
        )}

        {activeTab === 'settings' && (
          <div className={styles.settingsContainer}>
            <div className={styles.settingSection}>
              <h3 className={styles.settingHeader}>Informa»õii Cont</h3>
              
              <div className={styles.formGroup}>
                <label htmlFor="username">Nume utilizator</label>
                <input 
                  type="text" 
                  id="username" 
                  value={userInfo?.username || ''} 
                  className={styles.formInput} 
                />
              </div>
              
              <div className={styles.formGroup}>
                <label htmlFor="email">Email</label>
                <input 
                  type="email" 
                  id="email" 
                  value={userInfo?.email || ''} 
                  className={styles.formInput} 
                />
              </div>
              
              <button className={styles.saveButton}>SalveazƒÉ ModificƒÉrile</button>
            </div>
            
            <div className={styles.settingSection}>
              <h3 className={styles.settingHeader}>SchimbƒÉ Parola</h3>
              
              <div className={styles.formGroup}>
                <label htmlFor="currentPassword">Parola actualƒÉ</label>
                <input 
                  type="password" 
                  id="currentPassword" 
                  className={styles.formInput} 
                />
              </div>
              
              <div className={styles.formGroup}>
                <label htmlFor="newPassword">Parola nouƒÉ</label>
                <input 
                  type="password" 
                  id="newPassword" 
                  className={styles.formInput} 
                />
              </div>
              
              <div className={styles.formGroup}>
                <label htmlFor="confirmPassword">ConfirmƒÉ parola</label>
                <input 
                  type="password" 
                  id="confirmPassword" 
                  className={styles.formInput} 
                />
              </div>
              
              <button className={styles.saveButton}>SchimbƒÉ Parola</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Profile;
