// src/components/Login.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/FormPage.css';

function Login({ setIsAuthenticated }) {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validare simplă
    if (!formData.email || !formData.password) {
      setError('Te rugăm să completezi toate câmpurile.');
      return;
    }
    
    setIsLoading(true);
    setError('');

    try {
      console.log('Trimit cerere de autentificare...');
      
      const response = await fetch('http://localhost:5000/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      console.log('Răspuns primit, status:', response.status);
      const data = await response.json();
      console.log('Date răspuns:', data);

      if (data.success) {
        console.log('Autentificare reușită!');
        
        // Stocăm token-ul în localStorage
        localStorage.setItem('authToken', data.token);
        
        // Folosim direct userId-ul din răspuns (dacă există)
        if (data.userId) {
          localStorage.setItem('userId', data.userId);
          console.log("Am salvat userId din răspuns:", data.userId);
        } else {
          console.log("Răspunsul nu conține userId, folosesc decodare token");
          
          // Decodificăm token-ul ca metodă de rezervă
          try {
            const base64Url = data.token.split('.')[1];
            const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
            const jsonPayload = decodeURIComponent(atob(base64)
              .split('').map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2)).join(''));
            const decoded = JSON.parse(jsonPayload);
            
            console.log("Token decodat:", decoded);
            
            localStorage.setItem('userId', decoded.id);
            console.log("Am salvat userId din token:", decoded.id);
          } catch (err) {
            console.error("Eroare la decodarea token-ului:", err);
          }
        }
        
        // Setăm starea de autentificare
        setIsAuthenticated(true);
        
        // Redirecționăm utilizatorul către pagina principală
        navigate('/');
      } else {
        console.log('Autentificare eșuată:', data.message);
        setError(data.message || 'Autentificare eșuată. Verifică email-ul și parola.');
      }
    } catch (error) {
      console.error('Eroare în timpul autentificării:', error);
      setError('Eroare de rețea. Te rugăm să încerci din nou.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="form-container">
      <h1>Autentificare</h1>
      
      {error && <div className="error-message">{error}</div>}
      
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="input-group">
          <label htmlFor="password">Parolă</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        
        <button 
          type="submit" 
          className="submit-button"
          disabled={isLoading}
        >
          {isLoading ? 'Se procesează...' : 'Autentificare'}
        </button>
      </form>
      
      <div className="switch-page">
        Nu ai un cont? <Link to="/register">Înregistrează-te</Link>
      </div>
    </div>
  );
}

export default Login;
