// src/components/Header.js - Modificat cu suport pentru accesibilitate
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Button from './Button';
import { useAccessibility } from '../hooks/useAccessibility'; // AdƒÉugat hook accesibilitate
import '../styles/Header.css';

function Header({ isAuthenticated, handleLogout }) {
  const navigate = useNavigate();
  const [scrolled, setScrolled] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  // AdƒÉugat suport pentru accesibilitate
  const { 
    isAccessibilityMode, 
    useNavigationAccessibility,
    announceNavigation 
  } = useAccessibility();
  
  const { announceMenuOpen, announceMenuClose } = useNavigationAccessibility();

  // Efect pentru a detecta c√¢nd utilizatorul deruleazƒÉ pagina
  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 10;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [scrolled]);

  // Listener pentru comenzile rapide de accesibilitate √Æn header
  useEffect(() => {
    if (!isAccessibilityMode) return;

    const handleKeyPress = (e) => {
      if (!isAccessibilityMode) return;

      // ESC pentru √Ænchiderea meniului mobil
      if (e.key === 'Escape' && menuOpen) {
        setMenuOpen(false);
        if (isAccessibilityMode) {
          announceMenuClose('navigare');
        }
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [isAccessibilityMode, menuOpen, announceMenuClose]);

  const toggleMenu = () => {
    const newMenuState = !menuOpen;
    setMenuOpen(newMenuState);
    
    if (isAccessibilityMode) {
      if (newMenuState) {
        announceMenuOpen('navigare');
      } else {
        announceMenuClose('navigare');
      }
    }
  };

  // Func»õie pentru anun»õarea navigƒÉrii
  const handleNavigation = (destination, callback) => {
    if (isAccessibilityMode) {
      announceNavigation(destination);
    }
    if (callback) {
      callback();
    }
  };

  return (
    <header className={`jv-header ${scrolled ? 'jv-header-scrolled' : ''}`}>
      {isAccessibilityMode && (
        <div className="accessibility-instructions" style={{ 
          position: 'absolute', 
          left: '-9999px',
          width: '1px',
          height: '1px' 
        }}>
          <p>
            Header de navigare JavaVerse. 
            {isAuthenticated 
              ? 'Op»õiuni disponibile: Cursuri, Playground, Profil, Deconectare.' 
              : 'Op»õiuni disponibile: Autentificare, √énregistrare.'
            }
          </p>
        </div>
      )}
      
      <div className="jv-header-container">
        <div className="jv-header-logo">
          <Link 
            to="/" 
            className="logo-link"
            onClick={() => handleNavigation('pagina principalƒÉ')}
            aria-label={isAccessibilityMode ? "Mergi la pagina principalƒÉ JavaVerse" : undefined}
          >
            <h1 className="text-gradient">JavaVerse</h1>
          </Link>
        </div>

        {/* Buton de meniu pentru mobile */}
        <div 
          className="jv-header-mobile-toggle" 
          onClick={toggleMenu}
          role={isAccessibilityMode ? "button" : undefined}
          aria-label={isAccessibilityMode ? 
            (menuOpen ? "√énchide meniul de navigare" : "Deschide meniul de navigare") : 
            undefined
          }
          tabIndex={isAccessibilityMode ? 0 : undefined}
        >
          <div className={`menu-icon ${menuOpen ? 'open' : ''}`}>
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>

        {/* Navigare */}
        <nav 
          className={`jv-header-nav ${menuOpen ? 'open' : ''}`}
          role={isAccessibilityMode ? "navigation" : undefined}
          aria-label={isAccessibilityMode ? "Meniu principal de navigare" : undefined}
        >
          {isAuthenticated ? (
            <ul className="jv-nav-list">
              <li className="jv-nav-item">
                <Link 
                  to="/courses" 
                  className="jv-nav-link"
                  onClick={() => handleNavigation('cursuri')}
                  aria-label={isAccessibilityMode ? "Mergi la pagina cu cursuri" : undefined}
                >
                  Cursuri
                </Link>
              </li>
              <li className="jv-nav-item">
                <Link 
                  to="/playground" 
                  className="jv-nav-link"
                  onClick={() => handleNavigation('playground')}
                  aria-label={isAccessibilityMode ? "Mergi la editorul de cod playground" : undefined}
                >
                  Playground
                </Link>
              </li>
              <li className="jv-nav-item">
                <Link 
                  to="/profile" 
                  className="jv-nav-link"
                  onClick={() => handleNavigation('profil')}
                  aria-label={isAccessibilityMode ? "Mergi la pagina de profil" : undefined}
                >
                  Profil
                </Link>
              </li>
              <li className="jv-nav-item">
                <Button 
                  variant="secondary" 
                  size="small" 
                  onClick={() => handleNavigation('deconectare', handleLogout)}
                  icon="üö™"
                  iconPosition="left"
                  aria-label={isAccessibilityMode ? "DeconecteazƒÉ-te din cont" : undefined}
                >
                  Deconectare
                </Button>
              </li>
            </ul>
          ) : (
            <ul className="jv-nav-list">
              <li className="jv-nav-item">
                <Button 
                  variant="secondary" 
                  size="small" 
                  onClick={() => handleNavigation('autentificare', () => navigate('/login'))}
                  aria-label={isAccessibilityMode ? "Mergi la pagina de autentificare" : undefined}
                >
                  Autentificare
                </Button>
              </li>
              <li className="jv-nav-item">
                <Button 
                  variant="primary" 
                  size="small" 
                  onClick={() => handleNavigation('√Ænregistrare', () => navigate('/register'))}
                  glow={true}
                  aria-label={isAccessibilityMode ? "Mergi la pagina de √Ænregistrare" : undefined}
                >
                  √énregistrare
                </Button>
              </li>
            </ul>
          )}
        </nav>
      </div>
    </header>
  );
}

export default Header;
