import React, { useState, useEffect, useRef } from 'react';
import { useToast } from './ToastProvider';
import '../styles/CodeEditor.css';
import Button from './Button';

function CodeEditor({ initialCode, lessonId }) {
  const defaultCode = `public class Main {
    public static void main(String[] args) {
        // Scrie codul tÄƒu aici
        System.out.println("Bine ai venit la JavaVerse!");
    }
}`;

  const [code, setCode] = useState(initialCode || defaultCode);
  const [output, setOutput] = useState('');
  const [isRunning, setIsRunning] = useState(false);
  const [isEditorReady, setIsEditorReady] = useState(false);
  const [feedback, setFeedback] = useState('');
  const [showFeedback, setShowFeedback] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const monacoRef = useRef(null);
  const editorRef = useRef(null);
  const toast = useToast();

  // IniÈ›ializarea Monaco Editor
  useEffect(() => {
    // ImportÄƒm Monaco Editor abia cÃ¢nd componenta se monteazÄƒ
    // pentru a evita erorile de SSR
    import('@monaco-editor/react').then(monaco => {
      monacoRef.current = monaco;
    });
    return () => {
      // CurÄƒÈ›enie la demontare
      if (editorRef.current) {
        editorRef.current.dispose();
      }
    };
  }, []);

  // ActualizÄƒm codul cÃ¢nd se modificÄƒ initialCode
  useEffect(() => {
    if (initialCode) {
      setCode(initialCode);
    }
  }, [initialCode]);

  const handleEditorDidMount = (editor) => {
    editorRef.current = editor;
    setIsEditorReady(true);
  };

  const handleRunCode = async () => {
    if (!isEditorReady) {
      toast.warning('Editorul nu este pregÄƒtit Ã®ncÄƒ. Te rugÄƒm sÄƒ aÈ™tepÈ›i.');
      return;
    }

    setIsRunning(true);
    setOutput('Se executÄƒ codul...');

    try {
      // Apelul cÄƒtre API-ul nostru backend
      const response = await fetch('http://localhost:5000/api/execute-code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`
        },
        body: JSON.stringify({
          code: code
        }),
      });

      const data = await response.json();
      
      if (data.error) {
        setOutput(`Eroare: ${data.error}`);
        toast.error('A apÄƒrut o eroare la executarea codului.');
      } else {
        setOutput(data.output);
        if (data.statusCode === 200) {
          toast.success('Codul a fost executat cu succes!');
        }
      }
    } catch (error) {
      console.error('Error running code:', error);
      
      // DacÄƒ apelul API eÈ™ueazÄƒ, revenim la simulare pentru dezvoltare
      handleRunCodeSimulation();
    } finally {
      setIsRunning(false);
    }
  };

  // Simulare a rulÄƒrii codului (pentru dezvoltare fÄƒrÄƒ API)
  const handleRunCodeSimulation = () => {
    setIsRunning(true);
    setOutput('Se executÄƒ codul...');

    // SimulÄƒm un delay pentru execuÈ›ie
    setTimeout(() => {
      if (code.includes('System.out.println')) {
        // Extragem mesajul din println folosind regex
        const outputMatches = code.match(/System\.out\.println\("(.*)"\)/g);
        if (outputMatches && outputMatches.length > 0) {
          // Pentru multiple println-uri
          const outputs = outputMatches.map(match => {
            const content = match.match(/System\.out\.println\("(.*)"\)/);
            return content && content[1] ? content[1] : '';
          });
          setOutput(outputs.join('\n'));
          toast.success('Codul a fost executat cu succes!');
        } else {
          setOutput('Bine ai venit la JavaVerse!');
          toast.success('Codul a fost executat cu succes!');
        }
      } else if (code.includes('error') || code.includes('Error')) {
        setOutput('Exception in thread "main" java.lang.RuntimeException: Simulare eroare\n   at Main.main(Main.java:4)');
        toast.error('Codul conÈ›ine erori de compilare.');
      } else {
        setOutput('ExecuÈ›ie reuÈ™itÄƒ! (fÄƒrÄƒ output)');
        toast.success('Codul a fost executat cu succes!');
      }
      setIsRunning(false);
    }, 1500);
  };

  // FuncÈ›ie pentru a obÈ›ine feedback AI pentru cod
  const handleGetFeedback = async () => {
    setIsAnalyzing(true);
    setFeedback('Se analizeazÄƒ codul...');
    setShowFeedback(true);

    try {
      const response = await fetch('http://localhost:5000/api/analyze-code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`
        },
        body: JSON.stringify({
          code: code,
          lessonId: lessonId
        }),
      });

      const data = await response.json();
      
      if (data.success) {
        setFeedback(data.feedback);
        toast.success('Feedback generat cu succes!');
      } else {
        setFeedback('Nu am putut genera feedback pentru acest cod.');
        toast.error('Eroare la generarea feedback-ului.');
      }
    } catch (error) {
      console.error('Error analyzing code:', error);
      
      // DacÄƒ apelul API eÈ™ueazÄƒ, generÄƒm feedback simplu pentru dezvoltare
      setTimeout(() => {
        const simulatedFeedback = `# Analiza codului tÄƒu

Salut! IatÄƒ feedback-ul meu pentru codul tÄƒu Java:

## Puncte forte:
- Structura de bazÄƒ a programului este corectÄƒ
- FoloseÈ™ti corect metoda System.out.println()

## Sugestii de Ã®mbunÄƒtÄƒÈ›ire:
- AdaugÄƒ comentarii pentru a explica ce face codul tÄƒu
- ConsiderÄƒ folosirea mai multor metode pentru a separa logica

ContinuÄƒ sÄƒ exersezi È™i sÄƒ Ã®mbunÄƒtÄƒÈ›eÈ™ti codul!`;
        
        setFeedback(simulatedFeedback);
        setIsAnalyzing(false);
        toast.success('Feedback generat cu succes!');
      }, 2000);
    } finally {
      setIsAnalyzing(false);
    }
  };

  // ReseteazÄƒ editorul la codul iniÈ›ial
  const handleResetCode = () => {
    if (window.confirm('Sigur doreÈ™ti sÄƒ resetezi codul? Toate modificÄƒrile vor fi pierdute.')) {
      setCode(initialCode || defaultCode);
      toast.info('Codul a fost resetat.');
    }
  };

  return (
    <div className="code-editor-container">
      <div className="editor-header">
        <h3>Editor de cod Java</h3>
        <div className="editor-actions">
          <Button 
            variant="secondary"
            size="small"
            onClick={handleResetCode}
            icon="ðŸ”„"
            iconPosition="left"
          >
            ReseteazÄƒ
          </Button>
          <Button 
            variant="info"
            size="small"
            onClick={handleGetFeedback}
            disabled={isAnalyzing}
            icon={isAnalyzing ? "â³" : "ðŸ¤–"}
            iconPosition="left"
          >
            {isAnalyzing ? 'Se analizeazÄƒ...' : 'ObÈ›ine feedback AI'}
          </Button>
          <Button 
            variant="primary"
            size="small"
            onClick={handleRunCode}
            disabled={isRunning}
            icon={isRunning ? "â³" : "â–¶ï¸"}
            iconPosition="left"
          >
            {isRunning ? 'Se executÄƒ...' : 'RuleazÄƒ codul'}
          </Button>
        </div>
      </div>

      <div className="editor-wrapper">
        {monacoRef.current ? (
          <monacoRef.current.default
            height="400px"
            language="java"
            theme="vs-dark"
            value={code}
            onChange={setCode}
            onMount={handleEditorDidMount}
            options={{
              minimap: { enabled: false },
              fontSize: 14,
              scrollBeyondLastLine: false,
              automaticLayout: true,
              tabSize: 2,
              folding: true,
              lineNumbers: 'on',
              renderLineHighlight: 'all',
              suggests: true
            }}
          />
        ) : (
          <div className="editor-loading">Se Ã®ncarcÄƒ editorul...</div>
        )}
      </div>

      <div className="output-container">
        <div className="output-header">
          <h4>Rezultat</h4>
        </div>
        <pre className="output-content">{output}</pre>
      </div>

      {showFeedback && (
        <div className="feedback-container">
          <div className="feedback-header">
            <h4>Feedback AI</h4>
            <button 
              className="close-feedback" 
              onClick={() => setShowFeedback(false)}
              aria-label="ÃŽnchide feedback"
            >
              &times;
            </button>
          </div>
          <div className="feedback-content">
            {feedback}
          </div>
        </div>
      )}
    </div>
  );
}

export default CodeEditor;
