import React, { useEffect, useState } from 'react'; 
import { Link } from 'react-router-dom';
import '../styles/CoursesPage.css';  // Asigură-te că stilurile sunt corecte

function CoursesPage() {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    // Fetch cursuri de pe server (backend)
    fetch('http://localhost:5000/api/courses')
      .then((response) => response.json())
      .then((data) => {
        setCourses(data.courses);  // Setează cursurile în state
      })
      .catch((error) => console.error('Error fetching courses:', error));
  }, []);

  return (
    <div className="courses-page">
      <h1 className="page-title">Available Java Courses</h1>
      <div className="courses-grid">
        {courses.length > 0 ? (
          courses.map((course) => (
            <div key={course.courseId} className="course-card">
              <h2 className="course-title">{course.name}</h2>
              <p className="course-description">{course.description}</p>
              <Link to={`/courses/${course.courseId}`} className="course-link">View Details</Link>
            </div>
          ))
        ) : (
          <p>Loading courses...</p>
        )}
      </div>
    </div>
  );
}

export default CoursesPage;
