    observer.observe(document.body, {
      childList: true,
      subtree: true
    });
  }
  registerShortcuts() {
    // Comenzi de navigare principală (nu se schimbă)
    this.shortcuts.set('Tab', () => this.navigateNext());
    this.shortcuts.set('Shift+Tab', () => this.navigatePrevious());
    this.shortcuts.set('Enter', () => this.activateElement());
    this.shortcuts.set(' ', () => this.toggleReading()); // Space
    this.shortcuts.set('Escape', () => this.stopReading());

    // Navigare rapidă cu F-keys pentru a evita conflictele cu tastarea
    this.shortcuts.set('F1', () => this.readHelp());
    this.shortcuts.set('F2', () => this.navigateToSection('header'));
    this.shortcuts.set('F3', () => this.navigateToSection('main'));
    this.shortcuts.set('F4', () => this.navigateToSection('footer'));

    // Navigare în pagini - folosim Alt + literă pentru a evita conflictele
