    this.shortcuts.set('alt+s', () => this.adjustSpeed());
    this.shortcuts.set('alt+b', () => this.navigateBack());
    this.shortcuts.set('alt+l', () => this.listAllElements());

    // Navigare prin elemente similare cu Ctrl pentru a evita conflictele
    this.shortcuts.set('ctrl+b', () => this.nextButton());
    this.shortcuts.set('ctrl+l', () => this.nextLink());
    this.shortcuts.set('ctrl+i', () => this.nextInput());
    this.shortcuts.set('ctrl+t', () => this.nextHeading());

    // Navigare în quiz
    this.shortcuts.set('alt+1', () => this.selectQuizOption(0));
    this.shortcuts.set('alt+2', () => this.selectQuizOption(1));
    this.shortcuts.set('alt+3', () => this.selectQuizOption(2));
    this.shortcuts.set('alt+4', () => this.selectQuizOption(3));

    // Comenzi pentru editor de cod
    this.shortcuts.set('alt+e', () => this.executeCode());
    this.shortcuts.set('alt+f', () => this.getFeedback());

    // Comenzi noi pentru modul editare
    this.shortcuts.set('alt+q', () => this.readCurrentLine());
    this.shortcuts.set('alt+w', () => this.readCurrentWord());
    this.shortcuts.set('alt+d', () => this.toggleLiveReading());
  }
  handleEditModeKeydown(e) {
    // În modul editare, permitem majoritatea tastelor

    // Pentru Monaco Editor - NU FACEM NIMIC, îl lăsăm să funcționeze normal
    if (this.isMonacoEditor(this.currentEditElement)) {
      // Doar pentru Escape ieșim din modul editare
      if (e.key === 'Escape') {
        e.preventDefault();
        this.exitEditMode();
        return;
      }

      // Comenzi de citire - le procesăm dar nu prevenim default
      if (e.altKey && e.key === 'q') {
        this.readCurrentLine();
        return;
      }
      if (e.altKey && e.key === 'w') {
        this.readCurrentWord();
        return;
      }

      // Pentru ORICE altă tastă, nu facem absolut nimic
      // Lăsăm Monaco să funcționeze normal
      return;
    }

    // Restul codului pentru input-uri normale...

    // Comenzi speciale care funcționează și în modul editare
    if (e.key === 'Escape') {
      e.preventDefault();
      this.exitEditMode();
      return;
    }

    // Citire linie curentă cu Alt+Q
    if (e.altKey && e.key === 'q') {
      e.preventDefault();
      this.readCurrentLine();
      return;
    }

    // Citire cuvânt curent cu Alt+W
    if (e.altKey && e.key === 'w') {
      e.preventDefault();
      this.readCurrentWord();
      return;
    }

    // Toggle live reading cu Alt+D
    if (e.altKey && e.key === 'd') {
      e.preventDefault();
      this.toggleLiveReading();
      return;
    }

    // Pentru input-uri normale - citire la navigare stânga/dreapta
    if (['ArrowLeft', 'ArrowRight'].includes(e.key)) {
      setTimeout(() => {
        this.readCharacterAtCursor();
      }, 50);
    }
  }
  handleLiveReading(e) {
    const target = e.target;

    // Pentru Monaco Editor, folosim o abordare diferită
    if (this.isMonacoEditor(target)) {
      // Monaco are propriul său sistem de evenimente
      // Nu procesăm aici pentru a evita conflicte
      return;
    }
    if (!['INPUT', 'TEXTAREA'].includes(target.tagName)) {
      return;
    }

    // Anulăm timer-ul anterior
    if (this.typingTimer) {
      clearTimeout(this.typingTimer);
    }

    // Citim caracterul tastat imediat
    if (this.charEchoEnabled && e.data) {
      this.speak(this.getCharDescription(e.data), 'high');
    }

    // Setăm un timer pentru a citi cuvântul complet
    if (this.readFullWords) {
      this.typingTimer = setTimeout(() => {
        const word = this.getCurrentWord(target);
        if (word && word.length > 1) {
          this.speak(`Cuvânt: ${word}`, 'normal');
        }
      }, 500);
    }
  }
  getCharDescription(char) {
    // Descrieri speciale pentru caractere
    const charDescriptions = {
      ' ': 'spațiu',
      '.': 'punct',
      ',': 'virgulă',
      ';': 'punct și virgulă',
      ':': 'două puncte',
      '!': 'semnul exclamării',
      '?': 'semnul întrebării',
      '"': 'ghilimele',
      "'": 'apostrof',
      '(': 'paranteză deschisă',
      ')': 'paranteză închisă',
      '[': 'paranteză pătrată deschisă',
      ']': 'paranteză pătrată închisă',
      '{': 'acoladă deschisă',
      '}': 'acoladă închisă',
      '<': 'mai mic',
      '>': 'mai mare',
      '=': 'egal',
      '+': 'plus',
      '-': 'minus',
      '*': 'asterisc',
      '/': 'slash',
      '\\': 'backslash',
      '@': 'arobase',
      '#': 'diez',
      '$': 'dolar',
      '%': 'procent',
      '^': 'circumflex',
      '&': 'ampersand',
      '_': 'underscore',
      '|': 'bară verticală',
      '~': 'tildă',
      '`': 'accent grav',
      '\n': 'linie nouă',
      '\t': 'tab'
    };
    return charDescriptions[char] || char;
  }
  getCurrentWord(element) {
    const value = element.value || element.textContent;
    const cursorPos = element.selectionStart || value.length;

    // Găsim limitele cuvântului curent
    let start = cursorPos;
    let end = cursorPos;
    while (start > 0 && !/\s/.test(value[start - 1])) {
      start--;
    }
    while (end < value.length && !/\s/.test(value[end])) {
      end++;
    }
    return value.substring(start, end).trim();
  }
  readCurrentLine() {
    if (!this.currentEditElement) {
      this.speak("Nu ești într-un câmp de editare", 'high');
      return;
    }

    // Pentru Monaco Editor - versiune simplificată
    if (this.isMonacoEditor(this.currentEditElement)) {
      this.speak("Citirea liniei în Monaco Editor nu este disponibilă momentan", 'high');
      return;
    }

    // Pentru input/textarea normal
    const element = this.currentEditElement;
    if (element.tagName === 'TEXTAREA') {
      const value = element.value || '';
      const cursorPos = element.selectionStart || 0;

      // Găsim linia curentă
      const lines = value.split('\n');
      let currentLineIndex = 0;
      let charCount = 0;
      for (let i = 0; i < lines.length; i++) {
        charCount += lines[i].length + 1; // +1 pentru \n
        if (charCount > cursorPos) {
          currentLineIndex = i;
          break;
        }
      }
      const currentLine = lines[currentLineIndex];
      if (currentLine.trim()) {
        this.speak(`Linia ${currentLineIndex + 1}: ${currentLine}`, 'high');
      } else {
        this.speak(`Linia ${currentLineIndex + 1}: linie goală`, 'high');
      }
    } else {
      // Pentru input simplu, citim tot conținutul
      const value = element.value;
      if (value) {
        this.speak(`Conținut: ${value}`, 'high');
      } else {
        this.speak("Câmp gol", 'high');
      }
    }
  }
  readCurrentWord() {
    if (!this.currentEditElement) {
      this.speak("Nu ești într-un câmp de editare", 'high');
      return;
    }

    // Pentru Monaco Editor - versiune simplificată
    if (this.isMonacoEditor(this.currentEditElement)) {
      this.speak("Citirea cuvântului în Monaco Editor nu este disponibilă momentan", 'high');
      return;
    }

    // Pentru input/textarea normal
    const word = this.getCurrentWord(this.currentEditElement);
    if (word) {
      this.speak(`Cuvânt: ${word}`, 'high');
    } else {
      this.speak("Nu există cuvânt la poziția curentă", 'high');
    }
  }
  readCharacterAtCursor() {
    if (!this.currentEditElement) return;
    const element = this.currentEditElement;
    const value = element.value;
    const cursorPos = element.selectionStart;
    if (cursorPos < value.length) {
      const char = value[cursorPos];
      this.speak(this.getCharDescription(char), 'high');
    } else {
      this.speak("Sfârșit de text", 'high');
    }
  }
  toggleLiveReading() {
    this.liveReadingEnabled = !this.liveReadingEnabled;
    this.speak(this.liveReadingEnabled ? "Citire live activată" : "Citire live dezactivată", 'high');
  }
  activateElement() {
    if (!this.focusedElement) {
      this.speak("Niciun element selectat", 'high');
      return;
    }
    const tagName = this.focusedElement.tagName.toLowerCase();

    // Pentru Monaco Editor - SUGERĂM ALTERNATIVA
    if (this.isMonacoEditor(this.focusedElement)) {
      this.speak("Editor de cod detectat. Pentru o experiență mai bună cu modul nevăzători, recomandăm să folosiți un editor text simplu. Apasă Alt+T pentru a comuta la editorul text alternativ, sau Enter pentru a continua cu editorul actual.", 'high');

      // Oferim opțiunea de a folosi editorul normal
      this.waitingForEditorChoice = true;
      this.currentEditorElement = this.focusedElement;
      return;
    }

    // Pentru inputuri normale, permitem editarea
    if (['input', 'textarea', 'select'].includes(tagName)) {
      this.enterEditMode(this.focusedElement);
      return;
    }

    // Pentru alte elemente, simulăm click
    this.speak("Activez elementul", 'normal');
    if (this.focusedElement.click) {
      this.focusedElement.click();
    } else if (tagName === 'a') {
      window.location.href = this.focusedElement.href;
    }
  }
  enterEditMode(element) {
    this.isEditMode = true;
    this.currentEditElement = element;

    // Pentru Monaco Editor - NU FACEM NIMIC SPECIAL
    if (this.isMonacoEditor(element)) {
      const announcement = "Editor de cod selectat. Scrie normal. Escape pentru a ieși.";
      this.speak(announcement, 'high');

      // Doar marcăm vizual
      const editorContainer = element.classList.contains('monaco-editor') ? element : element.closest('.monaco-editor');
      if (editorContainer) {
        editorContainer.classList.add('accessibility-edit-mode');
      }
      document.body.classList.add('editing');
      return;
    }

    // Pentru input-uri normale
    element.focus();
    element.classList.add('accessibility-edit-mode');
    const announcement = `Mod editare activat. ${this.getEditModeInstructions(element)}`;
    this.speak(announcement, 'high');
    document.body.classList.add('editing');

    // Dezactivăm temporar navigarea
    document.dispatchEvent(new CustomEvent('editModeChanged', {
      detail: {
        isEditMode: true,
        element: element
      }
    }));
  }
  exitEditMode() {
    if (!this.isEditMode) return;
    this.isEditMode = false;
    if (this.currentEditElement) {
      this.currentEditElement.blur();
      this.currentEditElement.classList.remove('accessibility-edit-mode');
      document.body.classList.remove('editing');
      this.speak("Mod editare dezactivat. Navigare reactivată", 'high');

