import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';

function CourseDetailPage() {
  const { courseId } = useParams(); // Obține ID-ul cursului din URL
  const [course, setCourse] = useState(null);

  useEffect(() => {
    // Simulăm apelul la API pentru a obține detaliile cursului
    const courses = [
      { id: 1, name: 'Introduction to Java Programming', description: 'Learn the basics of Java programming, from syntax to simple algorithms.', content: 'In this course, you will learn how to write your first Java program, understand basic syntax, and explore fundamental programming concepts.' },
      { id: 2, name: 'Object-Oriented Programming in Java', description: 'Understand object-oriented programming concepts using Java.', content: 'This course covers OOP concepts like classes, objects, inheritance, polymorphism, and interfaces in Java.' },
      { id: 3, name: 'Advanced Java', description: 'Deep dive into advanced Java concepts like multi-threading and networking.', content: 'Explore advanced topics such as multi-threading, concurrency, networking, and Java I/O in this course.' },
      { id: 4, name: 'Java Data Structures and Algorithms', description: 'Learn how to implement various data structures and algorithms in Java.', content: 'Learn how to work with data structures like arrays, linked lists, stacks, queues, trees, and algorithms like sorting and searching.' },
      { id: 5, name: 'Java Web Development with Spring', description: 'Develop web applications using Java and the Spring framework.', content: 'In this course, you will learn how to create robust web applications using Spring MVC, Spring Boot, and Spring Data JPA.' },
      { id: 6, name: 'Java for Android Development', description: 'Build Android apps using Java as the programming language.', content: 'This course will guide you through building Android applications with Java, focusing on UI components, databases, and networking.' },
    ];

    // Căutăm cursul după ID
    const selectedCourse = courses.find(course => course.id === parseInt(courseId));
    setCourse(selectedCourse);
  }, [courseId]);

  if (!course) {
    return <div>Loading...</div>;
  }

  return (
    <div className="course-detail">
      <h1>{course.name}</h1>
      <p>{course.description}</p>
      <div>{course.content}</div>
      <Link to="/courses">Back to Courses</Link>
    </div>
  );
}

export default CourseDetailPage;
