import React from 'react';
import { Link } from 'react-router-dom'; // Importă Link din react-router-dom
import '../styles/Header.css'; // Asigură-te că calea către fișierul CSS este corectă

function Header({ isAuthenticated, handleLogout }) {
  return (
    <header className="header">
      <Link to={isAuthenticated ? '/courses' : '/'} className="logo-link">
        <h1>JavaVerse</h1>
      </Link>
      <nav className="nav">
        {/* Verificăm dacă utilizatorul este autentificat */}
        {isAuthenticated ? (
          <>
            <Link to="/profile">
              <button>Profile</button>
            </Link>
            <button onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login">
              <button>Login</button>
            </Link>
            <Link to="/register">
              <button>Register</button>
            </Link>
          </>
        )}
      </nav>
    </header>
  );
}

export default Header;
