import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/CourseDetailPage.css';

function CourseDetailPage() {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentLessonIndex, setCurrentLessonIndex] = useState(0);  // Urmărirea lecției curente

  useEffect(() => {
    fetch(`http://localhost:5000/api/courses/${courseId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.course) {
          setCourse(data.course);  // Setăm cursul în stare
          setLoading(false);
        } else {
          setLoading(false);
        }
      })
      .catch(() => setLoading(false));  // Tratează eventualele erori
  }, [courseId]);

  if (loading) return <div>Loading...</div>;
  if (!course) return <div>Course not found.</div>;

  const handleNextLesson = () => {
    if (currentLessonIndex < course.content.length - 1) {
      setCurrentLessonIndex(currentLessonIndex + 1); // Mergi la lecția următoare
    }
  };

  const currentLesson = course.content[currentLessonIndex];

  return (
    <div className="course-detail-page">
      <h1 className="course-detail-title">{course.name}</h1>
      <p className="course-detail-description">{course.description}</p>

      <h3 className="course-detail-content-title">Course Content:</h3>

      {/* Verificăm dacă content este un array valid înainte de a apela map */}
      {Array.isArray(course.content) && course.content.length > 0 ? (
        <div className="course-detail-content">
          <h4>{currentLesson.title}</h4>
          <p>{currentLesson.content}</p>
          <div className="course-lesson-text">
            {currentLesson.examples.map((example, index) => (
              <div key={index}>
                <pre>{example.code}</pre>
                <p>{example.explanation}</p>
              </div>
            ))}
          </div>
        </div>
      ) : (
        <p>No content available for this course.</p>
      )}

      <div className="course-buttons">
        <button className="back-to-courses-btn" onClick={() => navigate('/courses')}>
          Back to Courses
        </button>

        {/* Dacă suntem la prima lecție, arătăm "Start Course", altfel "Next Lesson" */}
        {currentLessonIndex === 0 ? (
          <button
            className="start-course-btn"
            onClick={() => setCurrentLessonIndex(0)} // Lansează lecția 1
          >
            Start Course
          </button>
        ) : (
          <button
            className="next-lesson-btn"
            onClick={handleNextLesson} // Mergi la lecția următoare
          >
            Next Lesson
          </button>
        )}
      </div>
    </div>
  );
}

export default CourseDetailPage;
