import React, { useEffect } from 'react';  // Am adăugat importul pentru useEffect
import { useNavigate } from 'react-router-dom';
import '../styles/MainPage.css';

function MainPage() {
  const navigate = useNavigate();
  const isAuthenticated = localStorage.getItem('authToken') ? true : false;

  // Dacă utilizatorul este autentificat, redirecționăm automat către pagina de cursuri
  useEffect(() => {
    if (isAuthenticated) {
      navigate('/courses');
    }
  }, [isAuthenticated, navigate]);

  return (
    <section className="main-banner">
      <div className="banner-content">
        <h1>Învață limbajul Java într-un mod interactiv!</h1>
        <p>Explorează cursurile pas cu pas, testează-ți cunoștințele și scrie cod în timp real într-un mediu modern și prietenos</p>
        
        {/* Afișează butonul doar dacă utilizatorul nu este autentificat */}
        {!isAuthenticated && (
          <button onClick={() => navigate('/login')}>Începe acum</button>
        )}
      </div>
      
      {/* Adăugăm elemente decorative */}
      <div className="floating-code left">
        <pre>{`public class HelloWorld {
  public static void main(String[] args) {
    System.out.println("Welcome to JavaVerse!");
  }
}`}</pre>
      </div>
      
      <div className="floating-code right">
        <pre>{`for(int i = 0; i < stars.length; i++) {
  stars[i].twinkle();
  universe.expand();
}`}</pre>
      </div>
    </section>
  );
}

export default MainPage;
