// Login.js
import React, { useState } from 'react';
import '../styles/FormPage.css'; // Importăm fișierul CSS pentru stiluri

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('/api/users/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    })
    .then(response => response.json())
    .then(data => {
      if (data.success) {
        // Stocăm token-ul pentru autentificare viitoare
        localStorage.setItem('authToken', data.token);
        // Redirect sau mesaje de succes (poți adăuga un redirect aici)
      } else {
        // Afișează eroare
        alert(data.message || 'Autentificare eșuată');
      }
    });
  };

  return (
    <div className="form-container">
      <h1>Login</h1>
      <form className="form" onSubmit={handleSubmit}>
        <div className="input-group">
          <label htmlFor="email">Email</label>
          <input 
            type="email" 
            id="email" 
            placeholder="Enter your email" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="input-group">
          <label htmlFor="password">Password</label>
          <input 
            type="password" 
            id="password" 
            placeholder="Enter your password" 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="submit-button">Login</button>
      </form>
      <p className="switch-page">
        Don't have an account? <a href="/register">Register</a>
      </p>
    </div>
  );
}

export default Login;
