// src/providers/AccessibilityProvider.js
import React, { createContext, useContext, useEffect, useState } from 'react';
import accessibilityManager from '../utils/AccessibilityManager';

const AccessibilityContext = createContext();

export const useAccessibilityContext = () => {
  const context = useContext(AccessibilityContext);
  if (!context) {
    throw new Error('useAccessibilityContext must be used within AccessibilityProvider');
  }
  return context;
};

export const AccessibilityProvider = ({ children }) => {
  const [isAccessibilityMode, setIsAccessibilityMode] = useState(false);

  useEffect(() => {
    // Listener pentru schimbări de mod accesibilitate
    const handleAccessibilityChange = (event) => {
      setIsAccessibilityMode(event.detail.isActive);
    };

    document.addEventListener('accessibilityModeChanged', handleAccessibilityChange);
    
    // Setăm starea inițială
    setIsAccessibilityMode(accessibilityManager.isActive);

    // Anunțăm instrucțiunile de activare la încărcarea paginii
    const announceInitialInstructions = () => {
      setTimeout(() => {
        accessibilityManager.speak(
          "Pentru a accesa modul de către nevăzători, apasă Control plus Shift plus A",
          'high'
        );
      }, 2000);
    };

    // Anunțăm doar o dată la încărcarea aplicației
    if (!sessionStorage.getItem('accessibilityInstructionsShown')) {
      announceInitialInstructions();
      sessionStorage.setItem('accessibilityInstructionsShown', 'true');
    }

    return () => {
      document.removeEventListener('accessibilityModeChanged', handleAccessibilityChange);
    };
  }, []);

  const value = {
    isAccessibilityMode,
    accessibilityManager
  };

  return (
    <AccessibilityContext.Provider value={value}>
      {children}
    </AccessibilityContext.Provider>
  );
};
