// src/components/AccessibleCodePlayground.js - Navigation with arrow keys through examples
import React, { useState, useEffect } from 'react';
import { useAccessibility } from '../hooks/useAccessibility';
import MonacoCodeEditor from './MonacoCodeEditor';
import '../styles/CodePlayground.css';

function AccessibleCodePlayground() {
  const [selectedExampleIndex, setSelectedExampleIndex] = useState(0);
  const [isInEditor, setIsInEditor] = useState(false);
  const [code, setCode] = useState('');

  const { 
    isAccessibilityMode, 
    useCodeEditorAccessibility,
    useNavigationAccessibility,
    announceLoading 
  } = useAccessibility();
  
  const { announceCodeExecution, announceExecutionResult } = useCodeEditorAccessibility();
  const { announcePageLoad } = useNavigationAccessibility();

  // Exemple de cod predefinite
  const codeExamples = [
    {
      title: "Hello World",
      description: "Primul tău program Java - afișează un salut simplu",
      code: `public class HelloWorld {
    public static void main(String[] args) {
        System.out.println("Hello, World!");
    }
}`
    },
    {
      title: "Variabile și Tipuri",
      description: "Învață să declari și să folosești variabile în Java",
      code: `public class Variables {
    public static void main(String[] args) {
        int age = 25;
        String name = "Java Developer";
        double salary = 5000.50;
        boolean isEmployed = true;
        
        System.out.println("Nume: " + name);
        System.out.println("Vârsta: " + age);
        System.out.println("Salar: " + salary);
        System.out.println("Angajat: " + isEmployed);
    }
}`
    },
    {
      title: "Structuri Condiționale",
      description: "Folosește if-else pentru a lua decizii în cod",
      code: `public class Conditionals {
    public static void main(String[] args) {
        int score = 85;
        
        if (score >= 90) {
            System.out.println("Nota: A");
        } else if (score >= 80) {
            System.out.println("Nota: B");
        } else if (score >= 70) {
            System.out.println("Nota: C");
        } else {
            System.out.println("Nota: F");
        }
    }
}`
    },
    {
      title: "Bucle",
      description: "Repetă operații cu for și while",
      code: `public class Loops {
    public static void main(String[] args) {
        // Bucla for
        System.out.println("Numerele de la 1 la 5:");
        for (int i = 1; i <= 5; i++) {
            System.out.println(i);
        }
        
        // Bucla while
        System.out.println("Countdown:");
        int countdown = 3;
        while (countdown > 0) {
            System.out.println(countdown);
            countdown--;
        }
        System.out.println("Start!");
    }
}`
    },
    {
      title: "Arrays",
      description: "Lucrează cu colecții de elemente",
      code: `public class Arrays {
    public static void main(String[] args) {
        String[] fruits = {"măr", "banană", "portocală", "strugure"};
        
        System.out.println("Fructele mele preferate:");
        for (int i = 0; i < fruits.length; i++) {
            System.out.println((i + 1) + ". " + fruits[i]);
        }
        
        // Enhanced for loop
        System.out.println("\\nCu enhanced for:");
        for (String fruit : fruits) {
            System.out.println("- " + fruit);
        }
    }
}`
    }
  ];

  // Listener pentru comenzile rapide de accesibilitate
  useEffect(() => {
    if (!isAccessibilityMode) return;

    const handleKeyPress = (e) => {
      if (!isAccessibilityMode) return;

      // Dacă suntem în editor, lăsăm Monaco să gestioneze
      if (isInEditor) return;

      // Navigare prin exemple cu săgețile
      if (e.key === 'ArrowLeft') {
        e.preventDefault();
        const newIndex = selectedExampleIndex > 0 ? selectedExampleIndex - 1 : codeExamples.length - 1;
        setSelectedExampleIndex(newIndex);
        announceExample(codeExamples[newIndex], newIndex);
      } else if (e.key === 'ArrowRight') {
        e.preventDefault();
        const newIndex = selectedExampleIndex < codeExamples.length - 1 ? selectedExampleIndex + 1 : 0;
        setSelectedExampleIndex(newIndex);
        announceExample(codeExamples[newIndex], newIndex);
      }
      
      // Enter pentru a încărca exemplul în editor
      if (e.key === 'Enter') {
        e.preventDefault();
        loadExampleInEditor(codeExamples[selectedExampleIndex]);
      }
      
      // Escape pentru a ieși din editor și a reveni la exemple
      if (e.key === 'Escape' && isInEditor) {
        e.preventDefault();
        exitEditor();
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [isAccessibilityMode, selectedExampleIndex, isInEditor]);

  // Anunțarea unui exemplu
  const announceExample = (example, index) => {
    const announcement = `Exemplul ${index + 1} din ${codeExamples.length}: ${example.title}. ${example.description}. Apasă Enter pentru a încărca în editor.`;
    announceLoading(announcement);
  };

  // Încărcarea paginii
  useEffect(() => {
    if (isAccessibilityMode) {
      announcePageLoad(
        'Playground de cod Java', 
        `Aici poți experimenta cu cod Java. Sunt disponibile ${codeExamples.length} exemple. Folosește săgețile stânga și dreapta pentru navigare.`
      );
      
      // Anunțăm primul exemplu
      setTimeout(() => {
        announceExample(codeExamples[0], 0);
      }, 1500);
    }
  }, [isAccessibilityMode]);

  // Încărcarea unui exemplu în editor
  const loadExampleInEditor = (example) => {
    setCode(example.code);
    setIsInEditor(true);
    
    if (isAccessibilityMode) {
      announceLoading(`Exemplul "${example.title}" a fost încărcat în editor. Modul editare activat.`);
    }
  };

  // Ieșirea din editor
  const exitEditor = () => {
    setIsInEditor(false);
    
    if (isAccessibilityMode) {
      announceLoading('Ai ieșit din editor. Navighează din nou prin exemple cu săgețile.');
    }
  };

  // Executarea codului
  const executeCode = () => {
    if (isAccessibilityMode) {
      announceCodeExecution();
    }
    
    // Simulare execuție (în realitate ar trebui să trimitem la un server)
    setTimeout(() => {
      const output = "Cod executat cu succes! Verifică rezultatul în consola de output.";
      if (isAccessibilityMode) {
        announceExecutionResult(output);
      }
    }, 1000);
  };

  return (
    <div className="code-playground-container">
      {isAccessibilityMode && (
        <div className="accessibility-instructions" style={{ 
          position: 'absolute', 
          left: '-9999px',
          width: '1px',
          height: '1px' 
        }}>
          <p>
            Playground de cod Java. Navighează prin exemple cu săgețile stânga și dreapta. 
            Enter pentru a încărca exemplul în editor. Escape pentru a ieși din editor.
          </p>
        </div>
      )}

      <h1>Java Code Playground</h1>
      <p className="playground-description">
        Experimentează cu cod Java folosind exemplele de mai jos sau scrie propriul tău cod.
      </p>

      {!isInEditor ? (
        // Secțiunea cu exemple
        <div className="examples-section">
          <h2>Exemple de cod</h2>
          <div className="examples-grid">
            {codeExamples.map((example, index) => (
              <div 
                key={index}
                className={`example-card ${isAccessibilityMode && index === selectedExampleIndex ? 'accessibility-focus' : ''}`}
                onClick={() => loadExampleInEditor(example)}
                role={isAccessibilityMode ? "button" : undefined}
                aria-label={isAccessibilityMode ? `Exemplul ${index + 1}: ${example.title}` : undefined}
                tabIndex={isAccessibilityMode ? (index === selectedExampleIndex ? 0 : -1) : 0}
              >
                <h3 className="example-title">{example.title}</h3>
                <p className="example-description">{example.description}</p>
                <div className="example-code-preview">
                  <code>{example.code.substring(0, 100)}...</code>
                </div>
                {!isAccessibilityMode && (
                  <button 
                    className="load-example-btn"
                    onClick={(e) => {
                      e.stopPropagation();
                      loadExampleInEditor(example);
                    }}
                  >
                    Încarcă în Editor
                  </button>
                )}
              </div>
            ))}
          </div>
          
          {isAccessibilityMode && (
            <div className="accessibility-navigation-info">
              <p>
                Exemplul curent: {selectedExampleIndex + 1} din {codeExamples.length}. 
                Folosește săgețile pentru navigare și Enter pentru încărcare.
              </p>
            </div>
          )}
        </div>
      ) : (
        // Secțiunea cu editorul
        <div className="editor-section">
          <h2>Editor de cod</h2>
          <div className="editor-container">
            <MonacoCodeEditor
              value={code}
              onChange={setCode}
              language="java"
              height="400px"
            />
          </div>
          
          <div className="editor-actions">
            <button 
              onClick={executeCode}
              className="execute-btn"
              aria-label={isAccessibilityMode ? "Execută codul din editor" : undefined}
            >
              🚀 Execută Codul
            </button>
            
            <button 
              onClick={exitEditor}
              className="back-to-examples-btn"
              aria-label={isAccessibilityMode ? "Înapoi la lista de exemple" : undefined}
            >
              📚 Înapoi la Exemple
            </button>
          </div>
          
          <div className="output-section">
            <h3>Output</h3>
            <div className="output-console">
              <p>Apasă "Execută Codul" pentru a vedea rezultatul...</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AccessibleCodePlayground;
