import React, { useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/MainPage.css';

function MainPage() {
  const navigate = useNavigate();
  const isAuthenticated = localStorage.getItem('authToken') ? true : false;
  const mouseBlobRef = useRef(null);
  
  // Redirecționare dacă utilizatorul este autentificat
  useEffect(() => {
    if (isAuthenticated) {
      navigate('/courses');
    }
  }, [isAuthenticated, navigate]);
  
  // Efect pentru inițializarea fundalului ultramodern
  useEffect(() => {
    // Referință la container
    const bannerElement = document.querySelector('.main-banner');
    
    // 1. Adăugăm glow neon la partea de sus
    const neonGlow = document.createElement('div');
    neonGlow.classList.add('neon-glow-top');
    bannerElement.appendChild(neonGlow);
    
    // 2. Adăugăm grid-ul modern 3D
    const modernGrid = document.createElement('div');
    modernGrid.classList.add('modern-grid');
    bannerElement.appendChild(modernGrid);
    
    // 3. Adăugăm strălucirile soft
    const glowColors = [
      { className: 'soft-glow-1' },
      { className: 'soft-glow-2' },
      { className: 'soft-glow-3' }
    ];
    
    glowColors.forEach(glow => {
      const glowElement = document.createElement('div');
      glowElement.classList.add('soft-glow', glow.className);
      bannerElement.appendChild(glowElement);
    });
    
    // 4. Adăugăm liniile de cod fluide
    const flowingCode = document.createElement('div');
    flowingCode.classList.add('flowing-code');
    bannerElement.appendChild(flowingCode);
    
    // Generăm 10 linii de cod fluide
    for (let i = 0; i < 10; i++) {
      const codeLine = document.createElement('div');
      codeLine.classList.add('code-line');
      
      // Proprietăți random pentru fiecare linie
      const width = Math.random() * 100 + 100;
      const posX = Math.random() * 100;
      const delay = Math.random() * 20;
      const duration = Math.random() * 5 + 10;
      
      codeLine.style.width = `${width}px`;
      codeLine.style.left = `${posX}%`;
      codeLine.style.animationDelay = `${delay}s`;
      codeLine.style.animationDuration = `${duration}s`;
      
      flowingCode.appendChild(codeLine);
    }
    
    // 5. Adăugăm particule moderne
    for (let i = 0; i < 50; i++) {
      const particle = document.createElement('div');
      particle.classList.add('modern-particle');
      
      // Proprietăți random pentru fiecare particulă
      const size = Math.random() * 3 + 1;
      const posX = Math.random() * 100;
      const posY = Math.random() * 100;
      const delay = Math.random() * 10;
      const duration = Math.random() * 5 + 5;
      
      // Setăm stilurile pentru particule
      particle.style.width = `${size}px`;
      particle.style.height = `${size}px`;
      particle.style.left = `${posX}%`;
      particle.style.top = `${posY}%`;
      particle.style.animationDelay = `${delay}s`;
      particle.style.animationDuration = `${duration}s`;
      
      bannerElement.appendChild(particle);
    }
    
    // 6. Adăugăm efectul de nebulozitate
    const nebula = document.createElement('div');
    nebula.classList.add('nebula');
    bannerElement.appendChild(nebula);
    
    // 7. Adăugăm blob-ul care urmează mouse-ul
    const mouseBlob = document.createElement('div');
    mouseBlob.classList.add('mouse-blob');
    bannerElement.appendChild(mouseBlob);
    mouseBlobRef.current = mouseBlob;
    
    // 8. Adăugăm efectul de curvature la margini
    const edgeCurvature = document.createElement('div');
    edgeCurvature.classList.add('edge-curvature');
    bannerElement.appendChild(edgeCurvature);
    
    // Adăugăm event listener pentru mișcarea mouse-ului
    const handleMouseMove = (e) => {
      if (mouseBlobRef.current) {
        const mouseX = e.clientX;
        const mouseY = e.clientY;
        
        mouseBlobRef.current.style.left = `${mouseX}px`;
        mouseBlobRef.current.style.top = `${mouseY}px`;
        mouseBlobRef.current.style.opacity = '0.7';
      }
    };
    
    // Adăugăm event listener pentru mouseenter/mouseleave
    const handleMouseEnter = () => {
      if (mouseBlobRef.current) {
        mouseBlobRef.current.style.opacity = '0.5';
      }
    };
    
    const handleMouseLeave = () => {
      if (mouseBlobRef.current) {
        mouseBlobRef.current.style.opacity = '0';
      }
    };
    
    document.addEventListener('mousemove', handleMouseMove);
    bannerElement.addEventListener('mouseenter', handleMouseEnter);
    bannerElement.addEventListener('mouseleave', handleMouseLeave);
    
    // Atribuim valoarea data-text pentru efectul de glow al titlului
    document.querySelectorAll('.title-word').forEach(word => {
      word.setAttribute('data-text', word.textContent);
    });
    
    // Cleanup function
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      bannerElement.removeEventListener('mouseenter', handleMouseEnter);
      bannerElement.removeEventListener('mouseleave', handleMouseLeave);
      
      // Curățăm toate elementele adăugate
      const elementsToRemove = [
        '.neon-glow-top', 
        '.modern-grid', 
        '.soft-glow', 
        '.flowing-code',
        '.modern-particle', 
        '.nebula', 
        '.mouse-blob', 
        '.edge-curvature'
      ];
      
      elementsToRemove.forEach(selector => {
        document.querySelectorAll(selector).forEach(el => el.remove());
      });
    };
  }, []);

  return (
    <section className="main-banner">
      {/* Container principal pentru conținut */}
      <div className="banner-content">
        <h1 className="animated-title">
          <span className="title-word">Învață</span>
          <span className="title-word">limbajul</span>
          <span className="title-word">Java</span>
          <span className="title-word">într-un mod interactiv!</span>
        </h1>
        
        {/* Butonul principal pentru începere */}
        {!isAuthenticated && (
          <div className="cta-container">
            <button 
              className="main-cta" 
              onClick={() => navigate('/login')}
            >
              <span className="button-text">Începe acum</span>
              <span className="button-icon">→</span>
            </button>
          </div>
        )}
        
        {/* Container pentru cardurile cu caracteristici */}
        <div className="feature-cards-container">
          <div className="feature-card">
            <div className="feature-icon-container courses-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M12 2L2 7L12 12L22 7L12 2Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M2 17L12 22L22 17" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M2 12L12 17L22 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
            <div className="feature-content">
              <h3>Cursuri interactive</h3>
              <p>Lecții structurate și exemple practice</p>
            </div>
          </div>
          
          <div className="feature-card">
            <div className="feature-icon-container tests-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M9 11L12 14L22 4" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M21 12V19C21 19.5304 20.7893 20.0391 20.4142 20.4142C20.0391 20.7893 19.5304 21 19 21H5C4.46957 21 3.96086 20.7893 3.58579 20.4142C3.21071 20.0391 3 19.5304 3 19V5C3 4.46957 3.21071 3.96086 3.58579 3.58579C3.96086 3.21071 4.46957 3 5 3H16" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
            <div className="feature-content">
              <h3>Teste practice</h3>
              <p>Verifică-ți cunoștințele dobândite</p>
            </div>
          </div>
          
          <div className="feature-card">
            <div className="feature-icon-container editor-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M16 18L22 12L16 6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M8 6L2 12L8 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
            <div className="feature-content">
