import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/CourseDetailPage.css';  // Asigură-te că ai importat fișierul CSS

function CourseDetailPage() {
  const { courseId } = useParams();  // Obținem courseId din URL
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentLessonIndex, setCurrentLessonIndex] = useState(0); // Indexul lecției curente
  const [showContent, setShowContent] = useState(false); // Control pentru a extinde lecțiile

  useEffect(() => {
    // Fetch pentru a obține informațiile despre curs
    fetch(`http://localhost:5000/api/courses/${courseId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.course) {
          setCourse(data.course);
          setLoading(false);
        } else {
          setLoading(false);
        }
      })
      .catch(() => setLoading(false));  // Tratează erorile de rețea
  }, [courseId]);

  if (loading) return <div>Loading...</div>;
  if (!course) return <div>Course not found.</div>;

  const handleStartCourse = () => {
    setShowContent(true);  // Când apasă "Start Course", afișăm lecțiile
  };

  const handleNextLesson = () => {
    if (currentLessonIndex < course.content.length - 1) {
      setCurrentLessonIndex(currentLessonIndex + 1); // Mergem la lecția următoare
    }
  };

  const handleBackToCourses = () => {
    navigate('/courses');
  };

  return (
    <div className="course-detail-page">
      <h1 className="course-detail-title">{course.name}</h1>
      <p className="course-detail-description">{course.description}</p>

      {/* Titlul pentru "Course Content" */}
      <div className="course-detail-content">
        <h3 className="course-detail-content-title">Course Content:</h3>
        
        {/* Afișăm lista de lecții */}
        <ul className="course-detail-content-list">
          {course.content.map((lesson, index) => (
            <li key={index} className={`course-detail-content-item ${currentLessonIndex === index ? 'active' : ''}`}>
              {lesson.title}
            </li>
          ))}
        </ul>
      </div>

      {/* Afisăm detaliile lecției curente */}
      {showContent && course.content[currentLessonIndex] && (
        <div className="course-lesson-text">
          <h4>{course.content[currentLessonIndex].title}</h4>
          <p>{course.content[currentLessonIndex].content}</p>
          <pre>
            {course.content[currentLessonIndex].examples.map((example, idx) => (
              <div key={idx}>
                <code>{example.code}</code>
                <p>{example.explanation}</p>
              </div>
            ))}
          </pre>
        </div>
      )}

      <div className="course-buttons">
        <button className="back-to-courses-btn" onClick={handleBackToCourses}>
          Back to Courses
        </button>

        {/* Butonul de "Next Lesson" care trece la următoarea lecție */}
        <button
          className="start-course-btn"
          onClick={showContent ? handleNextLesson : handleStartCourse}
        >
          {currentLessonIndex === course.content.length - 1 ? 'Finish Course' : 'Next Lesson'}
        </button>
      </div>
    </div>
  );
}

export default CourseDetailPage;
