    }
  ];

  const [selectedExample, setSelectedExample] = useState(predefinedExamples[0]);
  const [code, setCode] = useState(predefinedExamples[0].code);
  const [showAllExamples, setShowAllExamples] = useState(false);
  const [editorKey, setEditorKey] = useState(0); // Cheie pentru a forÈ›a re-randarea editorului

  // Gestionar pentru schimbarea exemplului selectat
  const handleExampleChange = (example) => {
    setSelectedExample(example);
    setCode(example.code);
    // ForÈ›Äƒm re-randarea editorului pentru a ne asigura cÄƒ se Ã®ncarcÄƒ
    setEditorKey(prev => prev + 1);
  };

  // Gestionar pentru resetarea codului la exemplu
  const handleResetCode = () => {
    setCode(selectedExample.code);
    // ForÈ›Äƒm re-randarea editorului
    setEditorKey(prev => prev + 1);
  };

  // Effect pentru a forÈ›a Ã®ncÄƒrcarea iniÈ›ialÄƒ a editorului
  useEffect(() => {
    // ForÈ›Äƒm o re-randare dupÄƒ ce componenta s-a montat
    const timer = setTimeout(() => {
      setEditorKey(prev => prev + 1);
    }, 100);

    return () => clearTimeout(timer);
  }, []); // RuleazÄƒ doar la prima Ã®ncÄƒrcare

  // Filtru pentru exemplele afiÈ™ate
  const visibleExamples = showAllExamples 
    ? predefinedExamples 
    : predefinedExamples.slice(0, 3);

  return (
    <div className="code-playground-container">
      <h1 className="code-playground-title">Playground Java</h1>
      <p className="code-playground-description">
        ExperimenteazÄƒ cu Java Ã®n editorul nostru interactiv. Alege unul dintre exemplele predefinite 
        sau scrie propriul tÄƒu cod pentru a Ã®nvÄƒÈ›a prin practicÄƒ.
      </p>

      <div className="playground-layout">
        <div className="examples-sidebar">
          <h3>Exemple</h3>
          <div className="examples-list">
            {visibleExamples.map((example) => (
              <Card 
                key={example.id}
                className={`example-card ${selectedExample.id === example.id ? 'selected' : ''}`}
                variant="dark"
                title={example.name}
                onClick={() => handleExampleChange(example)}
                hoverable={true}
              />
            ))}
            
            {!showAllExamples && predefinedExamples.length > 3 && (
              <Button 
                variant="secondary" 
                size="small"
                onClick={() => setShowAllExamples(true)}
                className="show-more-btn"
              >
                AratÄƒ toate exemplele
              </Button>
            )}
            
            {showAllExamples && (
              <Button 
                variant="secondary" 
                size="small"
                onClick={() => setShowAllExamples(false)}
                className="show-less-btn"
              >
                AratÄƒ mai puÈ›ine
              </Button>
            )}
          </div>
          
          <div className="playground-tips">
            <h4>Sfaturi</h4>
            <ul>
              <li>FoloseÈ™te exemplele pentru a Ã®nvÄƒÈ›a sintaxa Java</li>
              <li>ExperimenteazÄƒ prin modificarea codului</li>
              <li>RuleazÄƒ codul pentru a vedea rezultatele imediat</li>
              <li>Cere feedback AI pentru a Ã®mbunÄƒtÄƒÈ›i codul</li>
            </ul>
          </div>
        </div>
        
        <div className="code-editor-area">
          <div className="editor-header-bar">
            <h3>Editor: {selectedExample.name}</h3>
            <Button 
              variant="secondary" 
              size="small"
              onClick={handleResetCode}
              icon="ðŸ”„"
              iconPosition="left"
            >
              Reset la exemplu
            </Button>
          </div>
          
          <CodeEditor 
            key={editorKey} // Cheia forÈ›eazÄƒ re-randarea cÃ¢nd se schimbÄƒ
            initialCode={code} 
            onChange={setCode} 
          />
        </div>
      </div>
    </div>
  );
}

export default CodePlayground;
