import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Importă useNavigate pentru redirecționare
import '../styles/MainPage.css'; // Asigură-te că calea către fișierul CSS este corectă

function MainPage() {
  const navigate = useNavigate();  // Folosește useNavigate pentru redirecționare
  const isAuthenticated = localStorage.getItem('authToken') ? true : false;

  // Dacă utilizatorul este autentificat, redirecționează-l automat către pagina de cursuri
  useEffect(() => {
    if (isAuthenticated) {
      navigate('/courses');
    }
  }, [isAuthenticated, navigate]);

  return (
    <section className="main-banner">
      <div className="banner-content">
        <h1>Învață limbajul Java într-un mod interactiv!</h1>
        <p>Explorează cursurile pas cu pas, testează-ți cunoștințele și scrie cod în timp real</p>
        {/* Afișează butonul doar dacă utilizatorul nu este autentificat */}
        {!isAuthenticated && (
          <button onClick={() => navigate('/login')}>Începe acum</button>
        )}
      </div>
    </section>
  );
}

export default MainPage;
