// src/components/AccessibilityIndicator.js
import React from 'react';
import { useAccessibility } from '../hooks/useAccessibility';

const AccessibilityIndicator = () => {
  const { isAccessibilityMode, isSpeaking } = useAccessibility();

  if (!isAccessibilityMode) return null;

  return (
    <div style={{
      position: 'fixed',
      top: '10px',
      right: '10px',
      background: 'rgba(0, 0, 0, 0.8)',
      color: 'white',
      padding: '8px 12px',
      borderRadius: '4px',
      fontSize: '12px',
      zIndex: 9999,
      display: 'flex',
      alignItems: 'center',
      gap: '8px'
    }}>
      <span style={{
        width: '8px',
        height: '8px',
        borderRadius: '50%',
        backgroundColor: isSpeaking ? '#00ff00' : '#ffff00',
        animation: isSpeaking ? 'pulse 1s infinite' : 'none'
      }}></span>
      <span>Mod accesibilitate {isSpeaking ? '(vorbește)' : '(activ)'}</span>
      
      <style>{`
        @keyframes pulse {
          0% { opacity: 1; }
          50% { opacity: 0.5; }
          100% { opacity: 1; }
        }
      `}</style>
    </div>
  );
};

export default AccessibilityIndicator;
