// src/components/Register.js - Improved with accessibility focus management
import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useToast } from './ToastProvider';
import { useAccessibility } from '../hooks/useAccessibility';
import '../styles/FormPage.css';

function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const toast = useToast();
  
  // Refs pentru focus management în modul accesibilitate
  const usernameRef = useRef(null);
  const emailRef = useRef(null);
  const passwordRef = useRef(null);
  
  // Adăugat suport pentru accesibilitate
  const { 
    isAccessibilityMode, 
    useNavigationAccessibility,
    announceFormError,
    announceFormSuccess,
    announceLoading 
  } = useAccessibility();
  
  const { announcePageLoad } = useNavigationAccessibility();

  // Anunțăm încărcarea paginii pentru utilizatorii cu accesibilitate
  useEffect(() => {
    if (isAccessibilityMode) {
      announcePageLoad(
        'Înregistrare', 
        'Creează un cont nou în JavaVerse completând numele de utilizator, email-ul și parola.'
      );
      
      // ÎMBUNĂTĂȚIRE: Focus direct pe username în modul accesibilitate
      setTimeout(() => {
        if (usernameRef.current) {
          usernameRef.current.focus();
          announceLoading('Focus pe câmpul nume utilizator. Introdu numele de utilizator dorit.');
        }
      }, 1500);
    }
  }, [isAccessibilityMode, announcePageLoad, announceLoading]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validare simplă
    if (!username || !email || !password) {
      const errorMessage = 'Te rugăm să completezi toate câmpurile';
      toast.warning(errorMessage);
      if (isAccessibilityMode) {
        announceFormError('formular', errorMessage);
      }
      return;
    }
    
    // Validare parolă
    if (password.length < 6) {
      const errorMessage = 'Parola trebuie să conțină cel puțin 6 caractere';
      toast.warning(errorMessage);
      if (isAccessibilityMode) {
        announceFormError('parolă', errorMessage);
      }
      return;
    }

    setIsLoading(true);
    
    if (isAccessibilityMode) {
      announceLoading('Se procesează înregistrarea...');
    }

    try {
      const response = await fetch('http://localhost:5000/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password }),
      });
      
      const data = await response.json();

      if (data.success) {
        const successMessage = 'Cont creat cu succes! Te poți autentifica acum.';
        toast.success(successMessage, 5000);
        if (isAccessibilityMode) {
          announceFormSuccess(successMessage);
          // Persistăm modul de accesibilitate înainte de navigare
          sessionStorage.setItem('accessibilityModeActive', 'true');
        }
        navigate('/login'); // Redirect la login după înregistrare
      } else {
        let errorMessage;
        if (data.message.includes('email')) {
          errorMessage = 'Adresa de email este deja asociată unui cont.';
        } else if (data.message.includes('username')) {
          errorMessage = 'Numele de utilizator este deja folosit.';
        } else {
          errorMessage = data.message || 'Înregistrare eșuată. Te rugăm să încerci din nou.';
        }
        
        toast.error(errorMessage);
        if (isAccessibilityMode) {
          announceFormError('înregistrare', errorMessage);
        }
      }
    } catch (error) {
      console.error('Error:', error);
      const errorMessage = 'A apărut o eroare de comunicare cu serverul. Încearcă din nou.';
      toast.error(errorMessage);
      if (isAccessibilityMode) {
        announceFormError('conexiune', errorMessage);
      }
    } finally {
      setIsLoading(false);
    }
  };

  // ÎMBUNĂTĂȚIRE: Handler pentru navigarea cu tastatura în modul accesibilitate
  const handleKeyDown = (e) => {
    if (!isAccessibilityMode) return;
    
    if (e.key === 'Tab') {
      // Pentru navigarea cu Tab în formular
      if (e.target === usernameRef.current && !e.shiftKey) {
        e.preventDefault();
        emailRef.current.focus();
        announceLoading('Focus pe câmpul email.');
      } else if (e.target === emailRef.current && !e.shiftKey) {
        e.preventDefault();
        passwordRef.current.focus();
        announceLoading('Focus pe câmpul parolă.');
      } else if (e.target === passwordRef.current && e.shiftKey) {
        e.preventDefault();
        emailRef.current.focus();
        announceLoading('Focus pe câmpul email.');
      } else if (e.target === emailRef.current && e.shiftKey) {
        e.preventDefault();
        usernameRef.current.focus();
        announceLoading('Focus pe câmpul nume utilizator.');
      }
    }
  };

  return (
    <div className="form-container">
      {isAccessibilityMode && (
        <div className="accessibility-instructions" style={{ 
          position: 'absolute', 
          left: '-9999px',
          width: '1px',
          height: '1px' 
        }}>
          <p>
            Formular de înregistrare. Completează numele de utilizator, email-ul și parola, 
            apoi apasă Enter sau butonul Înregistrare.
          </p>
        </div>
      )}
      
      <h1>Înregistrare</h1>
      <form className="form" onSubmit={handleSubmit} onKeyDown={handleKeyDown}>
        <div className="input-group">
          <label htmlFor="username">Nume utilizator</label>
          <input 
            ref={usernameRef}
            type="text" 
            id="username" 
            placeholder="Alege un nume de utilizator" 
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            autoComplete="username"
            aria-label={isAccessibilityMode ? "Numele de utilizator pentru contul nou" : undefined}
            aria-describedby={isAccessibilityMode ? "username-help" : undefined}
            tabIndex={isAccessibilityMode ? -1 : undefined} // Controlăm manual focus-ul în accessibility mode
          />
          {isAccessibilityMode && (
            <div id="username-help" style={{ fontSize: '0.9rem', color: '#bbb', marginTop: '5px' }}>
              Alege un nume unic pentru contul tău
            </div>
          )}
        </div>
        <div className="input-group">
          <label htmlFor="email">Email</label>
          <input 
            ref={emailRef}
            type="email" 
            id="email" 
            placeholder="Introdu adresa de email" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            autoComplete="email"
            aria-label={isAccessibilityMode ? "Adresa de email pentru contul nou" : undefined}
            aria-describedby={isAccessibilityMode ? "email-help" : undefined}
            tabIndex={isAccessibilityMode ? -1 : undefined} // Controlăm manual focus-ul în accessibility mode
          />
          {isAccessibilityMode && (
            <div id="email-help" style={{ fontSize: '0.9rem', color: '#bbb', marginTop: '5px' }}>
              Introdu o adresă de email validă
            </div>
          )}
        </div>
        <div className="input-group">
          <label htmlFor="password">Parolă</label>
          <input 
            ref={passwordRef}
            type="password" 
            id="password" 
            placeholder="Creează o parolă" 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            autoComplete="new-password"
            aria-label={isAccessibilityMode ? "Parola pentru contul nou, minim 6 caractere" : undefined}
            aria-describedby={isAccessibilityMode ? "password-help" : undefined}
            tabIndex={isAccessibilityMode ? -1 : undefined} // Controlăm manual focus-ul în accessibility mode
          />
          {isAccessibilityMode && (
            <div id="password-help" style={{ fontSize: '0.9rem', color: '#bbb', marginTop: '5px' }}>
              Parola trebuie să aibă cel puțin 6 caractere
            </div>
          )}
        </div>
        <button 
          type="submit" 
          className="submit-button"
          disabled={isLoading}
          aria-label={isAccessibilityMode ? 
            (isLoading ? "Se procesează înregistrarea..." : "Creează contul nou în JavaVerse") : 
            undefined
          }
        >
          {isLoading ? 'Se procesează...' : 'Înregistrare'}
        </button>
      </form>
      <p className="switch-page">
        Ai deja un cont?{' '}
        <Link 
          to="/login"
          aria-label={isAccessibilityMode ? "Mergi la pagina de autentificare dacă ai deja un cont" : undefined}
          onClick={() => {
            if (isAccessibilityMode) {
              sessionStorage.setItem('accessibilityModeActive', 'true');
            }
          }}
        >
          Autentifică-te
        </Link>
      </p>
    </div>
  );
}

export default Register;
