import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/CourseDetailPage.css';

function CourseDetailPage() {
  const { courseId } = useParams();  // Preluăm ID-ul cursului din URL
  const [course, setCourse] = useState(null);  // Inițial setăm la null pentru a reprezenta că datele nu sunt încă disponibile
  const [loading, setLoading] = useState(true);  // Stare pentru loading
  const navigate = useNavigate();

  useEffect(() => {
    // Aici preluăm datele pentru cursul selectat
    fetch(`http://localhost:5000/api/courses/${courseId}`)
      .then((response) => response.json())
      .then((data) => {
        setCourse(data.course);  // Salvăm datele cursului
        setLoading(false);  // Setăm loading la false atunci când datele sunt disponibile
      })
      .catch((error) => {
        console.error('Error fetching course data:', error);
        setLoading(false);  // Dacă apare o eroare, setăm loading la false
      });
  }, [courseId]);

  if (loading) {
    return <div>Loading...</div>;  // Afișăm Loading doar dacă datele nu sunt încă disponibile
  }

  if (!course) {
    return <div>Course not found.</div>;  // În cazul în care nu găsim cursul, arătăm un mesaj de eroare
  }

  return (
    <div className="course-detail-page">
      <h1 className="course-detail-title">{course.name}</h1>
      <p className="course-detail-description">{course.description}</p>
      <h3 className="course-detail-content-title">Course Content:</h3>
      <ul className="course-detail-content-list">
        {course.content.map((item, index) => (
          <li key={index} className="course-detail-content-item">
            {item}
          </li>
        ))}
      </ul>
      <button className="back-to-courses-btn" onClick={() => navigate('/courses')}>
        Back to Courses
      </button>
    </div>
  );
}

export default CourseDetailPage;
