import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/CoursesPage.css';  // Asigură-te că stilurile sunt corecte

function CoursesPage() {
  const courses = [
    { _id: '67f1b51e70f14f47321ac618', name: 'Introduction to Java Programming', description: 'Learn the basics of Java programming, from syntax to simple algorithms.' },
    { _id: '67f1b5a470f14f47321ac61a', name: 'Object-Oriented Programming in Java', description: 'Understand object-oriented programming concepts using Java.' },
    { _id: '67f1b5d970f14f47321ac61c', name: 'Advanced Java', description: 'Deep dive into advanced Java concepts like multi-threading and networking.' },
    { id: 4, name: 'Java Data Structures and Algorithms', description: 'Learn how to implement various data structures and algorithms in Java.' },
    { id: 5, name: 'Java Web Development with Spring', description: 'Develop web applications using Java and the Spring framework.' },
    { id: 6, name: 'Java for Android Development', description: 'Build Android apps using Java as the programming language.' },
  ];

  return (
    <div className="courses-page">
      <h1 className="page-title">Available Java Courses</h1>
      <div className="courses-grid">
        {courses.map((course, index) => (
          <div key={course.id} className="course-card">
            <h2 className="course-title">{course.name}</h2>
            <p className="course-description">{course.description}</p>
            <Link to={`/courses/${course._id}`} className="course-link">View Details</Link>
            </div>
        ))}
      </div>
    </div>
  );
}

export default CoursesPage;
