import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/CourseDetailPage.css';

function CourseDetailPage() {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [started, setStarted] = useState(false);  // Starea pentru a ști dacă cursul a fost început

  useEffect(() => {
    // Fetch course data from backend
    fetch(`http://localhost:5000/api/courses/${courseId}`)
      .then(response => response.json())
      .then(data => {
        if (data.course) {
          setCourse(data.course);  // Set course data
        }
        setLoading(false); // Stop loading once data is fetched
      })
      .catch(() => {
        setLoading(false); // Stop loading if there is an error
      });
  }, [courseId]);

  const handleStartCourse = () => {
    // Actualizează progresul utilizatorului în backend
    fetch(`http://localhost:5000/api/users/progress/${courseId}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ started: true })
    })
      .then(response => response.json())
      .then(data => {
        if (data.success) {
          setStarted(true);  // Marchează că cursul a început
        } else {
          console.log('Error starting the course');
        }
      })
      .catch(error => {
        console.error('Error starting the course:', error);
      });
  };

  if (loading) return <div>Loading...</div>;
  if (!course) return <div>Course not found.</div>;

  return (
    <div className="course-detail-page">
      <h1 className="course-detail-title">{course.name}</h1>
      <p className="course-detail-description">{course.description}</p>

      {!started && (
        <div className="course-buttons">
          <button className="back-to-courses-btn" onClick={() => navigate('/courses')}>
            Back to Courses
          </button>
          <button className="start-course-btn" onClick={handleStartCourse}>
            Start Course
          </button>
        </div>
      )}

      {started && (
        <div>
          <h3 className="course-detail-content-title">Course Content:</h3>
          <ul className="course-detail-content-list">
            {course.content && course.content.length > 0 ? (
              course.content.map((lesson, index) => (
                <li key={index} className="course-detail-content-item">
                  <h4>{lesson.title}</h4>
                  <p>{lesson.content}</p>
                </li>
              ))
            ) : (
              <p>No lessons available.</p>
            )}
          </ul>
        </div>
      )}
    </div>
  );
}

export default CourseDetailPage;
