import React, { useState, useEffect } from 'react';
import './Profile.css'; // Importăm fișierul CSS

function Profile() {
  const [userProgress, setUserProgress] = useState([]);

  useEffect(() => {
    // Fetch progresul utilizatorului
    fetch('/api/user/progress/USER_ID') // Înlocuiește cu ID-ul utilizatorului real
      .then(response => response.json())
      .then(data => setUserProgress(data))
      .catch(error => console.error('Error fetching user progress:', error));
  }, []);

  const calculateProgress = (completedLessons, totalLessons) => {
    return (completedLessons / totalLessons) * 100;
  };

  return (
    <div className="profile-container">
      <header className="profile-header">
        <h2>Profilul Utilizatorului</h2>
        <div className="user-info">
          <img src="https://via.placeholder.com/150" alt="User Avatar" className="user-avatar" />
          <div className="user-details">
            <p><strong>Username:</strong> JohnDoe</p>
            <p><strong>Email:</strong> johndoe@example.com</p>
          </div>
        </div>
      </header>
      <div className="course-list">
        {userProgress.length > 0 ? (
          userProgress.map(progress => (
            <div key={progress._id} className="course-card">
              <h3 className="course-name">{progress.courseId.name}</h3>
              <div className="progress-bar-container">
                <div
                  className="progress-bar"
                  style={{
                    width: `${calculateProgress(progress.completedLessons, progress.totalLessons)}%`,
                  }}
                ></div>
              </div>
              <p className="progress-text">
                Lecții finalizate: {progress.completedLessons} din {progress.totalLessons}
              </p>
              {progress.testScore !== null ? (
                <p className="test-score">Testul: {progress.testScore} din 100</p>
              ) : (
                <p className="test-not-taken">Test nesustinut</p>
              )}
            </div>
          ))
        ) : (
          <p className="no-progress">Încă nu ai progres la niciun curs.</p>
        )}
      </div>
    </div>
  );
}

export default Profile;
