import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from '../styles/Profile.module.css';

function Profile() {
  const [userProgress, setUserProgress] = useState([]);
  const [userInfo, setUserInfo] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('courses');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // În loc să redirecționăm imediat dacă nu există userId,
  // vom folosi date mock pentru demonstrație
  useEffect(() => {
    // Simulăm datele utilizatorului pentru demonstrație
    const mockUserInfo = {
      username: 'JavaLearner',
      email: 'java.learner@example.com',
      joinDate: '10 Februarie 2023'
    };

    // Simulăm progresul utilizatorului pentru demonstrație
    const mockProgress = [
      {
        _id: '1',
        completedLessons: 3, 
        totalLessons: 5,
        testScore: 90,
        courseId: {
          _id: '101',
          name: 'Introducere în Java',
          description: 'Învață bazele limbajului Java, de la sintaxă și tipuri de date până la controlul fluxului.'
        }
      },
      {
        _id: '2',
        completedLessons: 2,
        totalLessons: 5,
        testScore: 80,
        courseId: {
          _id: '102',
          name: 'Programare Orientată pe Obiecte în Java',
          description: 'Concepte fundamentale OOP în Java: clase, obiecte, moștenire și polimorfism.'
        }
      },
      {
        _id: '3',
        completedLessons: 1,
        totalLessons: 5,
        testScore: null,
        courseId: {
          _id: '103',
          name: 'Java pentru Aplicații Web cu Spring',
          description: 'Dezvoltă aplicații web folosind Spring Boot, Spring MVC și Spring Data.'
        }
      }
    ];

    // Simulăm un delay de încărcare pentru a demonstra loading state
    setTimeout(() => {
      setUserInfo(mockUserInfo);
      setUserProgress(mockProgress);
      setIsLoading(false);
    }, 800);

    // Când ești gata să conectezi la backend, poți decomenta aceste funcții:
    /*
    const userId = localStorage.getItem('userId');
    
    // Funcție pentru obținerea informațiilor utilizatorului
    const fetchUserInfo = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/users/${userId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch user information');
        }
        const data = await response.json();
        if (data.success && data.user) {
          setUserInfo(data.user);
        } else {
          throw new Error(data.message || 'Error fetching user data');
        }
      } catch (error) {
        console.error('Error fetching user info:', error);
        setError('Nu am putut obține informațiile utilizatorului.');
      }
    };
    
    // Funcție pentru obținerea progresului utilizatorului
    const fetchUserProgress = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/user/progress/${userId}`);
        if (!response.ok) {
          throw new Error('Failed to fetch user progress');
        }
        const data = await response.json();
        setUserProgress(data);
      } catch (error) {
        console.error('Error fetching user progress:', error);
        setError('Nu am putut obține progresul utilizatorului.');
      } finally {
        setIsLoading(false);
      }
    };
    
    if (userId) {
      fetchUserInfo();
      fetchUserProgress();
    } else {
      setIsLoading(false);
    }
    */
  }, []);

  // Funcție pentru a naviga către un curs
  const handleContinueCourse = (courseId, lessonId = 0) => {
    navigate(`/courses/${courseId}/lesson/${lessonId}`);
  };

  // Funcție pentru a naviga către pagina de cursuri
  const handleExploreCourses = () => {
    navigate('/courses');
  };

  if (isLoading) {
    return (
      <div className={styles.loadingContainer}>
        <div className={styles.loadingSpinner}></div>
        <p>Se încarcă profilul...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className={styles.errorContainer}>
        <p className={styles.errorMessage}>{error}</p>
        <button 
          className={styles.returnButton} 
          onClick={() => navigate('/courses')}
        >
          Înapoi la Cursuri
        </button>
      </div>
    );
  }

  // Calculare statistici totale
  const totalCourses = userProgress.length;
  const totalCompletedLessons = userProgress.reduce((total, course) => total + course.completedLessons, 0);
  
  // Calcularea scorului mediu (doar pentru testele susținute)
  const takenTests = userProgress.filter(progress => progress.testScore !== null);
  const averageScore = takenTests.length > 0
    ? Math.round(takenTests.reduce((total, course) => total + course.testScore, 0) / takenTests.length)
    : 0;

  return (
    <div className={styles.profileContainer}>
      {/* Secțiunea header cu informații utilizator și statistici */}
      <div className={styles.profileHeader}>
        <div className={styles.userInfo}>
          <div className={styles.avatarContainer}>
            <img src="https://via.placeholder.com/150" alt="User Avatar" className={styles.userAvatar} />
            <div className={styles.avatarOverlay}>
              <button className={styles.changeAvatarBtn}>
                📷
              </button>
            </div>
          </div>
          
          <div className={styles.userDetails}>
            <h2 className={styles.username}>{userInfo?.username || 'Utilizator JavaVerse'}</h2>
            <p className={styles.email}>{userInfo?.email || 'email@example.com'}</p>
            <p className={styles.joinDate}>Membru din: {userInfo?.joinDate || 'Ianuarie 2023'}</p>
          </div>
        </div>

        {/* Statistici utilizator */}
        <div className={styles.statsContainer}>
          <div className={styles.statCard}>
            <div className={styles.statIcon}>
              📚
            </div>
            <div className={styles.statInfo}>
              <span className={styles.statValue}>{totalCourses}</span>
              <span className={styles.statLabel}>Cursuri Active</span>
            </div>
          </div>
          
          <div className={styles.statCard}>
            <div className={styles.statIcon}>
              ✓
            </div>
            <div className={styles.statInfo}>
              <span className={styles.statValue}>{totalCompletedLessons}</span>
              <span className={styles.statLabel}>Lecții Finalizate</span>
            </div>
          </div>
          
          <div className={styles.statCard}>
            <div className={styles.statIcon}>
              🏆
            </div>
            <div className={styles.statInfo}>
              <span className={styles.statValue}>{averageScore}%</span>
              <span className={styles.statLabel}>Scor Mediu</span>
            </div>
          </div>
        </div>
      </div>

      {/* Tab-uri pentru navigare între secțiuni */}
      <div className={styles.tabsContainer}>
        <button 
          className={`${styles.tabButton} ${activeTab === 'courses' ? styles.activeTab : ''}`}
          onClick={() => setActiveTab('courses')}
        >
          Cursurile Mele
        </button>
        <button 
          className={`${styles.tabButton} ${activeTab === 'achievements' ? styles.activeTab : ''}`}
          onClick={() => setActiveTab('achievements')}
        >
          Realizări
        </button>
        <button 
          className={`${styles.tabButton} ${activeTab === 'settings' ? styles.activeTab : ''}`}
          onClick={() => setActiveTab('settings')}
        >
          Setări
        </button>
      </div>

      {/* Conținut în funcție de tab-ul activ */}
      <div className={styles.tabContent}>
        {activeTab === 'courses' && (
          <div className={styles.courseGrid}>
            {userProgress.length > 0 ? (
              userProgress.map(progress => {
                const progressPercent = (progress.completedLessons / progress.totalLessons) * 100;
                
                // Funcție pentru a determina clasa CSS pentru progres
                const getProgressColorClass = (progress) => {
                  if (progress < 30) return styles.progressLow;
                  if (progress < 70) return styles.progressMedium;
                  return styles.progressHigh;
                };
                
                // Funcție pentru a determina clasa CSS pentru scorul testului
                const getScoreClass = (score) => {
                  if (score === null) return styles.scoreNone;
                  if (score < 50) return styles.scoreLow;
                  if (score < 80) return styles.scoreMedium;
                  return styles.scoreHigh;
                };
                
                return (
                  <div key={progress._id} className={styles.courseCard}>
                    <div className={styles.courseHeader}>
                      <h3 className={styles.courseName}>{progress.courseId.name}</h3>
                      <div className={styles.courseActions}>
                        <button 
                          className={styles.continueButton}
                          onClick={() => handleContinueCourse(progress.courseId._id, progress.completedLessons)}
                        >
                          Continuă →
                        </button>
                      </div>
                    </div>
                    
                    <p className={styles.courseDescription}>{progress.courseId.description}</p>
                    
                    <div className={styles.progressSection}>
                      <div className={styles.progressInfo}>
                        <span>Progres: {Math.round(progressPercent)}%</span>
                        <span>{progress.completedLessons} din {progress.totalLessons} lecții</span>
                      </div>
                      
                      <div className={styles.progressBarContainer}>
                        <div
                          className={`${styles.progressBar} ${getProgressColorClass(progressPercent)}`}
                          style={{ width: `${progressPercent}%` }}
                        ></div>
                      </div>
                    </div>
                    
                    <div className={styles.testScoreSection}>
                      {progress.testScore !== null ? (
                        <div className={`${styles.testScore} ${getScoreClass(progress.testScore)}`}>
                          🏅 Test: {progress.testScore}%
                        </div>
                      ) : (
                        <div className={styles.testNotTaken}>
                          ⏳ Test nesusținut
                        </div>
                      )}
                    </div>
                  </div>
                );
              })
            ) : (
              <div className={styles.noCourses}>
                <div className={styles.noCoursesIcon}>
                  📚
                </div>
                <h3>Nu ești înscris la niciun curs</h3>
                <p>Înscrie-te la cursuri pentru a începe călătoria ta Java!</p>
                <button 
                  className={styles.exploreCourses} 
                  onClick={handleExploreCourses}
                >
                  Explorează Cursuri
                </button>
              </div>
            )}
          </div>
        )}

        {activeTab === 'achievements' && (
          <div className={styles.achievementsContainer}>
            <div className={styles.achievementCard}>
              <div className={styles.achievementIcon}>🏆</div>
              <h3>Primul Curs Început</h3>
              <p>Ai început călătoria de învățare Java!</p>
            </div>
            
            <div className={styles.achievementCard}>
              <div className={styles.achievementIcon}>🥉</div>
              <h3>Prima Lecție Completată</h3>
              <p>Ai finalizat prima ta lecție</p>
            </div>
            
            <div className={`${styles.achievementCard} ${styles.lockedAchievement}`}>
              <div className={styles.achievementIcon}>🔒</div>
              <h3>Primul Test Perfect</h3>
              <p>Completează un test cu scor 100%</p>
            </div>
            
            <div className={`${styles.achievementCard} ${styles.lockedAchievement}`}>
              <div className={styles.achievementIcon}>🔒</div>
              <h3>Primul Proiect Complet</h3>
              <p>Finalizează un proiect Java</p>
            </div>
          </div>
        )}

        {activeTab === 'settings' && (
          <div className={styles.settingsContainer}>
            <div className={styles.settingSection}>
              <h3 className={styles.settingHeader}>Informații Cont</h3>
              
              <div className={styles.formGroup}>
                <label htmlFor="username">Nume utilizator</label>
                <input 
                  type="text" 
                  id="username" 
                  value={userInfo?.username || ''} 
                  className={styles.formInput} 
                  readOnly
                />
              </div>
              
              <div className={styles.formGroup}>
                <label htmlFor="email">Email</label>
                <input 
                  type="email" 
                  id="email" 
                  value={userInfo?.email || ''} 
                  className={styles.formInput}
                  readOnly
                />
              </div>
              
              <button className={styles.saveButton} disabled>Salvează Modificările</button>
              <p className={styles.demoNote}>(Funcționalitate demonstrativă - actualizarea este dezactivată)</p>
            </div>
            
            <div className={styles.settingSection}>
              <h3 className={styles.settingHeader}>Schimbă Parola</h3>
              
              <div className={styles.formGroup}>
                <label htmlFor="currentPassword">Parola actuală</label>
                <input 
                  type="password" 
                  id="currentPassword" 
                  className={styles.formInput}
                  disabled
                />
              </div>
              
              <div className={styles.formGroup}>
                <label htmlFor="newPassword">Parola nouă</label>
                <input 
                  type="password" 
                  id="newPassword" 
                  className={styles.formInput}
                  disabled
                />
              </div>
              
              <div className={styles.formGroup}>
                <label htmlFor="confirmPassword">Confirmă parola</label>
                <input 
                  type="password" 
                  id="confirmPassword" 
                  className={styles.formInput}
                  disabled
                />
              </div>
              
              <button className={styles.saveButton} disabled>Schimbă Parola</button>
              <p className={styles.demoNote}>(Funcționalitate demonstrativă - schimbarea parolei este dezactivată)</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Profile;
