// src/components/AccessibleCodePlayground.js
import React, { useState } from 'react';
import AccessibleCodeEditor from './AccessibleCodeEditor';
import Card from './Card';
import Button from './Button';
import { useAccessibility } from '../hooks/useAccessibility';
import '../styles/CodePlayground.css';

function AccessibleCodePlayground() {
  const predefinedExamples = [
    {
      id: 'hello-world',
      name: 'Hello World',
      code: `public class HelloWorld {
    public static void main(String[] args) {
        System.out.println("Hello, JavaVerse!");
    }
}`
    },
    {
      id: 'variables',
      name: 'Variabile și Tipuri de Date',
      code: `public class Variables {
    public static void main(String[] args) {
        // Declararea variabilelor de tip primitiv
        int number = 42;
        double pi = 3.14159;
        char letter = 'A';
        boolean isJavaFun = true;
        
        // Afișarea valorilor
        System.out.println("Numărul: " + number);
        System.out.println("PI: " + pi);
        System.out.println("Litera: " + letter);
        System.out.println("Java este distractiv? " + isJavaFun);
        
        // Declararea unui String (tip referință)
        String message = "Învățarea Java este fascinantă!";
        System.out.println(message);
    }
}`
    },
    {
      id: 'loops',
      name: 'Bucle',
      code: `public class Loops {
    public static void main(String[] args) {
        // Bucla for - numără de la 1 la 5
        System.out.println("Exemplu de buclă for:");
        for (int i = 1; i <= 5; i++) {
            System.out.println("Numărul: " + i);
        }
        
        // Bucla while - numără de la 5 la 1
        System.out.println("\\nExemplu de buclă while:");
        int count = 5;
        while (count > 0) {
            System.out.println("Numărătoare inversă: " + count);
            count--;
        }
    }
}`
    },
    {
      id: 'conditionals',
      name: 'Instrucțiuni Condiționale',
      code: `public class Conditionals {
    public static void main(String[] args) {
        int time = 15;
        
        // Instrucțiune if-else
        System.out.println("Exemplu if-else:");
        if (time < 12) {
            System.out.println("Bună dimineața!");
        } else if (time < 18) {
            System.out.println("Bună ziua!");
        } else {
            System.out.println("Bună seara!");
        }
    }
}`
    }
  ];

  const [selectedExample, setSelectedExample] = useState(predefinedExamples[0]);
  const [code, setCode] = useState(predefinedExamples[0].code);
  const [showAllExamples, setShowAllExamples] = useState(false);
  const [selectedExampleIndex, setSelectedExampleIndex] = useState(0);

  const { 
    isAccessibilityMode, 
    useNavigationAccessibility,
    announceLoading 
  } = useAccessibility();
  
  const { announcePageLoad } = useNavigationAccessibility();

  // Listener pentru comenzile rapide de accesibilitate
  React.useEffect(() => {
    if (!isAccessibilityMode) return;

    const handleKeyPress = (e) => {
      if (!isAccessibilityMode) return;

      // Navigare prin exemple cu săgețile stânga/dreapta
      if (e.key === 'ArrowLeft') {
        e.preventDefault();
        const newIndex = selectedExampleIndex > 0 ? selectedExampleIndex - 1 : predefinedExamples.length - 1;
        handleExampleChange(predefinedExamples[newIndex], newIndex);
      } else if (e.key === 'ArrowRight') {
        e.preventDefault();
        const newIndex = selectedExampleIndex < predefinedExamples.length - 1 ? selectedExampleIndex + 1 : 0;
        handleExampleChange(predefinedExamples[newIndex], newIndex);
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [isAccessibilityMode, selectedExampleIndex, predefinedExamples]);

  React.useEffect(() => {
    if (isAccessibilityMode) {
      announcePageLoad(
        'Playground Java', 
        'Editorul interactiv pentru experimentarea cu cod Java. Folosește săgețile stânga și dreapta pentru a naviga prin exemple.'
      );
    }
  }, [isAccessibilityMode, announcePageLoad]);

  const handleExampleChange = (example, index = null) => {
    setSelectedExample(example);
    setCode(example.code);
    if (index !== null) {
      setSelectedExampleIndex(index);
    } else {
      const foundIndex = predefinedExamples.findIndex(ex => ex.id === example.id);
      setSelectedExampleIndex(foundIndex);
    }
    
    if (isAccessibilityMode) {
      announceLoading(`Exemplul selectat: ${example.name}`);
    }
  };

  const handleResetCode = () => {
    setCode(selectedExample.code);
    if (isAccessibilityMode) {
      announceLoading('Codul a fost resetat la exemplul original');
    }
  };

  const visibleExamples = showAllExamples 
    ? predefinedExamples 
    : predefinedExamples.slice(0, 3);

  return (
    <div className="code-playground-container">
      {isAccessibilityMode && (
        <div className="accessibility-instructions" style={{ 
          position: 'absolute', 
          left: '-9999px',
          width: '1px',
          height: '1px' 
        }}>
          <p>
            Playground Java pentru experimentarea cu cod. Sunt disponibile {predefinedExamples.length} exemple.
            Folosește săgețile stânga și dreapta pentru a naviga prin exemple.
            Exemplul curent: {selectedExample.name}.
          </p>
        </div>
      )}
      
      <h1 className="code-playground-title">Playground Java</h1>
      <p className="code-playground-description">
        Experimentează cu Java în editorul nostru interactiv. Alege unul dintre exemplele predefinite 
        sau scrie propriul tău cod pentru a învăța prin practică.
      </p>

      <div className="playground-layout">
        <div className="examples-sidebar">
          <h3>Exemple</h3>
          <div className="examples-list">
            {visibleExamples.map((example, index) => (
              <Card 
                key={example.id}
                className={`example-card ${selectedExample.id === example.id ? 'selected accessibility-focus' : ''}`}
                variant="dark"
                title={example.name}
                onClick={() => handleExampleChange(example, index)}
                hoverable={true}
                role={isAccessibilityMode ? "button" : undefined}
                aria-label={isAccessibilityMode ? `Exemplul ${index + 1}: ${example.name}` : undefined}
                tabIndex={isAccessibilityMode ? 0 : undefined}
              />
            ))}
            
            {!showAllExamples && predefinedExamples.length > 3 && (
              <Button 
                variant="secondary" 
                size="small"
                onClick={() => setShowAllExamples(true)}
                className="show-more-btn"
                aria-label={isAccessibilityMode ? "Arată toate exemplele disponibile" : undefined}
              >
                Arată toate exemplele
              </Button>
            )}
            
            {showAllExamples && (
              <Button 
                variant="secondary" 
                size="small"
                onClick={() => setShowAllExamples(false)}
                className="show-less-btn"
                aria-label={isAccessibilityMode ? "Arată mai puține exemple" : undefined}
              >
                Arată mai puține
              </Button>
            )}
          </div>
          
          <div className="playground-tips">
            <h4>Sfaturi pentru accesibilitate</h4>
            <ul>
              <li>Ctrl+Shift+E pentru a executa codul</li>
              <li>Ctrl+Shift+F pentru feedback AI</li>
              <li>Tab pentru a citi linia curentă</li>
              <li>Săgeți stânga/dreapta pentru exemple</li>
            </ul>
          </div>
        </div>
        
        <div className="code-editor-area">
          <div className="editor-header-bar">
            <h3>Editor: {selectedExample.name}</h3>
            <Button 
              variant="secondary" 
              size="small"
              onClick={handleResetCode}
              icon="🔄"
              iconPosition="left"
              aria-label={isAccessibilityMode ? "Resetează codul la exemplul original" : undefined}
            >
              Reset la exemplu
            </Button>
          </div>
          
          <AccessibleCodeEditor 
            initialCode={code} 
            onChange={setCode}
            editorKey={selectedExample.id}
          />
        </div>
      </div>
    </div>
  );
}

export default AccessibleCodePlayground;
