import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/MainPage.css';

function MainPage() {
  const navigate = useNavigate();
  const isAuthenticated = localStorage.getItem('authToken') ? true : false;

  // Dacă utilizatorul este autentificat, redirecționăm automat către pagina de cursuri
  React.useEffect(() => {
    if (isAuthenticated) {
      navigate('/courses');
    }
  }, [isAuthenticated, navigate]);
  
  // Efectul pentru crearea particulelor în fundal
  useEffect(() => {
    const createParticles = () => {
      const container = document.querySelector('.main-banner');
      const particlesCount = 50; // Numărul de particule
      
      // Eliminăm orice particule existente anterior
      const existingParticles = document.querySelectorAll('.particle');
      existingParticles.forEach(particle => particle.remove());
      
      // Generăm particule noi
      for (let i = 0; i < particlesCount; i++) {
        const particle = document.createElement('div');
        particle.classList.add('particle');
        
        // Proprietăți random pentru fiecare particulă
        const size = Math.random() * 5 + 1;
        const posX = Math.random() * 100;
        const posY = Math.random() * 100;
        const delay = Math.random() * 5;
        const duration = Math.random() * 20 + 10;
        
        // Setăm stilurile pentru particule
        particle.style.width = `${size}px`;
        particle.style.height = `${size}px`;
        particle.style.left = `${posX}%`;
        particle.style.top = `${posY}%`;
        particle.style.backgroundColor = '#ffffff';
        particle.style.animation = `float ${duration}s ${delay}s linear infinite`;
        
        container.appendChild(particle);
      }
    };
    
    // Creăm forma geometrică
    const createShapes = () => {
      const container = document.querySelector('.main-banner');
      const shapesContainer = document.createElement('div');
      shapesContainer.classList.add('geometric-shapes');
      
      // Creăm forme geometrice
      const shapesCount = 8;
      const shapes = ['circle', 'square'];
      
      for (let i = 0; i < shapesCount; i++) {
        const shape = document.createElement('div');
        const shapeType = shapes[Math.floor(Math.random() * shapes.length)];
        
        shape.classList.add('shape', shapeType);
        
        // Proprietăți random pentru fiecare formă
        const size = Math.random() * 200 + 50;
        const posX = Math.random() * 100;
        const posY = Math.random() * 100;
        const delay = Math.random() * 5;
        const duration = Math.random() * 30 + 20;
        
        // Setăm stilurile pentru forme
        shape.style.width = `${size}px`;
        shape.style.height = `${size}px`;
        shape.style.left = `${posX}%`;
        shape.style.top = `${posY}%`;
        shape.style.animation = `float ${duration}s ${delay}s linear infinite`;
        
        shapesContainer.appendChild(shape);
      }
      
      container.appendChild(shapesContainer);
    };
    
    // Adăugăm grila de cod
    const addCodeGrid = () => {
      const container = document.querySelector('.main-banner');
      const codeGrid = document.createElement('div');
      codeGrid.classList.add('code-grid');
      container.appendChild(codeGrid);
    };
    
    // Adăugăm strălucire ambientală
    const addAmbientGlow = () => {
      const container = document.querySelector('.main-banner');
      
      const glow1 = document.createElement('div');
      glow1.classList.add('ambient-glow');
      container.appendChild(glow1);
      
      const glow2 = document.createElement('div');
      glow2.classList.add('ambient-glow', 'secondary');
      container.appendChild(glow2);
    };
    
    // Inițializăm efectele de fundal
    const initBackgroundEffects = () => {
      addCodeGrid();
      createShapes();
      createParticles();
      addAmbientGlow();
    };
    
    // Rulăm inițializarea o singură dată
    initBackgroundEffects();
    
    // Curățăm când componenta este demontată
    return () => {
      const particles = document.querySelectorAll('.particle');
      const shapes = document.querySelector('.geometric-shapes');
      const codeGrid = document.querySelector('.code-grid');
      const ambientGlows = document.querySelectorAll('.ambient-glow');
      
      particles.forEach(particle => particle.remove());
      if (shapes) shapes.remove();
      if (codeGrid) codeGrid.remove();
      ambientGlows.forEach(glow => glow.remove());
    };
  }, []);

  return (
    <section className="main-banner">
      {/* Container principal pentru conținut */}
      <div className="banner-content">
        <h1 className="animated-title">
          <span className="title-word">Învață</span>
          <span className="title-word">limbajul</span>
          <span className="title-word">Java</span>
          <span className="title-word">într-un mod interactiv!</span>
        </h1>
        
        {/* Butonul principal pentru începere */}
        {!isAuthenticated && (
          <div className="cta-container">
            <button 
              className="main-cta" 
              onClick={() => navigate('/register')}
            >
              <span className="button-text">Începe acum</span>
              <span className="button-icon">→</span>
            </button>
          </div>
        )}
        
        {/* Container pentru cardurile cu caracteristici */}
        <div className="feature-cards-container">
          <div className="feature-card">
            <div className="feature-icon-container courses-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M12 2L2 7L12 12L22 7L12 2Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M2 17L12 22L22 17" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M2 12L12 17L22 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
            <div className="feature-content">
              <h3>Cursuri interactive</h3>
              <p>Lecții structurate și exemple practice</p>
            </div>
          </div>
          
          <div className="feature-card">
            <div className="feature-icon-container tests-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M9 11L12 14L22 4" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M21 12V19C21 19.5304 20.7893 20.0391 20.4142 20.4142C20.0391 20.7893 19.5304 21 19 21H5C4.46957 21 3.96086 20.7893 3.58579 20.4142C3.21071 20.0391 3 19.5304 3 19V5C3 4.46957 3.21071 3.96086 3.58579 3.58579C3.96086 3.21071 4.46957 3 5 3H16" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
            <div className="feature-content">
              <h3>Teste practice</h3>
              <p>Verifică-ți cunoștințele dobândite</p>
            </div>
          </div>
          
          <div className="feature-card">
            <div className="feature-icon-container editor-icon">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M16 18L22 12L16 6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M8 6L2 12L8 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
            <div className="feature-content">
              <h3>Editor de cod</h3>
              <p>Scrie și rulează cod Java direct în browser</p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default MainPage;
