import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import CourseDetailPage from "./CourseDetailPage"; // Importăm pagina de detalii pentru cursuri

function App() {
  const [courses, setCourses] = useState([]);

  // Preluăm cursurile de pe server
  useEffect(() => {
    fetch('http://localhost:5000/api/courses')
      .then(response => response.json())
      .then(data => setCourses(data.courses));
  }, []);

  return (
    <Router>
      <div>
        <h1>Welcome to JavaVerse</h1>
        <h2>Available Courses</h2>
        <div>
          {courses.length === 0 ? (
            <p>Loading courses...</p>
          ) : (
            courses.map(course => (
              <div key={course._id}>
                <h3>{course.name}</h3>
                <p>{course.description}</p>
                <button onClick={() => window.location.href = `/courses/${course._id}`}>
                  Start Course
                </button>
              </div>
            ))
          )}
        </div>

        <Switch>
          <Route path="/courses/:courseId" component={CourseDetailPage} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
