import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/CoursesPage.css';  // Asigură-te că stilurile sunt corecte

function CoursesPage() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Obținem cursurile de la server
  useEffect(() => {
    fetch('http://localhost:5000/api/courses')
      .then((response) => response.json())
      .then((data) => {
        setCourses(data.courses);  // Salvează cursurile primite din backend
        setLoading(false);
      })
      .catch((error) => {
        setError('Error loading courses');
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="courses-page">
      <h1 className="page-title">Available Java Courses</h1>
      <div className="courses-grid">
        {courses.map((course) => (
          <div key={course._id} className="course-card">
            <h2 className="course-title">{course.name}</h2>
            <p className="course-description">{course.description}</p>
            <Link to={`/courses/${course._id}`} className="course-link">View Details</Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CoursesPage;
