import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/CourseDetailPage.css';

function CourseDetailPage() {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showContent, setShowContent] = useState(false); // Control pentru a extinde lecțiile

  useEffect(() => {
    fetch(`http://localhost:5000/api/courses/${courseId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.course) {
          setCourse(data.course);
          setLoading(false);
        } else {
          setLoading(false);
        }
      })
      .catch(() => setLoading(false));
  }, [courseId]);

  if (loading) return <div>Loading...</div>;
  if (!course) return <div>Course not found.</div>;

  return (
    <div className="course-detail-page">
      <h1 className="course-detail-title">{course.name}</h1>
      <p className="course-detail-description">{course.description}</p>

      <h3 className="course-detail-content-title">Course Content:</h3>
      <ul className="course-detail-content-list">
        {course.content.map((lesson, index) => (
          <li key={index} className="course-detail-content-item">
            Lesson {index + 1}: {lesson}
            {showContent && (
              <div className="course-lesson-text">
                <p>
                  {/* Poți înlocui acest text cu conținutul real al fiecărei lecții */}
                  This is the full content for "{lesson}". <br />
                  Here you can add more details about the lesson, code examples, explanations, etc.
                </p>
              </div>
            )}
          </li>
        ))}
      </ul>

      <div className="course-buttons">
        <button className="back-to-courses-btn" onClick={() => navigate('/courses')}>
          Back to Courses
        </button>
        <button
          className="start-course-btn"
          onClick={() => setShowContent(true)} // Extinde lecțiile la apăsarea butonului "Start Course"
        >
          Start Course
        </button>
      </div>
    </div>
  );
}

export default CourseDetailPage;
