import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import MainPage from './components/MainPage';
import Footer from './components/Footer';
import Login from './components/Login';
import Register from './components/Register';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Verifică dacă token-ul există în localStorage
    const token = localStorage.getItem('authToken');
    if (token) {
      setIsAuthenticated(true); // Setează starea ca autentificat
    }
  }, []);

  const handleLogout = () => {
    // Elimină token-ul din localStorage și setează starea la fals
    localStorage.removeItem('authToken');
    setIsAuthenticated(false);
    window.location.href = '/'; // Redirecționează la pagina principală
  };

  return (
    <Router>
      <div className="App">
        <Header />
        <div className="body-content">
          <Routes>
            <Route path="/" element={<MainPage />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
          </Routes>
        </div>
        <Footer />
        <div className="navbar">
          {/* Dacă utilizatorul este autentificat, afișează butoanele de profil și logout */}
          {isAuthenticated ? (
            <>
              <button onClick={() => window.location.href = '/profile'}>Profile</button>
              <button onClick={handleLogout}>Logout</button>
            </>
          ) : (
            <>
              <button onClick={() => window.location.href = '/login'}>Login</button>
              <button onClick={() => window.location.href = '/register'}>Register</button>
            </>
          )}
        </div>
      </div>
    </Router>
  );
}

export default App;
