import React, { useState, useEffect } from 'react';
import styles from '../styles/Profile.module.css'; // Importă module CSS

function Profile() {
  const [userProgress, setUserProgress] = useState([]);
  const [userInfo, setUserInfo] = useState(null);

  // Obține userId din token-ul JWT stocat în localStorage
  const userId = localStorage.getItem('userId');  // Înlocuiește cu metoda corectă de extragere a ID-ului utilizatorului

  useEffect(() => {
    if (userId) {
      fetch(`/api/user/progress/${userId}`) // Cererea API pentru progresul utilizatorului
        .then(response => response.json())
        .then(data => {
          console.log("Progresul utilizatorului:", data);
          setUserProgress(data);
          if (data[0]?.userId) {
            setUserInfo(data[0].userId); // Setăm informațiile utilizatorului
          }
        })
        .catch(error => console.error('Error fetching user progress:', error));
    }
  }, [userId]);

  const calculateProgress = (completedLessons, totalLessons) => {
    return (completedLessons / totalLessons) * 100;
  };

  return (
    <div className={styles.profileContainer}>
      <header className={styles.profileHeader}>
        <div className={styles.userInfo}>
          {userInfo ? (
            <>
              <img src="https://via.placeholder.com/150" alt="User Avatar" className={styles.userAvatar} />
              <div className={styles.userDetails}>
                <p><strong>Username:</strong> {userInfo.username}</p>
                <p><strong>Email:</strong> {userInfo.email}</p>
              </div>
            </>
          ) : (
            <p>Încă nu sunt disponibile informațiile utilizatorului.</p>
          )}
        </div>
      </header>

      <div className={styles.courseList}>
        {userProgress.length > 0 ? (
          userProgress.map(progress => (
            <div key={progress._id} className={styles.courseCard}>
              <h3 className={styles.courseName}>{progress.courseId.name}</h3>
              <div className={styles.progressBarContainer}>
                <div
                  className={styles.progressBar}
                  style={{
                    width: `${calculateProgress(progress.completedLessons, progress.totalLessons)}%`,
                  }}
                ></div>
              </div>
              <p className={styles.progressText}>
                Lecții finalizate: {progress.completedLessons} din {progress.totalLessons}
              </p>
              {progress.testScore !== null ? (
                <p className={styles.testScore}>Testul: {progress.testScore} din 100</p>
              ) : (
                <p className={styles.testNotTaken}>Test nesustinut</p>
              )}
            </div>
          ))
        ) : (
          <p className={styles.noProgress}>Încă nu ai progres la niciun curs.</p>
        )}
      </div>
    </div>
  );
}

export default Profile;
