// src/components/Login.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/FormPage.css'; // Ajustează calea în funcție de proiectul tău

function Login({ setIsAuthenticated }) {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validare simplă
    if (!formData.email || !formData.password) {
      setError('Te rugăm să completezi toate câmpurile.');
      return;
    }
    
    setIsLoading(true);
    setError('');

    try {
      console.log('Încercăm autentificarea cu:', { email: formData.email });
      
      const response = await fetch('http://localhost:5000/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      console.log('Răspuns primit de la server');
      const data = await response.json();
      console.log('Date parsate:', data);

      if (data.success) {
        console.log('Autentificare reușită, salvăm token-ul');
        // Stocăm token-ul în localStorage
        localStorage.setItem('authToken', data.token);
        
        // Stocăm userId-ul - presupunem că știm că acesta este ID-ul utilizatorului
        // Am simplificat această parte față de versiunea anterioară
        localStorage.setItem('userId', data.userId || data.id);
        
        // Setăm starea de autentificare
        setIsAuthenticated(true);
        
        // Redirecționăm utilizatorul către pagina principală
        navigate('/');
      } else {
        // Afișăm eroarea primită de la server
        setError(data.message || 'Autentificare eșuată. Verifică email-ul și parola.');
      }
    } catch (error) {
      console.error('Error during login:', error);
      setError('Eroare de rețea. Te rugăm să încerci din nou.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="form-container">
      <h1>Autentificare</h1>
      
      {error && <div className="error-message">{error}</div>}
      
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="input-group">
          <label htmlFor="password">Parolă</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        
        <button 
          type="submit" 
          className="submit-button"
          disabled={isLoading}
        >
          {isLoading ? 'Se procesează...' : 'Autentificare'}
        </button>
      </form>
      
      <div className="switch-page">
        Nu ai un cont? <Link to="/register">Înregistrează-te</Link>
      </div>
    </div>
  );
}

export default Login;
