import React, { useState, useEffect } from 'react';

function AccessibleCodePlayground() {
  const [selectedExampleIndex, setSelectedExampleIndex] = useState(0);
  const [isInEditor, setIsInEditor] = useState(false);
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('Apas캒 "Execut캒 Codul" pentru a vedea rezultatul...');

  // Verific캒m dac캒 hook-ul de accessibility este disponibil
  let isAccessibilityMode = false;
  let announceLoading = () => {};
  let announcePageLoad = () => {};
  let announceCodeExecution = () => {};
  let announceExecutionResult = () => {};

  try {
    const { useAccessibility } = require('../hooks/useAccessibility');
    const accessibility = useAccessibility();
    isAccessibilityMode = accessibility.isAccessibilityMode;
    announceLoading = accessibility.announceLoading;
    announcePageLoad = accessibility.useNavigationAccessibility().announcePageLoad;
    announceCodeExecution = accessibility.useCodeEditorAccessibility().announceCodeExecution;
    announceExecutionResult = accessibility.useCodeEditorAccessibility().announceExecutionResult;
  } catch (error) {
    console.log('Accessibility hooks not available, running in normal mode');
  }

  // Exemple de cod predefinite
  const codeExamples = [
    {
      title: "Hello World",
      description: "Primul t캒u program Java - afi탳eaz캒 un salut simplu",
      code: `public class HelloWorld {
    public static void main(String[] args) {
        System.out.println("Hello, World!");
    }
}`
    },
    {
      title: "Variabile 탳i Tipuri", 
      description: "칉nva탵캒 s캒 declari 탳i s캒 folose탳ti variabile 칥n Java",
      code: `public class Variables {
    public static void main(String[] args) {
        int age = 25;
        String name = "Java Developer";
        double salary = 5000.50;
        boolean isEmployed = true;
        
        System.out.println("Nume: " + name);
        System.out.println("V칙rsta: " + age);
        System.out.println("Salar: " + salary);
        System.out.println("Angajat: " + isEmployed);
    }
}`
    },
    {
      title: "Structuri Condi탵ionale",
      description: "Folose탳te if-else pentru a lua decizii 칥n cod", 
      code: `public class Conditionals {
    public static void main(String[] args) {
        int score = 85;
        
        if (score >= 90) {
            System.out.println("Nota: A");
        } else if (score >= 80) {
            System.out.println("Nota: B");
        } else if (score >= 70) {
            System.out.println("Nota: C");
        } else {
            System.out.println("Nota: F");
        }
    }
}`
    },
    {
      title: "Bucle",
      description: "Repet캒 opera탵ii cu for 탳i while",
      code: `public class Loops {
    public static void main(String[] args) {
        // Bucla for
        System.out.println("Numerele de la 1 la 5:");
        for (int i = 1; i <= 5; i++) {
            System.out.println(i);
        }
        
        // Bucla while
        System.out.println("Countdown:");
        int countdown = 3;
        while (countdown > 0) {
            System.out.println(countdown);
            countdown--;
        }
        System.out.println("Start!");
    }
}`
    },
    {
      title: "Arrays",
      description: "Lucreaz캒 cu colec탵ii de elemente",
      code: `public class Arrays {
    public static void main(String[] args) {
        String[] fruits = {"m캒r", "banan캒", "portocal캒", "strugure"};
        
        System.out.println("Fructele mele preferate:");
        for (int i = 0; i < fruits.length; i++) {
            System.out.println((i + 1) + ". " + fruits[i]);
        }
        
        // Enhanced for loop
        System.out.println("\\nCu enhanced for:");
        for (String fruit : fruits) {
            System.out.println("- " + fruit);
        }
    }
}`
    }
  ];

  // Anun탵area unui exemplu
  const announceExample = (example, index) => {
    if (isAccessibilityMode) {
      const announcement = `Exemplul ${index + 1} din ${codeExamples.length}: ${example.title}. ${example.description}. Apas캒 Enter pentru a 칥nc캒rca 칥n editor.`;
      announceLoading(announcement);
    }
  };

  // 칉nc캒rcarea unui exemplu 칥n editor
  const loadExampleInEditor = (example) => {
    setCode(example.code);
    setIsInEditor(true);
    
    if (isAccessibilityMode) {
      announceLoading(`Exemplul "${example.title}" a fost 칥nc캒rcat 칥n editor. Modul editare activat.`);
    }
  };

  // Ie탳irea din editor
  const exitEditor = () => {
    setIsInEditor(false);
    
    if (isAccessibilityMode) {
      announceLoading('Ai ie탳it din editor. Navigheaz캒 din nou prin exemple cu s캒ge탵ile.');
    }
  };

  // Executarea codului
  const executeCode = () => {
    if (isAccessibilityMode) {
      announceCodeExecution();
    }
    
    // Simulare execu탵ie
    setTimeout(() => {
      const simulatedOutput = "Hello, World!\nCod executat cu succes!";
      setOutput(simulatedOutput);
      if (isAccessibilityMode) {
        announceExecutionResult(simulatedOutput);
      }
    }, 1000);
  };

  // Handler pentru schimbarea codului 칥n editor
  const handleCodeChange = (e) => {
    setCode(e.target.value);
  };

  // Listener pentru comenzile rapide de accesibilitate
  useEffect(() => {
    if (!isAccessibilityMode) return;

    const handleKeyPress = (e) => {
      if (!isAccessibilityMode) return;

      // Dac캒 suntem 칥n editor, l캒s캒m editorul s캒 gestioneze
      if (isInEditor) return;

      // Navigare prin exemple cu s캒ge탵ile
      if (e.key === 'ArrowLeft') {
        e.preventDefault();
        const newIndex = selectedExampleIndex > 0 ? selectedExampleIndex - 1 : codeExamples.length - 1;
        setSelectedExampleIndex(newIndex);
        announceExample(codeExamples[newIndex], newIndex);
      } else if (e.key === 'ArrowRight') {
        e.preventDefault();
        const newIndex = selectedExampleIndex < codeExamples.length - 1 ? selectedExampleIndex + 1 : 0;
        setSelectedExampleIndex(newIndex);
        announceExample(codeExamples[newIndex], newIndex);
      }
      
      // Enter pentru a 칥nc캒rca exemplul 칥n editor
      if (e.key === 'Enter') {
        e.preventDefault();
        loadExampleInEditor(codeExamples[selectedExampleIndex]);
      }
      
      // Escape pentru a ie탳i din editor 탳i a reveni la exemple
      if (e.key === 'Escape' && isInEditor) {
        e.preventDefault();
        exitEditor();
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [isAccessibilityMode, selectedExampleIndex, isInEditor]);

  // 칉nc캒rcarea paginii
  useEffect(() => {
    if (isAccessibilityMode) {
      announcePageLoad(
        'Playground de cod Java', 
        `Aici po탵i experimenta cu cod Java. Sunt disponibile ${codeExamples.length} exemple. Folose탳te s캒ge탵ile st칙nga 탳i dreapta pentru navigare.`
      );
      
      // Anun탵캒m primul exemplu
      setTimeout(() => {
        announceExample(codeExamples[0], 0);
      }, 1500);
    }
  }, [isAccessibilityMode]);

  return (
    <div className="code-playground-container">
      {isAccessibilityMode && (
        <div className="accessibility-instructions" style={{ 
          position: 'absolute', 
          left: '-9999px',
          width: '1px',
          height: '1px' 
        }}>
          <p>
            Playground de cod Java. Navigheaz캒 prin exemple cu s캒ge탵ile st칙nga 탳i dreapta. 
            Enter pentru a 칥nc캒rca exemplul 칥n editor. Escape pentru a ie탳i din editor.
          </p>
        </div>
      )}

      <h1>Java Code Playground</h1>
      <p className="playground-description">
        Experimenteaz캒 cu cod Java folosind exemplele de mai jos sau scrie propriul t캒u cod.
      </p>

      {!isInEditor ? (
        // Sec탵iunea cu exemple
        <div className="examples-section">
          <h2>Exemple de cod</h2>
          <div className="examples-grid">
            {codeExamples.map((example, index) => (
              <div 
                key={index}
                className={`example-card ${isAccessibilityMode && index === selectedExampleIndex ? 'accessibility-focus' : ''}`}
                onClick={() => loadExampleInEditor(example)}
                role={isAccessibilityMode ? "button" : undefined}
                aria-label={isAccessibilityMode ? `Exemplul ${index + 1}: ${example.title}` : undefined}
                tabIndex={isAccessibilityMode ? (index === selectedExampleIndex ? 0 : -1) : 0}
              >
                <h3 className="example-title">{example.title}</h3>
                <p className="example-description">{example.description}</p>
                <div className="example-code-preview">
                  <code>{example.code.substring(0, 100)}...</code>
                </div>
                {!isAccessibilityMode && (
                  <button 
                    className="load-example-btn"
                    onClick={(e) => {
                      e.stopPropagation();
                      loadExampleInEditor(example);
                    }}
                  >
                    칉ncarc캒 칥n Editor
                  </button>
                )}
              </div>
            ))}
          </div>
          
          {isAccessibilityMode && (
            <div className="accessibility-navigation-info">
              <p>
                Exemplul curent: {selectedExampleIndex + 1} din {codeExamples.length}. 
                Folose탳te s캒ge탵ile pentru navigare 탳i Enter pentru 칥nc캒rcare.
              </p>
            </div>
          )}
        </div>
      ) : (
        // Sec탵iunea cu editorul
        <div className="editor-section">
          <h2>Editor de cod</h2>
          <div className="editor-container">
            <textarea
              value={code}
              onChange={handleCodeChange}
              className="simple-code-editor"
              placeholder="Scrie codul t캒u Java aici..."
              rows={20}
              cols={80}
              style={{
                fontFamily: 'monospace',
                fontSize: '14px',
                border: '1px solid #ccc',
                padding: '10px',
                width: '100%',
                resize: 'vertical',
                backgroundColor: '#f8f8f8'
              }}
            />
          </div>
          
          <div className="editor-actions">
            <button 
              onClick={executeCode}
              className="execute-btn"
              aria-label={isAccessibilityMode ? "Execut캒 codul din editor" : undefined}
              style={{
                backgroundColor: '#4CAF50',
                color: 'white',
                border: 'none',
                padding: '10px 20px',
                marginRight: '10px',
                cursor: 'pointer',
                borderRadius: '4px'
              }}
            >
              游 Execut캒 Codul
            </button>
            
            <button 
              onClick={exitEditor}
              className="back-to-examples-btn"
              aria-label={isAccessibilityMode ? "칉napoi la lista de exemple" : undefined}
              style={{
                backgroundColor: '#2196F3',
                color: 'white', 
                border: 'none',
                padding: '10px 20px',
                cursor: 'pointer',
                borderRadius: '4px'
              }}
            >
              游닄 칉napoi la Exemple
            </button>
          </div>
          
          <div className="output-section">
            <h3>Output</h3>
            <div className="output-console" style={{
              backgroundColor: '#f5f5f5',
              border: '1px solid #ddd',
              padding: '10px',
              fontFamily: 'monospace',
              whiteSpace: 'pre-wrap',
              minHeight: '100px',
              borderRadius: '4px'
            }}>
              <p>{output}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AccessibleCodePlayground;
