import React from 'react';
import { Link } from 'react-router-dom'; // Importă Link din react-router-dom

function Header({ isAuthenticated, handleLogout }) {
  return (
    <header className="header">
      <Link to={isAuthenticated ? '/courses' : '/'} className="logo-link">  {/* Redirecționează către cursuri dacă utilizatorul este logat */}
        <h1>JavaVerse</h1>
      </Link>
      <nav className="nav">
        {/* Butoane login/register sau profile/logout în funcție de autentificare */}
        {isAuthenticated ? (
          <>
            <Link to="/courses">
              <button>Courses</button>
            </Link>
            <button onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login">
              <button>Login</button>
            </Link>
            <Link to="/register">
              <button>Register</button>
            </Link>
          </>
        )}
      </nav>
    </header>
  );
}

export default Header;
