import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Button from './Button';
import '../styles/Header.css';

function Header({ isAuthenticated, handleLogout }) {
  const navigate = useNavigate();
  const [scrolled, setScrolled] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  // Efect pentru a detecta cÃ¢nd utilizatorul deruleazÄƒ pagina
  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 10;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [scrolled]);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <header className={`jv-header ${scrolled ? 'jv-header-scrolled' : ''}`}>
      <div className="jv-header-container">
        <div className="jv-header-logo">
          <Link to="/" className="logo-link">
            <h1 className="text-gradient">JavaVerse</h1>
          </Link>
        </div>

        {/* Buton de meniu pentru mobile */}
        <div className="jv-header-mobile-toggle" onClick={toggleMenu}>
          <div className={`menu-icon ${menuOpen ? 'open' : ''}`}>
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>

        {/* Navigare */}
        <nav className={`jv-header-nav ${menuOpen ? 'open' : ''}`}>
          {isAuthenticated ? (
            <ul className="jv-nav-list">
              <li className="jv-nav-item">
                <Link to="/courses" className="jv-nav-link">Cursuri</Link>
              </li>
              <li className="jv-nav-item">
                <Link to="/profile" className="jv-nav-link">Profil</Link>
              </li>
              <li className="jv-nav-item">
                <Button 
                  variant="secondary" 
                  size="small" 
                  onClick={handleLogout}
                  icon="ðŸšª"
                  iconPosition="left"
                >
                  Deconectare
                </Button>
              </li>
            </ul>
          ) : (
            <ul className="jv-nav-list">
              <li className="jv-nav-item">
                <Button 
                  variant="secondary" 
                  size="small" 
                  onClick={() => navigate('/login')}
                >
                  Autentificare
                </Button>
              </li>
              <li className="jv-nav-item">
                <Button 
                  variant="primary" 
                  size="small" 
                  onClick={() => navigate('/register')}
                  glow={true}
                >
                  ÃŽnregistrare
                </Button>
              </li>
            </ul>
          )}
        </nav>
      </div>
    </header>
  );
}

export default Header;
