import React from 'react';
import { useNavigate } from 'react-router-dom';  // Importă useNavigate pentru redirecționare

function MainPage() {
  const navigate = useNavigate();
  const isAuthenticated = localStorage.getItem('authToken') ? true : false;

  // Dacă utilizatorul este autentificat, redirecționează-l automat către pagina de cursuri
  if (isAuthenticated) {
    navigate('/courses');
  }

  return (
    <div className="main-page">
      <h1>Welcome to JavaVerse</h1>
      <p>Your journey to mastering Java starts here!</p>
      {!isAuthenticated && (
        <button onClick={() => navigate('/login')}>Incepe acum</button>
      )}
    </div>
  );
}

export default MainPage;
