// src/components/CodePlayground.js - ÃŽnlocuire completÄƒ cu versiunea accesibilÄƒ
import React, { useState, useEffect, useRef } from 'react';
import '../styles/CodePlayground.css';

function CodePlayground() {
  const [selectedExampleIndex, setSelectedExampleIndex] = useState(0);
  const [isEditorActive, setIsEditorActive] = useState(false);
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('ApasÄƒ "Alt+E" pentru a executa codul...');
  const [feedback, setFeedback] = useState('');
  const [isExecuting, setIsExecuting] = useState(false);
  const [isLoadingFeedback, setIsLoadingFeedback] = useState(false);
  const [hasAnnouncedWelcome, setHasAnnouncedWelcome] = useState(false);
  const [currentLine, setCurrentLine] = useState(1);
  const [isTyping, setIsTyping] = useState(false);
  
  const textareaRef = useRef(null);
  const typingTimeoutRef = useRef(null);

  // Editor gol + exemple de cod predefinite
  const codeExamples = [
    {
      title: "Editor Gol",
      description: "ÃŽncepe sÄƒ scrii propriul tÄƒu cod Java de la zero",
      code: `public class Main {
