          <div className="editor-container">
            <textarea
              ref={textareaRef}
              value={code}
              onChange={handleCodeChange}
              onKeyDown={handleEditorKeyDown}
              onSelect={updateCurrentLine}
              className="simple-code-editor"
              placeholder="Scrie codul tÄƒu Java aici..."
